}

// 模拟 API 延迟
export const delay = (ms: number) => new Promise(resolve => setTimeout(resolve, ms))

// Mock API 函数
export const mockApi = {
  // 获取所有原子上下文
  getAtomicContexts: async (): Promise<AtomicContext[]> => {
    await delay(300)
    return [...mockAtomicContexts]
  },

  // 根据 ID 获取单个原子上下文
  getAtomicContext: async (id: string): Promise<AtomicContext | null> => {
    await delay(200)
    return mockAtomicContexts.find(ctx => ctx.id === id) || null
  },

  // 搜索原子上下文
  searchAtomicContexts: async (query: string): Promise<AtomicContext[]> => {
    await delay(150)
    if (!query) return mockAtomicContexts

    const lowerQuery = query.toLowerCase()
    return mockAtomicContexts.filter(ctx => 
      ctx.name.toLowerCase().includes(lowerQuery) ||
      ctx.description.toLowerCase().includes(lowerQuery) ||
      ctx.tags.some(tag => tag.toLowerCase().includes(lowerQuery))
    )
  },

  // 按分类筛选
  filterByCategory: async (category: string): Promise<AtomicContext[]> => {
    await delay(100)
    if (!category || category === 'all') return mockAtomicContexts
    return mockAtomicContexts.filter(ctx => ctx.category === category)
  },

  // 按状态筛选
  filterByStatus: async (status: string): Promise<AtomicContext[]> => {
    await delay(100)
    if (!status || status === 'all') return mockAtomicContexts
    return mockAtomicContexts.filter(ctx => ctx.status === status)
  }
}

