'use client'

import React from 'react'
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar'
import { Skeleton } from '@/components/ui/skeleton'
import { Eye, GitBranch, Copy, Clock, TrendingUp } from 'lucide-react'
import { type AtomicContext } from '@/lib/mock-data'
import { toast } from 'sonner'

interface AtomicContextListProps {
  contexts: AtomicContext[]
  loading: boolean
  onViewDetails: (context: AtomicContext) => void
}

export function AtomicContextList({ contexts, loading, onViewDetails }: AtomicContextListProps) {
  const handleUseContext = (context: AtomicContext) => {
    // 模拟复制到剪贴板
    navigator.clipboard.writeText(context.content)
    toast.success(`已复制 "${context.name}" 到剪贴板`)
  }

  const handleViewVersions = (context: AtomicContext) => {
    toast.info(`查看 "${context.name}" 的版本历史`)
  }

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {Array.from({ length: 6 }).map((_, i) => (
          <Card key={i} className="h-[280px]">
            <CardHeader>
              <Skeleton className="h-6 w-3/4" />
              <Skeleton className="h-4 w-full" />
              <Skeleton className="h-4 w-2/3" />
            </CardHeader>
            <CardContent>
              <Skeleton className="h-4 w-full mb-2" />
              <Skeleton className="h-4 w-3/4 mb-4" />
              <div className="flex gap-2">
                <Skeleton className="h-6 w-16" />
                <Skeleton className="h-6 w-20" />
                <Skeleton className="h-6 w-12" />
              </div>
            </CardContent>
            <CardFooter>
              <Skeleton className="h-9 w-full" />
            </CardFooter>
          </Card>
        ))}
      </div>
    )
  }

  if (contexts.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center py-12 text-center">
        <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mb-4">
          <Eye className="w-8 h-8 text-muted-foreground" />
        </div>
        <h3 className="text-lg font-semibold mb-2">没有找到匹配的原子上下文</h3>
        <p className="text-muted-foreground mb-4">
          尝试调整搜索关键词或筛选条件
        </p>
        <Button variant="outline" onClick={() => window.location.reload()}>
          重置筛选
        </Button>
      </div>
    )
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {contexts.map((context) => (
        <Card key={context.id} className="group hover:shadow-lg transition-all duration-200 animate-fade-in">
          <CardHeader className="pb-3">
            <div className="flex items-start justify-between">
              <div className="space-y-1 min-w-0 flex-1">
                <CardTitle className="text-lg leading-tight truncate">
                  {context.name}
                </CardTitle>
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Badge variant={getStatusVariant(context.status)} className="text-xs">
                    {getStatusLabel(context.status)}
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    v{context.version}
                  </Badge>
                </div>
              </div>
              <Badge variant="secondary" className="text-xs ml-2">
                {getCategoryLabel(context.category)}
              </Badge>
            </div>
            <CardDescription className="line-clamp-2 leading-relaxed">
              {context.description}
            </CardDescription>
          </CardHeader>

          <CardContent className="py-3">
            {/* 标签 */}
            <div className="flex flex-wrap gap-1 mb-3">
              {context.tags.slice(0, 3).map((tag, index) => (
                <Badge key={index} variant="outline" className="text-xs px-2 py-0.5">
                  {tag}
                </Badge>
              ))}
              {context.tags.length > 3 && (
                <Badge variant="outline" className="text-xs px-2 py-0.5">
                  +{context.tags.length - 3}
                </Badge>
              )}
            </div>

            {/* 作者和使用统计 */}
            <div className="flex items-center justify-between text-sm text-muted-foreground">
              <div className="flex items-center gap-2">
                <Avatar className="w-6 h-6">
                  <AvatarImage src={context.author.avatar} alt={context.author.name} />
                  <AvatarFallback className="text-xs">
                    {context.author.name.charAt(0)}
                  </AvatarFallback>
                </Avatar>
                <span className="truncate">{context.author.name}</span>
              </div>
              <div className="flex items-center gap-3 text-xs">
                <div className="flex items-center gap-1">
                  <TrendingUp className="w-3 h-3" />
                  <span>{context.usageCount}</span>
                </div>
                <div className="flex items-center gap-1">
                  <Clock className="w-3 h-3" />
                  <span>{formatTimeAgo(context.lastUsed)}</span>
                </div>
              </div>
            </div>
          </CardContent>

          <CardFooter className="pt-3 gap-2">
            <Button
              onClick={() => onViewDetails(context)}
              className="flex-1"
              size="sm"
            >
              <Eye className="w-4 h-4 mr-2" />
              查看详情
            </Button>
            <Button
              onClick={() => handleUseContext(context)}
              variant="outline"
              size="sm"
            >
              <Copy className="w-4 h-4 mr-2" />
              使用
            </Button>
            <Button
              onClick={() => handleViewVersions(context)}
              variant="ghost"
              size="sm"
            >
              <GitBranch className="w-4 h-4" />
            </Button>
          </CardFooter>
        </Card>
      ))}
    </div>
  )
}

function getStatusVariant(status: string) {
  switch (status) {
    case 'active':
      return 'default' as const
    case 'deprecated':
      return 'destructive' as const
    case 'draft':
      return 'secondary' as const
    default:
      return 'outline' as const
  }
}

function getStatusLabel(status: string): string {
  const labels = {
    active: '活跃',
    deprecated: '已弃用',
    draft: '草稿'
  }
  return labels[status as keyof typeof labels] || status
}

function getCategoryLabel(category: string): string {
  const labels = {
    component: '组件',
    hook: 'Hook',
    utility: '工具',
    api: 'API',
    style: '样式'
  }
  return labels[category as keyof typeof labels] || category
}

function formatTimeAgo(dateString: string): string {
  const now = new Date()
  const date = new Date(dateString)
  const diffInSeconds = Math.floor((now.getTime() - date.getTime()) / 1000)

  if (diffInSeconds < 60) {
    return '刚刚'
  } else if (diffInSeconds < 3600) {
    return `${Math.floor(diffInSeconds / 60)}分钟前`
  } else if (diffInSeconds < 86400) {
    return `${Math.floor(diffInSeconds / 3600)}小时前`
  } else if (diffInSeconds < 604800) {
    return `${Math.floor(diffInSeconds / 86400)}天前`
  } else {
    return date.toLocaleDateString('zh-CN')
  }
}

