'use client'

import React, { useState, useEffect } from 'react'
import AppLayout from '@/components/layout/AppLayout'
import { AtomicContextList } from '@/components/AtomicContextList'
import { SearchFilters } from '@/components/SearchFilters'
import { AtomicContextModal } from '@/components/AtomicContextModal'
import { mockApi, type AtomicContext } from '@/lib/mock-data'
import { HelpCircle } from 'lucide-react'
import { Button } from '@/components/ui/button'
import Link from 'next/link'

export default function HomePage() {
  const [atomicContexts, setAtomicContexts] = useState<AtomicContext[]>([])
  const [filteredContexts, setFilteredContexts] = useState<AtomicContext[]>([])
  const [loading, setLoading] = useState(true)
  const [selectedContext, setSelectedContext] = useState<AtomicContext | null>(null)

  // æœç´¢å’Œç­›é€‰çŠ¶æ€
  const [searchQuery, setSearchQuery] = useState('')
  const [selectedCategory, setSelectedCategory] = useState('all')
  const [selectedStatus, setSelectedStatus] = useState('all')

  // åŠ è½½æ•°æ®
  useEffect(() => {
    const loadData = async () => {
      try {
        const contexts = await mockApi.getAtomicContexts()
        setAtomicContexts(contexts)
        setFilteredContexts(contexts)
      } catch (error) {
        console.error('Failed to load atomic contexts:', error)
      } finally {
        setLoading(false)
      }
    }

    loadData()
  }, [])

  // å¤„ç†æœç´¢å’Œç­›é€‰
  useEffect(() => {
    let filtered = [...atomicContexts]

    // æœç´¢è¿‡æ»¤
    if (searchQuery) {
      const query = searchQuery.toLowerCase()
      filtered = filtered.filter(ctx => 
        ctx.name.toLowerCase().includes(query) ||
        ctx.description.toLowerCase().includes(query) ||
        ctx.tags.some(tag => tag.toLowerCase().includes(query))
      )
    }

    // åˆ†ç±»è¿‡æ»¤
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(ctx => ctx.category === selectedCategory)
    }

    // çŠ¶æ€è¿‡æ»¤
    if (selectedStatus !== 'all') {
      filtered = filtered.filter(ctx => ctx.status === selectedStatus)
    }

    setFilteredContexts(filtered)
  }, [atomicContexts, searchQuery, selectedCategory, selectedStatus])

  return (
    <AppLayout>
      <div className="space-y-6">
        {/* æœç´¢å’Œç­›é€‰åŒºåŸŸ */}
        <SearchFilters
          searchQuery={searchQuery}
          onSearchChange={setSearchQuery}
          selectedCategory={selectedCategory}
          onCategoryChange={setSelectedCategory}
          selectedStatus={selectedStatus}
          onStatusChange={setSelectedStatus}
          totalCount={atomicContexts.length}
          filteredCount={filteredContexts.length}
        />

        {/* åŸå­ä¸Šä¸‹æ–‡åˆ—è¡¨ */}
        <AtomicContextList
          contexts={filteredContexts}
          loading={loading}
          onViewDetails={setSelectedContext}
        />

        {/* è¯¦æƒ…æ¨¡æ€æ¡† */}
        {selectedContext && (
          <AtomicContextModal
            context={selectedContext}
            isOpen={!!selectedContext}
            onClose={() => setSelectedContext(null)}
          />
        )}

        {/* å³ä¸‹è§’æ–‡æ¡£æŒ‰é’® */}
        <Link href="/docs">
          <Button
            size="icon"
            className="fixed bottom-4 right-4 h-12 w-12 rounded-full shadow-lg z-50"
          >
            <HelpCircle className="h-5 w-5" />
            <span className="sr-only">æŸ¥çœ‹æ–‡æ¡£</span>
          </Button>
        </Link>
      </div>
    </AppLayout>
  )
}

