'use client'

import React from 'react'
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar'
import { Skeleton } from '@/components/ui/skeleton'
import { Eye, GitBranch, Copy, Clock, TrendingUp } from 'lucide-react'
import { type AtomicContext } from '@/lib/mock-data'
import { toast } from 'sonner'

interface AtomicContextListProps {
  contexts: AtomicContext[]
  loading: boolean
  onViewDetails: (context: AtomicContext) => void
}

export function AtomicContextList({ contexts, loading, onViewDetails }: AtomicContextListProps) {
  const handleUseContext = (context: AtomicContext) => {
    // æ¨¡æ‹Ÿå¤åˆ¶åˆ°å‰ªè´´æ¿
    navigator.clipboard.writeText(context.content)
    toast.success(`å·²å¤åˆ¶ "${context.name}" åˆ°å‰ªè´´æ¿`)
  }

  const handleViewVersions = (context: AtomicContext) => {
    toast.info(`æŸ¥çœ‹ "${context.name}" çš„ç‰ˆæœ¬å†å²`)
  }

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {Array.from({ length: 6 }).map((_, i) => (
          <Card key={i} className="h-[280px]">
            <CardHeader>
              <Skeleton className="h-6 w-3/4" />
              <Skeleton className="h-4 w-full" />
              <Skeleton className="h-4 w-2/3" />
            </CardHeader>
            <CardContent>
              <Skeleton className="h-4 w-full mb-2" />
              <Skeleton className="h-4 w-3/4 mb-4" />
              <div className="flex gap-2">
                <Skeleton className="h-6 w-16" />
                <Skeleton className="h-6 w-20" />
                <Skeleton className="h-6 w-12" />
              </div>
            </CardContent>
            <CardFooter>
              <Skeleton className="h-9 w-full" />
            </CardFooter>
          </Card>
        ))}
      </div>
    )
  }

  if (contexts.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center py-12 text-center">
        <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mb-4">
          <Eye className="w-8 h-8 text-muted-foreground" />
        </div>
        <h3 className="text-lg font-semibold mb-2">æ²¡æœ‰æ‰¾åˆ°åŒ¹é…çš„åŸå­ä¸Šä¸‹æ–‡</h3>
        <p className="text-muted-foreground mb-4">
          å°è¯•è°ƒæ•´æœç´¢å…³é”®è¯æˆ–ç­›é€‰æ¡ä»¶
        </p>
        <Button variant="outline" onClick={() => window.location.reload()}>
          é‡ç½®ç­›é€‰
        </Button>
      </div>
    )
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {contexts.map((context) => (
        <Card key={context.id} className="group hover:shadow-lg transition-all duration-200 animate-fade-in">
          <CardHeader className="pb-3">
            <div className="flex items-start justify-between">
              <div className="space-y-1 min-w-0 flex-1">
                <CardTitle className="text-lg leading-tight truncate">
                  {context.name}
                </CardTitle>
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Badge variant={getStatusVariant(context.status)} className="text-xs">
                    {getStatusLabel(context.status)}
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    v{context.version}
                  </Badge>
                </div>
              </div>
              <Badge variant="secondary" className="text-xs ml-2">
                {getCategoryLabel(context.category)}
              </Badge>
            </div>
            <CardDescription className="line-clamp-2 leading-relaxed">
              {context.description}
            </CardDescription>
          </CardHeader>

          <CardContent className="py-3">
            {/* æ ‡ç­¾ */}
            <div className="flex flex-wrap gap-1 mb-3">
              {context.tags.slice(0, 3).map((tag, index) => (
                <Badge key={index} variant="outline" className="text-xs px-2 py-0.5">
                  {tag}
                </Badge>
              ))}
              {context.tags.length > 3 && (
                <Badge variant="outline" className="text-xs px-2 py-0.5">
                  +{context.tags.length - 3}
                </Badge>
              )}
            </div>

            {/* ä½œè€…å’Œä½¿ç”¨ç»Ÿè®¡ */}
            <div className="flex items-center justify-between text-sm text-muted-foreground">
