'use client'

import ReactMarkdown from 'react-markdown'
import remarkGfm from 'remark-gfm'
import rehypeHighlight from 'rehype-highlight'
import { cn } from '@/lib/utils'
import 'highlight.js/styles/github-dark.css'

interface MarkdownRendererProps {
  content: string
  className?: string
}

export default function MarkdownRenderer({ content, className }: MarkdownRendererProps) {
  return (
    <div className={cn(
      "prose prose-lg dark:prose-invert max-w-none",
      "prose-headings:text-foreground prose-p:text-muted-foreground",
      "prose-strong:text-foreground prose-code:text-foreground",
      "prose-pre:bg-muted prose-pre:border prose-pre:border-border",
      "prose-blockquote:border-l-primary prose-blockquote:text-muted-foreground",
      "prose-li:text-muted-foreground prose-li:marker:text-muted-foreground",
      "prose-a:text-primary prose-a:no-underline hover:prose-a:underline",
      "prose-table:border-collapse prose-table:border-border",
      "prose-th:border prose-th:border-border prose-th:bg-muted prose-th:px-4 prose-th:py-2",
      "prose-td:border prose-td:border-border prose-td:px-4 prose-td:py-2",
      className
    )}>
      <ReactMarkdown
        remarkPlugins={[remarkGfm]}
        rehypePlugins={[rehypeHighlight]}
        components={{
          blockquote: ({ children, ...props }) => (
            <blockquote 
              className="border-l-4 border-primary/50 bg-muted/30 pl-4 py-2 my-4 rounded-r-lg"
              {...props}
            >
              {children}
            </blockquote>
          ),
          h1: ({ children, ...props }) => (
            <h1 className="text-3xl font-bold mb-6 text-foreground border-b border-border pb-2" {...props}>
              {children}
            </h1>
          ),
          h2: ({ children, ...props }) => (
            <h2 className="text-2xl font-semibold mt-8 mb-4 text-foreground border-b border-border pb-1" {...props}>
              {children}
            </h2>
          ),
          h3: ({ children, ...props }) => (
            <h3 className="text-xl font-semibold mt-6 mb-3 text-foreground" {...props}>
              {children}
            </h3>
          ),
          code: ({ inline, children, ...props }) => {
            if (inline) {
              return (
                <code 
                  className="bg-muted px-1.5 py-0.5 rounded text-sm font-mono text-foreground border"
                  {...props}
                >
                  {children}
                </code>
              )
            }
            return (
              <code {...props}>
                {children}
              </code>
            )
          },
          pre: ({ children, ...props }) => (
            <pre 
              className="bg-muted border border-border rounded-lg p-4 overflow-x-auto my-4"
              {...props}
            >
              {children}
            </pre>
          ),
          ul: ({ children, ...props }) => (
            <ul className="space-y-1 my-4" {...props}>
              {children}
            </ul>
          ),
          ol: ({ children, ...props }) => (
            <ol className="space-y-1 my-4" {...props}>
              {children}
            </ol>
          ),
          li: ({ children, ...props }) => (
            <li className="text-muted-foreground leading-relaxed" {...props}>
              {children}
            </li>
          ),
          table: ({ children, ...props }) => (
            <div className="overflow-x-auto my-4">
              <table className="min-w-full border-collapse border border-border rounded-lg" {...props}>
                {children}
              </table>
            </div>
          ),
          th: ({ children, ...props }) => (
            <th className="border border-border bg-muted px-4 py-2 text-left font-semibold text-foreground" {...props}>
              {children}
            </th>
          ),
          td: ({ children, ...props }) => (
            <td className="border border-border px-4 py-2 text-muted-foreground" {...props}>
              {children}
            </td>
          )
        }}
      >
        {content}
      </ReactMarkdown>
    </div>
  )
}

