  const [currentThinking, setCurrentThinking] = useState<ThinkingStep[]>([])
  const [currentToolCalls, setCurrentToolCalls] = useState<ToolCall[]>([])

  const simulateThinking = useCallback(() => {
    const thinkingSteps: ThinkingStep[] = []
    const numSteps = Math.floor(Math.random() * 3) + 2 // 2-4 steps

    for (let i = 0; i < numSteps; i++) {
      thinkingSteps.push({
        id: `thinking-${Date.now()}-${i}`,
        content: mockThinkingSteps[Math.floor(Math.random() * mockThinkingSteps.length)],
        timestamp: new Date(Date.now() + i * 500)
      })
    }

    return thinkingSteps
  }, [])

  const simulateToolCalls = useCallback(() => {
    if (Math.random() < 0.7) return [] // 70% chance of no tool calls

    const toolCalls: ToolCall[] = []
    const numCalls = Math.floor(Math.random() * 2) + 1 // 1-2 tool calls

    for (let i = 0; i < numCalls; i++) {
      const toolName = mockTools[Math.floor(Math.random() * mockTools.length)]
      toolCalls.push({
        id: `tool-${Date.now()}-${i}`,
        name: toolName,
        arguments: { query: "ç¤ºä¾‹å‚æ•°", type: "demo" },
        status: 'pending',
        timestamp: new Date(Date.now() + i * 800)
      })
    }

    return toolCalls
  }, [])

