    for (let i = 0; i < toolCalls.length; i++) {
      setCurrentToolCalls(prev => [...prev, toolCalls[i]])

      await new Promise(resolve => setTimeout(resolve, 1000))

      setCurrentToolCalls(prev => 
        prev.map(call => 
          call.id === toolCalls[i].id 
            ? { ...call, status: 'running' as const }
            : call
        )
      )

      await new Promise(resolve => setTimeout(resolve, 1500))

      setCurrentToolCalls(prev => 
        prev.map(call => 
          call.id === toolCalls[i].id 
            ? { 
                ...call, 
                status: 'completed' as const,
                result: `${call.name} æ‰§è¡ŒæˆåŠŸ`
              }
            : call
        )
      )
    }

    // ç”Ÿæˆå›å¤
    await new Promise(resolve => setTimeout(resolve, 1000))

    const assistantMessage: Message = {
      id: (Date.now() + 1).toString(),
      role: 'assistant',
      content: mockResponses[Math.floor(Math.random() * mockResponses.length)],
      timestamp: new Date(),
      toolCalls: toolCalls.length > 0 ? toolCalls : undefined,
      thinking: thinkingSteps.length > 0 ? thinkingSteps : undefined
    }

    setMessages(prev => [...prev, assistantMessage])
    setIsLoading(false)
    setCurrentThinking([])
    setCurrentToolCalls([])
  }, [isLoading, simulateThinking, simulateToolCalls])

  const clearMessages = useCallback(() => {
    setMessages([
      {
        id: '1',
        role: 'assistant',
        content: 'ä½ å¥½ï¼æˆ‘æ˜¯Claude CodeåŠ©æ‰‹ï¼Œå…·å¤‡å·¥å…·è°ƒç”¨å’Œæ·±åº¦æ€è€ƒèƒ½åŠ›ã€‚æœ‰ä»€ä¹ˆå¯ä»¥å¸®åŠ©ä½ çš„å—ï¼Ÿ',
        timestamp: new Date(),
      },
    ])
    setCurrentThinking([])
    setCurrentToolCalls([])
  }, [])

  const exportChat = useCallback(() => {
    const chatData = {
      messages: messages,
      exportTime: new Date().toISOString(),
      totalMessages: messages.length
    }

    const blob = new Blob([JSON.stringify(chatData, null, 2)], {
      type: 'application/json'
    })

    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `chat-export-${new Date().toISOString().slice(0, 10)}.json`
    document.body.appendChild(a)
    a.click()
    document.body.removeChild(a)
    URL.revokeObjectURL(url)
  }, [messages])

  const searchMessages = useCallback((query: string) => {
    if (!query.trim()) return messages

    return messages.filter(message => 
      message.content.toLowerCase().includes(query.toLowerCase())
    )
  }, [messages])

  return {
    messages,
    isLoading,
    currentThinking,
    currentToolCalls,
    sendMessage,
    clearMessages,
    exportChat,
    searchMessages
  }
}

