'use client'

import { useState } from 'react'
import { ChevronDown, ChevronRight, Wrench, CheckCircle, XCircle, Clock, Loader2 } from 'lucide-react'
import { cn } from '@/lib/utils'
import { ToolCall } from '@/hooks/useAdvancedChat'

interface ToolCallsPanelProps {
  toolCalls: ToolCall[]
  currentToolCalls: ToolCall[]
  isVisible: boolean
}

const toolIcons: Record<string, React.ComponentType<{ className?: string }>> = {
  search_web: ({ className }) => <div className={cn("rounded bg-blue-100 p-1 dark:bg-blue-900", className)}>ğŸ”</div>,
  run_code: ({ className }) => <div className={cn("rounded bg-green-100 p-1 dark:bg-green-900", className)}>ğŸ’»</div>,
  analyze_file: ({ className }) => <div className={cn("rounded bg-purple-100 p-1 dark:bg-purple-900", className)}>ğŸ“Š</div>,
  create_chart: ({ className }) => <div className={cn("rounded bg-orange-100 p-1 dark:bg-orange-900", className)}>ğŸ“ˆ</div>,
  translate_text: ({ className }) => <div className={cn("rounded bg-pink-100 p-1 dark:bg-pink-900", className)}>ğŸŒ</div>,
  generate_image: ({ className }) => <div className={cn("rounded bg-yellow-100 p-1 dark:bg-yellow-900", className)}>ğŸ¨</div>,
  send_email: ({ className }) => <div className={cn("rounded bg-red-100 p-1 dark:bg-red-900", className)}>ğŸ“§</div>,
  schedule_meeting: ({ className }) => <div className={cn("rounded bg-indigo-100 p-1 dark:bg-indigo-900", className)}>ğŸ“…</div>,
}

function ToolCallItem({ toolCall }: { toolCall: ToolCall }) {
  const [isExpanded, setIsExpanded] = useState(false)
  const ToolIcon = toolIcons[toolCall.name] || Wrench

  const getStatusIcon = () => {
    switch (toolCall.status) {
      case 'pending':
        return <Clock className="h-4 w-4 text-muted-foreground" />
      case 'running':
        return <Loader2 className="h-4 w-4 animate-spin text-blue-500" />
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-500" />
      case 'error':
        return <XCircle className="h-4 w-4 text-red-500" />
    }
  }

  const getStatusColor = () => {
    switch (toolCall.status) {
      case 'pending':
        return 'border-gray-200 dark:border-gray-700'
      case 'running':
        return 'border-blue-200 dark:border-blue-700 bg-blue-50/50 dark:bg-blue-950/50'
      case 'completed':
        return 'border-green-200 dark:border-green-700 bg-green-50/50 dark:bg-green-950/50'
      case 'error':
        return 'border-red-200 dark:border-red-700 bg-red-50/50 dark:bg-red-950/50'
    }
  }

  return (
    <div className={cn("rounded-lg border p-3 transition-all", getStatusColor())}>
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <ToolIcon className="h-5 w-5" />
          <div>
            <h4 className="font-medium text-sm">{toolCall.name.replace('_', ' ')}</h4>
            <p className="text-xs text-muted-foreground">
              {toolCall.timestamp.toLocaleTimeString()}
            </p>
          </div>
        </div>
        <div className="flex items-center gap-2">
          {getStatusIcon()}
          <button
            onClick={() => setIsExpanded(!isExpanded)}
            className="rounded p-1 hover:bg-accent"
          >
            {isExpanded ? (
              <ChevronDown className="h-4 w-4" />
            ) : (
              <ChevronRight className="h-4 w-4" />
            )}
          </button>
        </div>
      </div>

      {isExpanded && (
        <div className="mt-3 space-y-2 border-t pt-3">
          <div>
            <h5 className="font-medium text-xs text-muted-foreground mb-1">å‚æ•°</h5>
            <pre className="text-xs bg-muted p-2 rounded overflow-x-auto">
              {JSON.stringify(toolCall.arguments, null, 2)}
            </pre>
          </div>
          {toolCall.result && (
            <div>
              <h5 className="font-medium text-xs text-muted-foreground mb-1">ç»“æœ</h5>
              <div className="text-xs bg-muted p-2 rounded">
                {typeof toolCall.result === 'string' ? toolCall.result : JSON.stringify(toolCall.result, null, 2)}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  )
}

export function ToolCallsPanel({ toolCalls, currentToolCalls, isVisible }: ToolCallsPanelProps) {
  const [isCollapsed, setIsCollapsed] = useState(false)
  const allToolCalls = [...toolCalls, ...currentToolCalls]

  if (!isVisible || allToolCalls.length === 0) {
    return null
  }

  return (
    <div className="border-l bg-background/95 backdrop-blur-sm">
      <div className="border-b p-3">
        <button
          onClick={() => setIsCollapsed(!isCollapsed)}
          className="flex w-full items-center justify-between text-sm font-medium"
        >
          <div className="flex items-center gap-2">
            <Wrench className="h-4 w-4" />
            <span>å·¥å…·è°ƒç”¨ ({allToolCalls.length})</span>
          </div>
          {isCollapsed ? (
            <ChevronRight className="h-4 w-4" />
          ) : (
            <ChevronDown className="h-4 w-4" />
          )}
        </button>
      </div>

      {!isCollapsed && (
        <div className="max-h-96 overflow-y-auto p-3">
          <div className="space-y-3">
            {allToolCalls.map((toolCall) => (
              <ToolCallItem key={toolCall.id} toolCall={toolCall} />
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

