// 原子上下文数据类型定义

export interface AtomVersion {
  id: string
  version: string // e.g., 'v2.1'
  content: string
  description: string
  createdAt: string
  author: string
  changelog: string
}

export interface ChangeLog {
  id: string
  version: string
  action: 'created' | 'updated' | 'archived' | 'restored'
  description: string
  timestamp: string
  author: string
}

export interface AtomContext {
  id: string
  title: string
  content: string
  description: string
  version: string // 当前版本，e.g., 'v2.1'
  tags: string[] // 可编辑功能标签
  category: 'role' | 'principle' | 'strategy' | 'template' | 'other'
  status: 'active' | 'draft' | 'archived'
  author: string
  createdAt: string
  updatedAt: string
  usageCount: number
  versions: AtomVersion[] // 版本历史
  changelog: ChangeLog[] // 变更日志
}

// 搜索和筛选相关类型
export interface SearchFilters {
  query: string
  category: string
  status: string
  sortBy: 'updatedAt' | 'createdAt' | 'usageCount' | 'title'
  sortOrder: 'asc' | 'desc'
}

// API响应类型
export interface APIResponse<T> {
  success: boolean
  data: T
  message?: string
}

// 统计数据类型
export interface UsageStats {
  totalAtoms: number
  activeAtoms: number
  totalUsage: number
  categoryCounts: Record<string, number>
  recentActivity: {
    atomId: string
    action: string
    timestamp: string
  }[]
}

