'use client'

import { useState } from 'react'
import { AtomContext } from '@/types'
import { 
  Dialog, 
  DialogContent, 
  DialogDescription, 
  DialogHeader, 
  DialogTitle 
} from '@/components/ui/dialog'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { X, Plus, Tag } from 'lucide-react'
import { toast } from 'sonner'

interface EditableTagsDialogProps {
  atom: AtomContext | null
  open: boolean
  onOpenChange: (open: boolean) => void
  onSave: (atomId: string, tags: string[]) => void
}

export default function EditableTagsDialog({ 
  atom, 
  open, 
  onOpenChange, 
  onSave 
}: EditableTagsDialogProps) {
  const [tags, setTags] = useState<string[]>([])
  const [newTag, setNewTag] = useState('')

  // 当对话框打开时，初始化标签
  const handleOpenChange = (isOpen: boolean) => {
    if (isOpen && atom) {
      setTags([...atom.tags])
      setNewTag('')
    }
    onOpenChange(isOpen)
  }

  const addTag = () => {
    if (newTag.trim() && !tags.includes(newTag.trim())) {
      setTags([...tags, newTag.trim()])
      setNewTag('')
    }
  }

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove))
  }

  const handleSave = () => {
    if (atom) {
      onSave(atom.id, tags)
      toast.success('标签更新成功！', {
        description: `已为"${atom.title}"更新功能标签`,
        duration: 3000,
      })
      onOpenChange(false)
    }
  }

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      e.preventDefault()
      addTag()
    }
  }

  if (!atom) return null

  return (
    <Dialog open={open} onOpenChange={handleOpenChange}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Tag className="h-5 w-5" />
            编辑功能标签
          </DialogTitle>
          <DialogDescription>
            为原子"{atom.title}"编辑功能标签，方便分类和搜索
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {/* 当前标签展示 */}
          <div className="space-y-2">
            <label className="text-sm font-medium">当前标签</label>
            <div className="flex flex-wrap gap-2 min-h-[40px] p-2 border rounded-md">
              {tags.length === 0 ? (
                <span className="text-muted-foreground text-sm">暂无标签</span>
              ) : (
                tags.map((tag, index) => (
                  <Badge 
                    key={index} 
                    variant="secondary" 
                    className="flex items-center gap-1"
                  >
                    {tag}
                    <X 
                      className="h-3 w-3 cursor-pointer hover:text-destructive" 
                      onClick={() => removeTag(tag)}
                    />
                  </Badge>
                ))
              )}
            </div>
          </div>

          {/* 添加新标签 */}
          <div className="space-y-2">
            <label className="text-sm font-medium">添加新标签</label>
            <div className="flex gap-2">
              <Input
                value={newTag}
                onChange={(e) => setNewTag(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="输入标签名称..."
                className="flex-1"
              />
              <Button 
                onClick={addTag}
                disabled={!newTag.trim() || tags.includes(newTag.trim())}
                size="sm"
              >
                <Plus className="h-4 w-4" />
                添加
              </Button>
            </div>
          </div>

          {/* 常用标签建议 */}
          <div className="space-y-2">
            <label className="text-sm font-medium">常用标签</label>
            <div className="flex flex-wrap gap-2">
              {['角色定义', '技术栈', '交互设计', '用户体验', '代码模板', '开发规范', '设计原则'].map((suggestedTag) => (
                <Badge 
                  key={suggestedTag}
                  variant="outline" 
                  className="cursor-pointer hover:bg-accent"
                  onClick={() => {
                    if (!tags.includes(suggestedTag)) {
                      setTags([...tags, suggestedTag])
                    }
                  }}
                >
                  + {suggestedTag}
                </Badge>
              ))}
            </div>
          </div>
        </div>

        {/* 操作按钮 */}
        <div className="flex justify-end gap-2 pt-4">
          <Button variant="outline" onClick={() => onOpenChange(false)}>
            取消
          </Button>
          <Button onClick={handleSave}>
            保存标签
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  )
}

