'use client'

import { useState, useEffect } from 'react'
import { AtomContext, SearchFilters } from '@/types'
import { MockAPI } from '@/mock/api'
import AtomCard from '@/components/atoms/AtomCard'
import SearchAndFilter from '@/components/atoms/SearchAndFilter'
import EditableTagsDialog from '@/components/atoms/EditableTagsDialog'
import VersionHistorySheet from '@/components/atoms/VersionHistorySheet'
import VersionCompareDialog from '@/components/atoms/VersionCompareDialog'
import AtomDetailSheet from '@/components/atoms/AtomDetailSheet'

export default function Home() {
  const [atoms, setAtoms] = useState<AtomContext[]>([])
  const [filteredAtoms, setFilteredAtoms] = useState<AtomContext[]>([])
  const [filters, setFilters] = useState<SearchFilters>({
    query: '',
    category: 'all',
    status: 'all',
    sortBy: 'updatedAt',
    sortOrder: 'desc'
  })

  // 对话框和面板状态
  const [selectedAtom, setSelectedAtom] = useState<AtomContext | null>(null)
  const [showTagsDialog, setShowTagsDialog] = useState(false)
  const [showVersionHistory, setShowVersionHistory] = useState(false)
  const [showVersionCompare, setShowVersionCompare] = useState(false)
  const [showAtomDetail, setShowAtomDetail] = useState(false)

  // 初始化数据
  useEffect(() => {
    const loadAtoms = async () => {
      const response = await MockAPI.getAtoms()
      if (response.success) {
        setAtoms(response.data)
      }
    }
    loadAtoms()
  }, [])

  // 搜索和筛选
  useEffect(() => {
    const applyFilters = async () => {
      const response = await MockAPI.searchAtoms(filters)
      if (response.success) {
        setFilteredAtoms(response.data)
      }
    }
    applyFilters()
  }, [filters, atoms])

  // 处理标签编辑
  const handleEditTags = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setShowTagsDialog(true)
  }

  const handleSaveTags = async (atomId: string, tags: string[]) => {
    const response = await MockAPI.updateAtomTags(atomId, tags)
    if (response.success) {
      // 更新本地状态
      setAtoms(prev => prev.map(atom => 
        atom.id === atomId ? { ...atom, tags } : atom
      ))
    }
  }

  // 处理版本历史
  const handleViewVersions = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setShowVersionHistory(true)
  }

  // 处理版本对比
  const handleCompareVersions = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setShowVersionCompare(true)
  }

  // 处理查看详情
  const handleViewDetails = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setShowAtomDetail(true)
  }

  return (
    <div className="space-y-6">
      {/* 搜索和筛选组件 */}
      <SearchAndFilter
        filters={filters}
        onFiltersChange={setFilters}
        resultCount={filteredAtoms.length}
      />

      {/* 原子卡片网格 */}
      {filteredAtoms.length === 0 ? (
        <div className="text-center py-12">
          <div className="text-muted-foreground">
            {filters.query || filters.category !== 'all' || filters.status !== 'all' 
              ? '没有找到匹配的原子' 
              : '暂无原子数据'
            }
          </div>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredAtoms.map((atom) => (
            <AtomCard
              key={atom.id}
              atom={atom}
              onViewDetails={handleViewDetails}
              onViewVersions={handleViewVersions}
              onCompareVersions={handleCompareVersions}
              onEditTags={handleEditTags}
            />
          ))}
        </div>
      )}

      {/* 对话框和面板 */}
      <EditableTagsDialog
        atom={selectedAtom}
        open={showTagsDialog}
        onOpenChange={setShowTagsDialog}
        onSave={handleSaveTags}
      />

      <VersionHistorySheet
        atom={selectedAtom}
        open={showVersionHistory}
        onOpenChange={setShowVersionHistory}
        onCompareVersions={() => {
          setShowVersionHistory(false)
          setShowVersionCompare(true)
        }}
      />

      <VersionCompareDialog
        atom={selectedAtom}
        open={showVersionCompare}
        onOpenChange={setShowVersionCompare}
      />

      <AtomDetailSheet
        atom={selectedAtom}
        open={showAtomDetail}
        onOpenChange={setShowAtomDetail}
      />
    </div>
  )
}

