'use client'

import { SearchFilters } from '@/types'
import { Input } from '@/components/ui/input'
import { Button } from '@/components/ui/button'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Badge } from '@/components/ui/badge'
import { 
  Search, 
  Filter, 
  X, 
  Plus,
  SortAsc,
  SortDesc
} from 'lucide-react'
import { categoryOptions, statusOptions, sortOptions } from '@/mock/data'

interface SearchAndFilterProps {
  filters: SearchFilters
  onFiltersChange: (filters: SearchFilters) => void
  resultCount: number
  onCreateNew?: () => void
}

export default function SearchAndFilter({ 
  filters, 
  onFiltersChange, 
  resultCount,
  onCreateNew 
}: SearchAndFilterProps) {

  const updateFilter = (key: keyof SearchFilters, value: string) => {
    onFiltersChange({
      ...filters,
      [key]: value
    })
  }

  const clearFilters = () => {
    onFiltersChange({
      query: '',
      category: 'all',
      status: 'all',
      sortBy: 'updatedAt',
      sortOrder: 'desc'
    })
  }

  const hasActiveFilters = filters.query || filters.category !== 'all' || filters.status !== 'all'

  const toggleSortOrder = () => {
    onFiltersChange({
      ...filters,
      sortOrder: filters.sortOrder === 'desc' ? 'asc' : 'desc'
    })
  }

  return (
    <div className="space-y-4">
      {/* 顶部操作栏 */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <h1 className="text-2xl font-bold">原子上下文库</h1>
          <Badge variant="secondary">{resultCount} 个原子</Badge>
        </div>
        {onCreateNew && (
          <Button onClick={onCreateNew} className="flex items-center gap-2">
            <Plus className="h-4 w-4" />
            新建原子
          </Button>
        )}
      </div>

      {/* 搜索和筛选栏 */}
      <div className="flex flex-col sm:flex-row gap-4">
        {/* 搜索框 */}
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
          <Input
            placeholder="搜索原子标题、描述、标签或内容..."
            value={filters.query}
            onChange={(e) => updateFilter('query', e.target.value)}
            className="pl-10"
          />
        </div>

        {/* 分类筛选 */}
        <Select value={filters.category} onValueChange={(value) => updateFilter('category', value)}>
          <SelectTrigger className="w-full sm:w-[180px]">
            <SelectValue placeholder="选择分类" />
          </SelectTrigger>
          <SelectContent>
            {categoryOptions.map((option) => (
              <SelectItem key={option.value} value={option.value}>
                {option.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        {/* 状态筛选 */}
        <Select value={filters.status} onValueChange={(value) => updateFilter('status', value)}>
          <SelectTrigger className="w-full sm:w-[180px]">
            <SelectValue placeholder="选择状态" />
          </SelectTrigger>
          <SelectContent>
            {statusOptions.map((option) => (
              <SelectItem key={option.value} value={option.value}>
                {option.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        {/* 排序方式 */}
        <div className="flex gap-2">
          <Select value={filters.sortBy} onValueChange={(value) => updateFilter('sortBy', value as any)}>
            <SelectTrigger className="w-full sm:w-[140px]">
              <SelectValue placeholder="排序方式" />
            </SelectTrigger>
            <SelectContent>
              {sortOptions.map((option) => (
                <SelectItem key={option.value} value={option.value}>
                  {option.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          <Button
            variant="outline"
            size="icon"
            onClick={toggleSortOrder}
            title={filters.sortOrder === 'desc' ? '降序' : '升序'}
          >
            {filters.sortOrder === 'desc' ? (
              <SortDesc className="h-4 w-4" />
            ) : (
              <SortAsc className="h-4 w-4" />
            )}
          </Button>
        </div>
      </div>

      {/* 活跃筛选条件和清空按钮 */}
      {hasActiveFilters && (
        <div className="flex items-center gap-2 flex-wrap">
          <Filter className="h-4 w-4 text-muted-foreground" />
          <span className="text-sm text-muted-foreground">当前筛选：</span>

          {filters.query && (
            <Badge variant="secondary" className="flex items-center gap-1">
              搜索: "{filters.query}"
              <X 
                className="h-3 w-3 cursor-pointer hover:text-destructive" 
                onClick={() => updateFilter('query', '')}
              />
            </Badge>
          )}

          {filters.category !== 'all' && (
            <Badge variant="secondary" className="flex items-center gap-1">
              分类: {categoryOptions.find(opt => opt.value === filters.category)?.label}
              <X 
                className="h-3 w-3 cursor-pointer hover:text-destructive" 
                onClick={() => updateFilter('category', 'all')}
              />
            </Badge>
          )}

          {filters.status !== 'all' && (
            <Badge variant="secondary" className="flex items-center gap-1">
              状态: {statusOptions.find(opt => opt.value === filters.status)?.label}
              <X 
                className="h-3 w-3 cursor-pointer hover:text-destructive" 
                onClick={() => updateFilter('status', 'all')}
              />
            </Badge>
          )}

          <Button
            variant="ghost"
            size="sm"
            onClick={clearFilters}
            className="h-6 px-2 text-xs"
          >
            清空筛选
          </Button>
        </div>
      )}

      {/* 搜索结果提示 */}
      {filters.query && (
        <div className="text-sm text-muted-foreground">
          为"{filters.query}"找到 {resultCount} 个匹配结果
        </div>
      )}
    </div>
  )
}

