import { AtomContext, SearchFilters, APIResponse } from '@/types'
import { mockAtoms } from './data'

// 瞬时响应的Mock API类
export class MockAPI {
  // 获取所有原子数据
  static getAtoms = (): Promise<APIResponse<AtomContext[]>> => {
    return Promise.resolve({
      success: true,
      data: mockAtoms
    })
  }

  // 根据ID获取单个原子
  static getAtomById = (id: string): Promise<APIResponse<AtomContext | null>> => {
    const atom = mockAtoms.find(atom => atom.id === id)
    return Promise.resolve({
      success: true,
      data: atom || null
    })
  }

  // 搜索和筛选原子
  static searchAtoms = (filters: SearchFilters): Promise<APIResponse<AtomContext[]>> => {
    let filteredAtoms = [...mockAtoms]

    // 按查询字符串筛选
    if (filters.query) {
      const query = filters.query.toLowerCase()
      filteredAtoms = filteredAtoms.filter(atom =>
        atom.title.toLowerCase().includes(query) ||
        atom.description.toLowerCase().includes(query) ||
        atom.tags.some(tag => tag.toLowerCase().includes(query)) ||
        atom.content.toLowerCase().includes(query)
      )
    }

    // 按分类筛选
    if (filters.category && filters.category !== 'all') {
      filteredAtoms = filteredAtoms.filter(atom => atom.category === filters.category)
    }

    // 按状态筛选
    if (filters.status && filters.status !== 'all') {
      filteredAtoms = filteredAtoms.filter(atom => atom.status === filters.status)
    }

    // 排序
    filteredAtoms.sort((a, b) => {
      const aValue = a[filters.sortBy as keyof AtomContext]
      const bValue = b[filters.sortBy as keyof AtomContext]

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        const comparison = aValue.localeCompare(bValue)
        return filters.sortOrder === 'desc' ? -comparison : comparison
      }

      if (typeof aValue === 'number' && typeof bValue === 'number') {
        const comparison = aValue - bValue
        return filters.sortOrder === 'desc' ? -comparison : comparison
      }

      return 0
    })

    return Promise.resolve({
      success: true,
      data: filteredAtoms
    })
  }

  // 更新原子标签
  static updateAtomTags = (id: string, tags: string[]): Promise<APIResponse<AtomContext>> => {
    const atomIndex = mockAtoms.findIndex(atom => atom.id === id)
    if (atomIndex === -1) {
      return Promise.resolve({
        success: false,
        data: null as any,
        message: '原子不存在'
      })
    }

    mockAtoms[atomIndex] = {
      ...mockAtoms[atomIndex],
      tags: [...tags],
      updatedAt: new Date().toISOString().split('T')[0]
    }

    return Promise.resolve({
      success: true,
      data: mockAtoms[atomIndex]
    })
  }

  // 模拟使用原子（增加使用计数）
  static useAtom = (id: string): Promise<APIResponse<{ message: string }>> => {
    const atomIndex = mockAtoms.findIndex(atom => atom.id === id)
    if (atomIndex !== -1) {
      mockAtoms[atomIndex].usageCount += 1
    }

    return Promise.resolve({
      success: true,
      data: { message: '原子已成功应用到项目中！' }
    })
  }

  // 获取原子版本历史
  static getAtomVersions = (id: string): Promise<APIResponse<AtomContext['versions']>> => {
    const atom = mockAtoms.find(atom => atom.id === id)
    return Promise.resolve({
      success: true,
      data: atom?.versions || []
    })
  }

  // 对比两个版本
  static compareVersions = (atomId: string, version1: string, version2: string): Promise<APIResponse<{
    version1: { version: string; content: string }
    version2: { version: string; content: string }
  }>> => {
    const atom = mockAtoms.find(atom => atom.id === atomId)
    if (!atom) {
      return Promise.resolve({
        success: false,
        data: null as any,
        message: '原子不存在'
      })
    }

    const v1 = atom.versions.find(v => v.version === version1)
    const v2 = atom.versions.find(v => v.version === version2)

    if (!v1 || !v2) {
      return Promise.resolve({
        success: false,
        data: null as any,
        message: '版本不存在'
      })
    }

    return Promise.resolve({
      success: true,
      data: {
        version1: { version: v1.version, content: v1.content },
        version2: { version: v2.version, content: v2.content }
      }
    })
  }

  // 删除原子
  static deleteAtom = (id: string): Promise<APIResponse<{ message: string }>> => {
    const atomIndex = mockAtoms.findIndex(atom => atom.id === id)
    if (atomIndex !== -1) {
      mockAtoms.splice(atomIndex, 1)
    }

    return Promise.resolve({
      success: true,
      data: { message: '原子已成功删除！' }
    })
  }

  // 创建新原子
  static createAtom = (atomData: Partial<AtomContext>): Promise<APIResponse<AtomContext>> => {
    const newAtom: AtomContext = {
      id: `atom-${Date.now()}`,
      title: atomData.title || '新原子',
      content: atomData.content || '',
      description: atomData.description || '',
      version: 'v1.0',
      tags: atomData.tags || [],
      category: atomData.category || 'other',
      status: 'draft',
      author: 'Current User',
      createdAt: new Date().toISOString().split('T')[0],
      updatedAt: new Date().toISOString().split('T')[0],
      usageCount: 0,
      versions: [{
        id: `${Date.now()}-v1`,
        version: 'v1.0',
        content: atomData.content || '',
        description: '初始版本',
        createdAt: new Date().toISOString().split('T')[0],
        author: 'Current User',
        changelog: '创建原子'
      }],
      changelog: [{
        id: `${Date.now()}-c1`,
        version: 'v1.0',
        action: 'created',
        description: '创建新原子',
        timestamp: new Date().toISOString(),
        author: 'Current User'
      }]
    }

    mockAtoms.push(newAtom)

    return Promise.resolve({
      success: true,
      data: newAtom
    })
  }
}

