'use client'
import { useState, useRef, useEffect } from 'react'
import { Tag, Input, Space, Tooltip } from 'antd'
import { PlusOutlined, EditOutlined } from '@ant-design/icons'

interface EditableTagsProps {
  tags: string[]
  onChange: (newTags: string[]) => void
  readonly?: boolean
  maxTags?: number
}

export const EditableTags: React.FC<EditableTagsProps> = ({
  tags,
  onChange,
  readonly = false,
  maxTags = 10
}) => {
  const [editMode, setEditMode] = useState(false)
  const [inputVisible, setInputVisible] = useState(false)
  const [inputValue, setInputValue] = useState('')
  // eslint-disable-next-line @typescript-eslint/no-explicit-any  
  const inputRef = useRef<any>(null)

  useEffect(() => {
    if (inputVisible && inputRef.current) {
      inputRef.current.focus()
    }
  }, [inputVisible])

  const handleClose = (removedTag: string) => {
    const newTags = tags.filter(tag => tag !== removedTag)
    onChange(newTags)
  }

  const showInput = () => {
    setInputVisible(true)
  }

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(e.target.value)
  }

  const handleInputConfirm = () => {
    if (inputValue && !tags.includes(inputValue) && tags.length < maxTags) {
      onChange([...tags, inputValue])
    }
    setInputVisible(false)
    setInputValue('')
  }

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleInputConfirm()
    }
  }

  if (readonly) {
    return (
      <Space wrap>
        {tags.map(tag => (
          <Tag key={tag} color="blue">
            {tag}
          </Tag>
        ))}
      </Space>
    )
  }

  return (
    <div
      style={{
        border: editMode ? '1px dashed #d9d9d9' : 'none',
        padding: editMode ? '8px' : '0',
        borderRadius: '6px',
        position: 'relative'
      }}
      onMouseEnter={() => setEditMode(true)}
      onMouseLeave={() => setEditMode(false)}
    >
      <Space wrap>
        {tags.map(tag => (
          <Tag
            key={tag}
            closable={editMode}
            onClose={() => handleClose(tag)}
            color="blue"
          >
            {tag}
          </Tag>
        ))}

        {inputVisible ? (
          <Input
            ref={inputRef}
            type="text"
            size="small"
            style={{ width: 120 }}
            value={inputValue}
            onChange={handleInputChange}
            onBlur={handleInputConfirm}
            onKeyPress={handleKeyPress}
            placeholder="è¾“å…¥æ ‡ç­¾å"
          />
        ) : (
          editMode && tags.length < maxTags && (
            <Tag
              onClick={showInput}
              style={{
                borderStyle: 'dashed',
                cursor: 'pointer'
              }}
            >
              <PlusOutlined /> æ·»åŠ æ ‡ç­¾
            </Tag>
          )
        )}
      </Space>

      {!editMode && (
        <Tooltip title="ç‚¹å‡»ç¼–è¾‘æ ‡ç­¾">
          <EditOutlined
            style={{
              position: 'absolute',
              top: -8,
              right: -8,
              opacity: 0.5,
              cursor: 'pointer'
            }}
            onClick={() => setEditMode(true)}
          />
        </Tooltip>
      )}
    </div>
  )
}

