  Col
} from 'antd'
import { 
  SearchOutlined, 
  PlusOutlined, 
  EditOutlined, 
  DeleteOutlined,
  UserOutlined,
  ReloadOutlined
} from '@ant-design/icons'

const { Search } = Input
const { Option } = Select
const { Text } = Typography

// Mockæ•°æ®
const initialUsers = [
  {
    id: '1',
    name: 'å¼ ä¸‰',
    email: 'zhangsan@example.com',
    role: 'admin',
    status: 'active',
    avatar: '',
    department: 'æŠ€æœ¯éƒ¨',
    joinDate: '2023-01-15'
  },
  {
    id: '2',
    name: 'æå››',
    email: 'lisi@example.com',
    role: 'user',
    status: 'active',
    avatar: '',
    department: 'äº§å“éƒ¨',
    joinDate: '2023-03-20'
  },
  {
    id: '3',
    name: 'ç‹äº”',
    email: 'wangwu@example.com',
    role: 'editor',
    status: 'inactive',
    avatar: '',
    department: 'è®¾è®¡éƒ¨',
    joinDate: '2023-05-10'
  }
]

export default function AntdUserManager() {
  const [users, setUsers] = useState(initialUsers)
  const [loading, setLoading] = useState(false)
  const [searchText, setSearchText] = useState('')
  const [modalVisible, setModalVisible] = useState(false)
  const [editingUser, setEditingUser] = useState(null)
  const [form] = Form.useForm()

  // ç­›é€‰ç”¨æˆ·
  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchText.toLowerCase()) ||
    user.email.toLowerCase().includes(searchText.toLowerCase())
  )

  // çŠ¶æ€æ ‡ç­¾é¢œè‰²
  const getStatusColor = (status: string) => {
    return status === 'active' ? 'success' : 'default'
  }

  // è§’è‰²æ ‡ç­¾é¢œè‰²
  const getRoleColor = (role: string) => {
    const colors = {
      admin: 'red',
      editor: 'blue',
      user: 'green'
    }
    return colors[role] || 'default'
  }

  // æ·»åŠ /ç¼–è¾‘ç”¨æˆ·
  const handleSubmit = async (values: any) => {
    setLoading(true)
    try {
      await new Promise(resolve => setTimeout(resolve, 500)) // æ¨¡æ‹ŸAPIè°ƒç”¨

      if (editingUser) {
        // ç¼–è¾‘
        setUsers(prev => prev.map(user => 
          user.id === editingUser.id ? { ...user, ...values } : user
        ))
        message.success('ç”¨æˆ·æ›´æ–°æˆåŠŸ')
      } else {
        // æ–°å¢
        const newUser = {
          id: Date.now().toString(),
          ...values,
          avatar: '',
          joinDate: new Date().toISOString().split('T')[0]
        }
        setUsers(prev => [...prev, newUser])
        message.success('ç”¨æˆ·åˆ›å»ºæˆåŠŸ')
      }

      setModalVisible(false)
      setEditingUser(null)
      form.resetFields()
    } catch (error) {
      message.error('æ“ä½œå¤±è´¥')
    } finally {
      setLoading(false)
    }
  }

  // åˆ é™¤ç”¨æˆ·
  const handleDelete = async (id: string) => {
    setLoading(true)
    try {
      await new Promise(resolve => setTimeout(resolve, 300))
      setUsers(prev => prev.filter(user => user.id !== id))
      message.success('ç”¨æˆ·åˆ é™¤æˆåŠŸ')
    } catch (error) {
      message.error('åˆ é™¤å¤±è´¥')
    } finally {
      setLoading(false)
    }
  }

  // ç¼–è¾‘ç”¨æˆ·
  const handleEdit = (user: any) => {
    setEditingUser(user)
    form.setFieldsValue(user)
    setModalVisible(true)
  }

  // æ–°å¢ç”¨æˆ·
  const handleAdd = () => {
    setEditingUser(null)
    form.resetFields()
    setModalVisible(true)
  }

  // è¡¨æ ¼åˆ—å®šä¹‰
  const columns = [
    {
      title: 'ç”¨æˆ·',
      dataIndex: 'name',
      key: 'name',
      render: (text: string, record: any) => (
        <Space>
          <Avatar icon={<UserOutlined />} src={record.avatar} />
          <div>
            <div>{text}</div>
            <Text type="secondary" style={{ fontSize: '12px' }}>
              {record.email}
            </Text>
          </div>
        </Space>
      )
    },
    {
      title: 'è§’è‰²',
      dataIndex: 'role',
      key: 'role',
      render: (role: string) => (
        <Tag color={getRoleColor(role)}>
          {role === 'admin' ? 'ç®¡ç†å‘˜' : role === 'editor' ? 'ç¼–è¾‘è€…' : 'æ™®é€šç”¨æˆ·'}
        </Tag>
      )
    },
    {
      title: 'éƒ¨é—¨',
      dataIndex: 'department',
      key: 'department'
    },
    {
      title: 'çŠ¶æ€',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getStatusColor(status)}>
          {status === 'active' ? 'æ´»è·ƒ' : 'éæ´»è·ƒ'}
        </Tag>
      )
    },
    {
      title: 'åŠ å…¥æ—¶é—´',
      dataIndex: 'joinDate',
      key: 'joinDate'
    },
    {
      title: 'æ“ä½œ',
      key: 'actions',
      render: (_: any, record: any) => (
        <Space>
          <Button 
            type="link" 
            icon={<EditOutlined />} 
            onClick={() => handleEdit(record)}
          >
            ç¼–è¾‘
          </Button>
          <Popconfirm
            title="ç¡®å®šåˆ é™¤è¿™ä¸ªç”¨æˆ·å—ï¼Ÿ"
            onConfirm={() => handleDelete(record.id)}
            okText="ç¡®å®š"
            cancelText="å–æ¶ˆ"
          >
            <Button 
              type="link" 
              danger 
              icon={<DeleteOutlined />}
            >
              åˆ é™¤
            </Button>
          </Popconfirm>
        </Space>
      )
    }
  ]

  return (
    <div>
      {/* ç»Ÿè®¡å¡ç‰‡ */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#1890ff' }}>
                {users.length}
              </div>
              <div style={{ color: '#666' }}>æ€»ç”¨æˆ·æ•°</div>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#52c41a' }}>
                {users.filter(u => u.status === 'active').length}
              </div>
              <div style={{ color: '#666' }}>æ´»è·ƒç”¨æˆ·</div>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#f5222d' }}>
                {users.filter(u => u.role === 'admin').length}
              </div>
              <div style={{ color: '#666' }}>ç®¡ç†å‘˜</div>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#fa8c16' }}>
                {users.filter(u => u.role === 'editor').length}
              </div>
              <div style={{ color: '#666' }}>ç¼–è¾‘è€…</div>
            </div>
          </Card>
        </Col>
      </Row>

      {/* æ“ä½œæ  */}
      <Card style={{ marginBottom: 16 }}>
        <Row justify="space-between" align="middle">
          <Col>
            <Space>
              <Search
                placeholder="æœç´¢ç”¨æˆ·å§“åæˆ–é‚®ç®±"
                allowClear
                style={{ width: 300 }}
                onSearch={setSearchText}
                onChange={(e) => setSearchText(e.target.value)}
              />
              <Button icon={<ReloadOutlined />}>åˆ·æ–°</Button>
            </Space>
          </Col>
          <Col>
            <Button type="primary" icon={<PlusOutlined />} onClick={handleAdd}>
              æ–°å¢ç”¨æˆ·
            </Button>
          </Col>
        </Row>
      </Card>

      {/* ç”¨æˆ·è¡¨æ ¼ */}
      <Card>
        <Table
          columns={columns}
          dataSource={filteredUsers}
          rowKey="id"
          loading={loading}
          pagination={{
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total, range) => `ç¬¬ ${range[0]}-${range[1]} æ¡/å…± ${total} æ¡`
          }}
        />
      </Card>

      {/* æ·»åŠ /ç¼–è¾‘æ¨¡æ€æ¡† */}
      <Modal
        title={editingUser ? 'ç¼–è¾‘ç”¨æˆ·' : 'æ–°å¢ç”¨æˆ·'}
        open={modalVisible}
        onCancel={() => {
          setModalVisible(false)
          setEditingUser(null)
          form.resetFields()
        }}
        footer={null}
        width={600}
      >
        <Form
          form={form}
          layout="vertical"
          onFinish={handleSubmit}
        >
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="name"
                label="å§“å"
                rules={[{ required: true, message: 'è¯·è¾“å…¥å§“å' }]}
              >
                <Input placeholder="è¯·è¾“å…¥å§“å" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="email"
                label="é‚®ç®±"
                rules={[
                  { required: true, message: 'è¯·è¾“å…¥é‚®ç®±' },
                  { type: 'email', message: 'è¯·è¾“å…¥æœ‰æ•ˆçš„é‚®ç®±åœ°å€' }
                ]}
              >
                <Input placeholder="è¯·è¾“å…¥é‚®ç®±" />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="role"
                label="è§’è‰²"
                rules={[{ required: true, message: 'è¯·é€‰æ‹©è§’è‰²' }]}
              >
                <Select placeholder="è¯·é€‰æ‹©è§’è‰²">
                  <Option value="admin">ç®¡ç†å‘˜</Option>
                  <Option value="editor">ç¼–è¾‘è€…</Option>
                  <Option value="user">æ™®é€šç”¨æˆ·</Option>
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="department"
                label="éƒ¨é—¨"
                rules={[{ required: true, message: 'è¯·è¾“å…¥éƒ¨é—¨' }]}
              >
                <Input placeholder="è¯·è¾“å…¥éƒ¨é—¨" />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            name="status"
            label="çŠ¶æ€"
            rules={[{ required: true, message: 'è¯·é€‰æ‹©çŠ¶æ€' }]}
          >
            <Select placeholder="è¯·é€‰æ‹©çŠ¶æ€">
              <Option value="active">æ´»è·ƒ</Option>
              <Option value="inactive">éæ´»è·ƒ</Option>
            </Select>
          </Form.Item>

          <Form.Item style={{ marginBottom: 0, textAlign: 'right' }}>
            <Space>
              <Button onClick={() => setModalVisible(false)}>
                å–æ¶ˆ
              </Button>
              <Button type="primary" htmlType="submit" loading={loading}>
                {editingUser ? 'æ›´æ–°' : 'åˆ›å»º'}
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  )
}

