}

@layer base {
  * {
    border-color: hsl(var(--border));
    box-sizing: border-box;
  }
  body {
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
    transition: background-color 0.3s ease;
    margin: 0;
    padding: 0;
  }
}

