'use client'
import { useState } from 'react'
import { useRouter, usePathname } from 'next/navigation'
import { Layout, Button, Typography, Menu, Tooltip } from 'antd'
import { 
  SunOutlined, 
  MoonOutlined, 
  MenuFoldOutlined, 
  MenuUnfoldOutlined 
} from '@ant-design/icons'
import { useTheme } from '@/contexts/ThemeContext'
import { menuItems } from '@/config/navigation'

const { Header, Content, Sider } = Layout
const { Title } = Typography

export default function AppLayout({ children }: { children: React.ReactNode }) {
  const { theme, toggleTheme } = useTheme()
  const [collapsed, setCollapsed] = useState(false)
  const router = useRouter()
  const pathname = usePathname()

  // Ê†πÊçÆÂΩìÂâçË∑ØÂæÑËé∑ÂèñÈÄâ‰∏≠ÁöÑËèúÂçïÈ°π
  const selectedKeys = menuItems
    .filter(item => item.path === pathname)
    .map(item => item.key)

  const handleMenuClick = ({ key }: { key: string }) => {
    const menuItem = menuItems.find(item => item.key === key)
    if (menuItem) {
      router.push(menuItem.path)
    }
  }

  const menuItemsWithIcons = menuItems.map(item => ({
    key: item.key,
    icon: <item.icon />,
    label: item.label,
    title: item.description
  }))

  return (
    <Layout style={{ minHeight: '100vh' }}>
      {/* Â∑¶‰æßÂØºËà™Ê†è */}
      <Sider
        trigger={null}
        collapsible
        collapsed={collapsed}
        width={240}
        style={{
          overflow: 'auto',
          height: '100vh',
          position: 'fixed',
          left: 0,
          top: 0,
          bottom: 0,
          zIndex: 100
        }}
      >
        {/* LogoÂå∫Âüü */}
        <div style={{ 
          height: 64, 
          margin: 16, 
          display: 'flex', 
          alignItems: 'center',
          justifyContent: collapsed ? 'center' : 'flex-start'
        }}>
          <Title 
            level={collapsed ? 5 : 4} 
            style={{ 
              margin: 0, 
              color: theme === 'dark' ? '#fff' : '#000',
              whiteSpace: 'nowrap',
              overflow: 'hidden'
            }}
          >
            {collapsed ? 'PS' : 'ProtoSOP'}
          </Title>
        </div>

        {/* ÂØºËà™ËèúÂçï */}
        <Menu
          mode="inline"
          selectedKeys={selectedKeys}
          items={menuItemsWithIcons}
          onClick={handleMenuClick}
          style={{ borderRight: 0 }}
          theme={theme === 'dark' ? 'dark' : 'light'}
        />
      </Sider>

      {/* ‰∏ªÂÜÖÂÆπÂå∫Âüü */}
      <Layout style={{ marginLeft: collapsed ? 80 : 240, transition: 'margin-left 0.2s' }}>
        {/* È°∂ÈÉ®ÂØºËà™Ê†è */}
        <Header style={{ 
          background: 'transparent',
          padding: '0 24px',
          borderBottom: '1px solid',
          borderBottomColor: theme === 'dark' ? '#434343' : '#d9d9d9',
          position: 'sticky',
          top: 0,
          zIndex: 10
        }}>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            height: '100%'
          }}>
            {/* Â∑¶‰æßÊäòÂè†ÊåâÈíÆÂíåÈ°µÈù¢Ê†áÈ¢ò */}
            <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
              <Button
                type="text"
                icon={collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
                onClick={() => setCollapsed(!collapsed)}
                style={{
                  fontSize: '16px',
                  width: 40,
                  height: 40,
                }}
              />

              <Title level={4} style={{ margin: 0, color: 'inherit' }}>
                {menuItems.find(item => item.path === pathname)?.label || 'ProtoSOP Âπ≥Âè∞'}
              </Title>
            </div>

            {/* Âè≥‰∏äËßí‰∏ªÈ¢òÂàáÊç¢ÊåâÈíÆ */}
            <Tooltip title={`ÂàáÊç¢Âà∞${theme === 'dark' ? '‰∫ÆËâ≤' : 'ÊöóËâ≤'}Ê®°Âºè`}>
              <Button
                type="text"
                icon={theme === 'dark' ? <SunOutlined /> : <MoonOutlined />}
                onClick={toggleTheme}
                style={{ 
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  width: 40,
                  height: 40,
                }}
              />
            </Tooltip>
          </div>
        </Header>

        {/* È°µÈù¢ÂÜÖÂÆπ */}
        <Content style={{ 
          margin: 24,
          padding: 0,
          minHeight: 'calc(100vh - 112px)',
          overflow: 'initial'
        }}>
          {children}
        </Content>
      </Layout>
    </Layout>
  )
}

