@import "tailwindcss";

/* ÂÖ®Â±ÄÊ†∑Âºè - ‰æùËµñ Ant Design ÂÆòÊñπ‰∏ªÈ¢òÔºå‰∏ç‰ΩøÁî®Á°¨ÁºñÁ†ÅÈ¢úËâ≤ */
body {
  transition: background-color 0.3s ease;
  margin: 0;
  padding: 0;
}

/* Á°Æ‰øù Ant Design ÁªÑ‰ª∂Ê≠£Á°ÆÂ∫îÁî®‰∏ªÈ¢ò */
html {
  font-size: 14px;
}

/* ÊªöÂä®Êù°Ê†∑Âºè‰ºòÂåñ */
::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background-color: rgba(0, 0, 0, 0.2);
  border-radius: 3px;
}

[data-theme="dark"] ::-webkit-scrollbar-thumb {
  background-color: rgba(255, 255, 255, 0.2);
}

/* Á°Æ‰øùÊâÄÊúâÊñáÊú¨ÈÉΩ‰∏ç‰ΩøÁî®Á°¨ÁºñÁ†ÅÈ¢úËâ≤ */
