'use client'
import { useState, useEffect } from 'react'
import { 
  Card, 
  Input, 
  Select, 
  Space, 
  Row, 
  Col, 
  Button, 
  Tag, 
  Typography, 
  Divider,
  Badge,
  message,
  Modal,
  Timeline,
  Drawer
} from 'antd'
import { 
  SearchOutlined, 
  FilterOutlined, 
  SortAscendingOutlined,
  EyeOutlined,
  BranchesOutlined,
  DiffOutlined,
  PlayCircleOutlined,
  ClockCircleOutlined
} from '@ant-design/icons'
import { AtomContext, FilterOptions } from '@/types'
import { MockAPI } from '@/mock/api'
import { categories, statusOptions } from '@/mock/data'
import { EditableTags } from '@/components/common/EditableTags'
import { PageInfo } from '@/components/common/PageInfo'

const { Search } = Input
const { Option } = Select
const { Text, Title, Paragraph } = Typography

export default function HomePage() {
  const [atoms, setAtoms] = useState<AtomContext[]>([])
  const [filteredAtoms, setFilteredAtoms] = useState<AtomContext[]>([])
  const [loading, setLoading] = useState(true)
  const [selectedAtom, setSelectedAtom] = useState<AtomContext | null>(null)
  const [versionDrawerVisible, setVersionDrawerVisible] = useState(false)
  const [detailDrawerVisible, setDetailDrawerVisible] = useState(false)

  const [filter, setFilter] = useState<FilterOptions>({
    search: '',
    category: 'all',
    tags: [],
    status: 'all',
    sortBy: 'updatedAt',
    sortOrder: 'desc'
  })

  // Âä†ËΩΩÊï∞ÊçÆ
  useEffect(() => {
    loadAtoms()
  }, [])

  // Â∫îÁî®Á≠õÈÄâ
  useEffect(() => {
    applyFilter()
  }, [atoms, filter]) // eslint-disable-line react-hooks/exhaustive-deps

  const loadAtoms = async () => {
    try {
      setLoading(true)
      const response = await MockAPI.getAtoms()
      if (response.success) {
        setAtoms(response.data)
      }
    } catch {
      message.error('Âä†ËΩΩÊï∞ÊçÆÂ§±Ë¥•')
    } finally {
      setLoading(false)
    }
  }

  const applyFilter = async () => {
    try {
      const response = await MockAPI.searchAtoms(filter)
      if (response.success) {
        setFilteredAtoms(response.data)
      }
    } catch {
      message.error('Á≠õÈÄâÂ§±Ë¥•')
    }
  }

  const handleSearch = (value: string) => {
    setFilter(prev => ({ ...prev, search: value }))
  }

  const handleCategoryChange = (value: string) => {
    setFilter(prev => ({ ...prev, category: value }))
  }

  const handleStatusChange = (value: string) => {
    setFilter(prev => ({ ...prev, status: value }))
  }

  const handleSortChange = (value: string) => {
    const [sortBy, sortOrder] = value.split('-') as [FilterOptions['sortBy'], 'asc' | 'desc']
    setFilter(prev => ({ ...prev, sortBy, sortOrder }))
  }

  const handleTagsChange = async (atomId: string, newTags: string[]) => {
    try {
      const response = await MockAPI.updateAtomTags(atomId, newTags)
      if (response.success) {
        // Êõ¥Êñ∞Êú¨Âú∞Êï∞ÊçÆ
        setAtoms(prev => prev.map(atom => 
          atom.id === atomId ? response.data : atom
        ))
        message.success('Ê†áÁ≠æÊõ¥Êñ∞ÊàêÂäü')
      }
    } catch {
      message.error('Ê†áÁ≠æÊõ¥Êñ∞Â§±Ë¥•')
    }
  }

  const handleUseAtom = async (atomId: string) => {
    try {
      // eslint-disable-next-line react-hooks/rules-of-hooks
      const response = await MockAPI.useAtom(atomId)
      if (response.success) {
        // Êõ¥Êñ∞Êú¨Âú∞Êï∞ÊçÆ
        setAtoms(prev => prev.map(atom => 
          atom.id === atomId ? response.data : atom
        ))
        message.success('Â∑≤Â∫îÁî®ÂéüÂ≠êÂà∞È°πÁõÆ‰∏≠')
      }
    } catch {
      message.error('Â∫îÁî®Â§±Ë¥•')
    }
  }

  const showVersionHistory = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setVersionDrawerVisible(true)
  }

  const showAtomDetail = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setDetailDrawerVisible(true)
  }

  const compareVersions = (atom: AtomContext) => {
    if (atom.versions.length < 2) {
      message.info('ÈúÄË¶ÅËá≥Â∞ë2‰∏™ÁâàÊú¨ÊâçËÉΩËøõË°åÂØπÊØî')
      return
    }

    const latestVersion = atom.versions[0]
    const previousVersion = atom.versions[1]

    Modal.info({
      title: 'ÁâàÊú¨ÂØπÊØî',
      width: 800,
      content: (
        <div>
          <Row gutter={16}>
            <Col span={12}>
              <Card title={`ÁâàÊú¨ ${previousVersion.version}`} size="small">
                <Text>{previousVersion.description}</Text>
                <br />
                <Text type="secondary">{previousVersion.timestamp}</Text>
              </Card>
            </Col>
            <Col span={12}>
              <Card title={`ÁâàÊú¨ ${latestVersion.version}`} size="small">
                <Text>{latestVersion.description}</Text>
                <br />
                <Text type="secondary">{latestVersion.timestamp}</Text>
              </Card>
            </Col>
          </Row>
          <Divider />
          <div>
            <Text strong>‰∏ªË¶ÅÂèòÊõ¥Ôºö</Text>
            <ul>
              {latestVersion.changes.map((change, index) => (
                <li key={index}>{change}</li>
              ))}
            </ul>
          </div>
        </div>
      )
    })
  }

  const getCategoryLabel = (category: string) => {
    const categoryItem = categories.find(cat => cat.value === category)
    return categoryItem ? categoryItem.label : category
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'green'
      case 'draft': return 'orange'
      case 'archived': return 'gray'
      default: return 'blue'
    }
  }

  return (
    <div>
      {/* Á≠õÈÄâÊéßÂà∂Ê†è */}
      <Card style={{ marginBottom: 24 }}>
        <Row gutter={[16, 16]} align="middle">
          <Col flex="auto">
            <Search
              placeholder="ÊêúÁ¥¢ÂéüÂ≠êÊ†áÈ¢ò„ÄÅÊèèËø∞ÊàñÂÜÖÂÆπ..."
              allowClear
              enterButton={<SearchOutlined />}
              size="large"
              onSearch={handleSearch}
            />
          </Col>
          <Col>
            <Space>
              <Select
                value={filter.category}
                onChange={handleCategoryChange}
                style={{ width: 120 }}
                suffixIcon={<FilterOutlined />}
              >
                <Option value="all">ÂÖ®ÈÉ®ÂàÜÁ±ª</Option>
                {categories.map(cat => (
                  <Option key={cat.value} value={cat.value}>
                    {cat.label}
                  </Option>
                ))}
              </Select>

              <Select
                value={filter.status}
                onChange={handleStatusChange}
                style={{ width: 100 }}
              >
                <Option value="all">ÂÖ®ÈÉ®Áä∂ÊÄÅ</Option>
                {statusOptions.map(status => (
                  <Option key={status.value} value={status.value}>
                    {status.label}
                  </Option>
                ))}
              </Select>

              <Select
                value={`${filter.sortBy}-${filter.sortOrder}`}
                onChange={handleSortChange}
                style={{ width: 140 }}
                suffixIcon={<SortAscendingOutlined />}
              >
                <Option value="updatedAt-desc">ÊúÄÊñ∞Êõ¥Êñ∞</Option>
                <Option value="createdAt-desc">ÊúÄÊñ∞ÂàõÂª∫</Option>
                <Option value="usageCount-desc">‰ΩøÁî®ÊúÄÂ§ö</Option>
                <Option value="title-asc">Ê†áÈ¢òA-Z</Option>
                <Option value="version-desc">ÁâàÊú¨Âè∑</Option>
              </Select>
            </Space>
          </Col>
        </Row>
      </Card>

      {/* ÂéüÂ≠êÂàóË°® */}
      <Row gutter={[24, 24]}>
        {filteredAtoms.map(atom => (
          <Col xs={24} sm={12} lg={8} xl={6} key={atom.id}>
            <Card
              hoverable
              style={{ height: '100%' }}
              bodyStyle={{ padding: '16px' }}
              actions={[
                <Button
                  key="view"
                  type="text"
                  icon={<EyeOutlined />}
                  onClick={() => showAtomDetail(atom)}
                >
                  Êü•Áúã
                </Button>,
                <Button
                  key="version"
                  type="text"
                  icon={<BranchesOutlined />}
                  onClick={() => showVersionHistory(atom)}
                >
                  ÁâàÊú¨
                </Button>,
                <Button
                  key="compare"
                  type="text"
                  icon={<DiffOutlined />}
                  onClick={() => compareVersions(atom)}
                  disabled={atom.versions.length < 2}
                >
                  ÂØπÊØî
                </Button>,
                <Button
                  key="use"
                  type="text"
                  icon={<PlayCircleOutlined />}
                  onClick={() => handleUseAtom(atom.id)}
                >
                  ‰ΩøÁî®
                </Button>
              ]}
            >
              <div style={{ marginBottom: 12 }}>
                <Space>
                  <Badge count={atom.usageCount} color="blue">
                    <Title level={5} style={{ margin: 0 }}>
                      {atom.title}
                    </Title>
                  </Badge>
                  <Tag color={getStatusColor(atom.status)}>
                    {statusOptions.find(s => s.value === atom.status)?.label}
                  </Tag>
                </Space>
              </div>

              <Paragraph 
                ellipsis={{ rows: 2 }}
                style={{ marginBottom: 12, minHeight: 44 }}
              >
                {atom.description}
              </Paragraph>

              <div style={{ marginBottom: 12 }}>
                <EditableTags
                  tags={atom.tags}
                  onChange={(newTags) => handleTagsChange(atom.id, newTags)}
                />
              </div>

              <Divider style={{ margin: '12px 0' }} />

              <Row justify="space-between" align="middle">
                <Col>
                  <Space direction="vertical" size={0}>
                    <Text type="secondary" style={{ fontSize: '12px' }}>
                      {getCategoryLabel(atom.category)} ‚Ä¢ v{atom.version}
                    </Text>
                    <Text type="secondary" style={{ fontSize: '12px' }}>
                      by {atom.author} ‚Ä¢ {atom.updatedAt}
                    </Text>
                  </Space>
                </Col>
              </Row>
            </Card>
          </Col>
        ))}
      </Row>

      {/* Á©∫Áä∂ÊÄÅ */}
      {filteredAtoms.length === 0 && !loading && (
        <Card style={{ textAlign: 'center', padding: '40px 0' }}>
          <Text type="secondary">Ê≤°ÊúâÊâæÂà∞ÂåπÈÖçÁöÑÂéüÂ≠ê</Text>
        </Card>
      )}

      {/* ÁâàÊú¨ÂéÜÂè≤ÊäΩÂ±â */}
      <Drawer
        title={`ÁâàÊú¨ÂéÜÂè≤ - ${selectedAtom?.title}`}
        placement="right"
        onClose={() => setVersionDrawerVisible(false)}
        open={versionDrawerVisible}
        width={480}
      >
        {selectedAtom && (
          <Timeline>
            {selectedAtom.versions.map((version, index) => (
              <Timeline.Item
                key={version.version}
                color={index === 0 ? 'green' : 'gray'}
                dot={index === 0 ? <ClockCircleOutlined /> : null}
              >
                <div>
                  <Space>
                    <Text strong>ÁâàÊú¨ {version.version}</Text>
                    <Text type="secondary">{version.timestamp}</Text>
                  </Space>
                  <br />
                  <Text>{version.description}</Text>
                  <br />
                  <Text type="secondary">by {version.author}</Text>
                  {version.changes.length > 0 && (
                    <div style={{ marginTop: 8 }}>
                      <Text strong>ÂèòÊõ¥ÂÜÖÂÆπÔºö</Text>
                      <ul style={{ marginTop: 4 }}>
                        {version.changes.map((change, idx) => (
                          <li key={idx}><Text type="secondary">{change}</Text></li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              </Timeline.Item>
            ))}
          </Timeline>
        )}
      </Drawer>

      {/* ËØ¶ÊÉÖÊäΩÂ±â */}
      <Drawer
        title={selectedAtom?.title}
        placement="right"
        onClose={() => setDetailDrawerVisible(false)}
        open={detailDrawerVisible}
        width={600}
      >
        {selectedAtom && (
          <Space direction="vertical" size="large" style={{ width: '100%' }}>
            <div>
              <Space>
                <Tag color={getStatusColor(selectedAtom.status)}>
                  {statusOptions.find(s => s.value === selectedAtom.status)?.label}
                </Tag>
                <Text type="secondary">ÁâàÊú¨ {selectedAtom.version}</Text>
                <Text type="secondary">‰ΩøÁî® {selectedAtom.usageCount} Ê¨°</Text>
              </Space>
            </div>

            <div>
              <Text strong>ÊèèËø∞Ôºö</Text>
              <br />
              <Text>{selectedAtom.description}</Text>
            </div>

            <div>
              <Text strong>ÂÜÖÂÆπÔºö</Text>
              <div style={{ 
                background: '#f5f5f5', 
                padding: '12px', 
                borderRadius: '6px',
                marginTop: '8px',
                maxHeight: '300px',
                overflow: 'auto'
              }}>
                <pre style={{ margin: 0, whiteSpace: 'pre-wrap' }}>
                  {selectedAtom.content}
                </pre>
              </div>
            </div>

            <div>
              <Text strong>ÂäüËÉΩÊ†áÁ≠æÔºö</Text>
              <br />
              <EditableTags
                tags={selectedAtom.tags}
                onChange={(newTags) => handleTagsChange(selectedAtom.id, newTags)}
              />
            </div>

            <div>
              <Text strong>ÂàõÂª∫‰ø°ÊÅØÔºö</Text>
              <br />
              <Text type="secondary">
                Áî± {selectedAtom.author} ÂàõÂª∫‰∫é {selectedAtom.createdAt}
              </Text>
              <br />
              <Text type="secondary">
                ÊúÄÂêéÊõ¥Êñ∞‰∫é {selectedAtom.updatedAt}
              </Text>
            </div>
          </Space>
        )}
      </Drawer>

      {/* È°µÈù¢ÂéüÂûãËØ¥Êòé */}
