export default function AIWorkspacePage() {
  const [tasks, setTasks] = useState<AITask[]>([])
  const [agents, setAgents] = useState<AIAgent[]>([])
  const [selectedTask, setSelectedTask] = useState<AITask | null>(null)
  const [newTaskVisible, setNewTaskVisible] = useState(false)
  const [taskDetailVisible, setTaskDetailVisible] = useState(false)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    loadWorkspaceData()

    // Ê®°ÊãüÂÆûÊó∂Êõ¥Êñ∞
    const interval = setInterval(() => {
      updateTaskProgress()
    }, 2000)

    return () => clearInterval(interval)
  }, [])

  const loadWorkspaceData = async () => {
    setLoading(true)
    setTimeout(() => {
      setTasks(mockAITasks)
      setAgents(mockAIAgents)
      setLoading(false)
    }, 1000)
  }

  const updateTaskProgress = () => {
    setTasks(prevTasks => 
      prevTasks.map(task => {
        if (task.status === 'running' && task.progress < 100) {
          const newProgress = Math.min(task.progress + Math.random() * 10, 100)
          const actions = [
            'Ê≠£Âú®ÂàÜÊûê‰ª£Á†ÅÁªìÊûÑ...',
            'ÁîüÊàêÁªÑ‰ª∂ÈÄªËæë...',
            'Ê∑ªÂä†Á±ªÂûãÂÆö‰πâ...',
            '‰ºòÂåñÊÄßËÉΩ...',
            'ÁîüÊàêÊµãËØïÁî®‰æã...',
            'ÂÆåÂñÑÊñáÊ°£Ê≥®Èáä...'
          ]

          return {
            ...task,
            progress: newProgress,
            currentAction: newProgress < 100 ? 
              actions[Math.floor(Math.random() * actions.length)] : 
              'Âç≥Â∞ÜÂÆåÊàê...'
          }
        }
        return task
      })
    )
  }

  const handleTaskAction = (taskId: string, action: 'pause' | 'resume' | 'stop') => {
    setTasks(prevTasks =>
      prevTasks.map(task => {
        if (task.id === taskId) {
          switch (action) {
            case 'pause':
              return { ...task, status: 'paused' as const }
            case 'resume':
              return { ...task, status: 'running' as const }
            case 'stop':
              return { ...task, status: 'failed' as const }
            default:
              return task
          }
        }
        return task
      })
    )
    message.success(`‰ªªÂä°${action === 'pause' ? 'ÊöÇÂÅú' : action === 'resume' ? 'ÊÅ¢Â§ç' : 'ÂÅúÊ≠¢'}ÊàêÂäü`)
  }

  const showTaskDetail = (task: AITask) => {
    setSelectedTask(task)
    setTaskDetailVisible(true)
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'running': return 'processing'
      case 'completed': return 'success'
      case 'failed': return 'error'
      case 'paused': return 'warning'
      default: return 'default'
    }
  }

  const getStatusText = (status: string) => {
    switch (status) {
      case 'running': return 'ËøêË°å‰∏≠'
      case 'completed': return 'Â∑≤ÂÆåÊàê'
      case 'failed': return 'Â§±Ë¥•'
      case 'paused': return 'ÊöÇÂÅú'
      case 'queued': return 'ÈòüÂàó‰∏≠'
      default: return 'Êú™Áü•'
    }
  }

  const getLogIcon = (level: string) => {
    switch (level) {
      case 'success': return <CheckCircleOutlined style={{ color: '#52c41a' }} />
      case 'warning': return <WarningOutlined style={{ color: '#faad14' }} />
      case 'error': return <StopOutlined style={{ color: '#ff4d4f' }} />
      default: return <MessageOutlined style={{ color: '#1890ff' }} />
    }
  }

  return (
    <div>
      {/* AIÂ∑•‰ΩúÂè∞Ê¶ÇËßà */}
      <Row gutter={[24, 24]}>
        <Col span={16}>
          <Card 
            title="AI‰ªªÂä°ÁõëÊéß" 
            extra={
              <Space>
                <Button 
                  type="primary" 
                  icon={<PlayCircleOutlined />}
                  onClick={() => setNewTaskVisible(true)}
                >
                  Êñ∞Âª∫‰ªªÂä°
                </Button>
              </Space>
            }
            loading={loading}
          >
            <div style={{ marginBottom: 16 }}>
              <Space>
                <Text strong>Ê¥ªË∑É‰ªªÂä°: </Text>
                <Badge count={tasks.filter(t => t.status === 'running').length} />
                <Text strong>Â∑≤ÂÆåÊàê: </Text>
                <Badge count={tasks.filter(t => t.status === 'completed').length} />
                <Text strong>ÈòüÂàó‰∏≠: </Text>
                <Badge count={tasks.filter(t => t.status === 'queued').length} />
              </Space>
            </div>

            <List
              dataSource={tasks}
              renderItem={(task) => (
                <List.Item
                  actions={[
                    <Button 
                      key="detail"
                      type="link" 
                      icon={<EyeOutlined />}
                      onClick={() => showTaskDetail(task)}
                    >
                      ËØ¶ÊÉÖ
                    </Button>,
                    ...(task.status === 'running' ? [
                      <Button 
                        key="pause"
                        type="link" 
                        icon={<PauseCircleOutlined />}
                        onClick={() => handleTaskAction(task.id, 'pause')}
                      >
                        ÊöÇÂÅú
                      </Button>
                    ] : []),
                    ...(task.status === 'paused' ? [
                      <Button 
                        key="resume"
                        type="link" 
                        icon={<PlayCircleOutlined />}
                        onClick={() => handleTaskAction(task.id, 'resume')}
                      >
                        ÊÅ¢Â§ç
                      </Button>
                    ] : []),
                    <Button 
                      key="stop"
                      type="link" 
                      danger
                      icon={<StopOutlined />}
                      onClick={() => handleTaskAction(task.id, 'stop')}
                    >
                      ÂÅúÊ≠¢
                    </Button>
                  ]}
                >
                  <List.Item.Meta
                    avatar={
                      <Avatar icon={
                        task.metadata.type === 'code_generation' ? <CodeOutlined /> :
                        task.metadata.type === 'analysis' ? <BulbOutlined /> :
                        task.metadata.type === 'testing' ? <ThunderboltOutlined /> :
                        <FileTextOutlined />
                      } />
                    }
                    title={
                      <Space>
                        {task.title}
                        <Badge status={getStatusColor(task.status)} text={getStatusText(task.status)} />
                        <Tag color={task.metadata.priority === 'high' ? 'red' : 
                                   task.metadata.priority === 'medium' ? 'orange' : 'blue'}>
                          {task.metadata.priority}
                        </Tag>
                      </Space>
                    }
                    description={
                      <div>
                        <div style={{ marginBottom: 8 }}>
                          {task.status === 'running' && (
                            <Text type="secondary">
                              <LoadingOutlined /> {task.currentAction}
                            </Text>
                          )}
                          {task.status === 'completed' && task.duration && (
                            <Text type="secondary">
                              <ClockCircleOutlined /> ËÄóÊó∂: {task.duration}
                            </Text>
                          )}
                        </div>
                        {task.status === 'running' && (
                          <Progress 
                            percent={Math.round(task.progress)} 
                            size="small"
                            status="active"
                          />
                        )}
                      </div>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>

        <Col span={8}>
          <Card title="AIÊô∫ËÉΩ‰ΩìÁä∂ÊÄÅ" loading={loading}>
            <List
              dataSource={agents}
              renderItem={(agent) => (
                <List.Item>
                  <List.Item.Meta
                    avatar={
                      <Badge 
                        status={agent.status === 'busy' ? 'processing' : 
                               agent.status === 'idle' ? 'success' : 'default'}
                        dot
                      >
                        <Avatar icon={<RobotOutlined />} />
                      </Badge>
                    }
                    title={agent.name}
                    description={
                      <div>
                        <div style={{ marginBottom: 4 }}>
                          <Text type="secondary">
                            {agent.status === 'busy' ? 'ÂøôÁ¢å‰∏≠' : 'Á©∫Èó≤'}
                            {agent.currentTask && ` - ÊâßË°å‰ªªÂä°: ${agent.currentTask}`}
                          </Text>
                        </div>
                        <Space wrap>
                          {agent.specialization.map(skill => (
                            <Tag key={skill}>{skill}</Tag>
                          ))}
                        </Space>
                        <div style={{ marginTop: 8, fontSize: '12px' }}>
                          <Text type="secondary">
                            Â∑≤ÂÆåÊàê: {agent.performance.tasksCompleted} | 
                            Âπ≥ÂùáÊó∂Èïø: {agent.performance.averageTime} | 
                            ÊàêÂäüÁéá: {agent.performance.successRate}%
                          </Text>
                        </div>
                      </div>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
      </Row>

      {/* ÂÆûÊó∂Êó•ÂøóÊµÅ */}
      <Row gutter={[24, 24]} style={{ marginTop: 24 }}>
        <Col span={24}>
          <Card title="ÂÆûÊó∂ÊâßË°åÊó•Âøó" size="small">
            <div style={{ maxHeight: 300, overflow: 'auto' }}>
              {tasks.filter(t => t.status === 'running').map(task => (
                <div key={task.id} style={{ marginBottom: 16 }}>
                  <Title level={5}>{task.title}</Title>
                  <Timeline>
                    {task.logs.map(log => (
                      <Timeline.Item
                        key={log.id}
                        dot={getLogIcon(log.level)}
                        color={log.level === 'error' ? 'red' : log.level === 'warning' ? 'orange' : 'blue'}
                      >
                        <div>
                          <Text type="secondary">{log.timestamp}</Text>
                          <br />
                          <Text>{log.message}</Text>
                          {log.details && (
                            <div style={{ marginTop: 4 }}>
                              <Text type="secondary" style={{ fontSize: '12px' }}>
                                {log.details}
                              </Text>
                            </div>
                          )}
                        </div>
                      </Timeline.Item>
                    ))}
                  </Timeline>
                </div>
              ))}
            </div>
          </Card>
        </Col>
      </Row>

      {/* ‰ªªÂä°ËØ¶ÊÉÖÂºπÁ™ó */}
      <Modal
        title={selectedTask?.title}
        open={taskDetailVisible}
        onCancel={() => setTaskDetailVisible(false)}
        footer={null}
        width={900}
      >
        {selectedTask && (
          <div>
            <Alert
              message={`ÂΩìÂâçÁä∂ÊÄÅ: ${getStatusText(selectedTask.status)}`}
              description={selectedTask.currentAction}
              type={selectedTask.status === 'running' ? 'info' : 
                    selectedTask.status === 'completed' ? 'success' : 'warning'}
              style={{ marginBottom: 16 }}
            />

            <Descriptions column={2} bordered>
              <Descriptions.Item label="‰ªªÂä°Á±ªÂûã">
                <Tag color="blue">{selectedTask.metadata.type}</Tag>
              </Descriptions.Item>
