export default function AIWorkspacePage() {
  const [tasks, setTasks] = useState<AITask[]>([])
  const [agents, setAgents] = useState<AIAgent[]>([])
  const [selectedTask, setSelectedTask] = useState<AITask | null>(null)
  const [newTaskVisible, setNewTaskVisible] = useState(false)
  const [taskDetailVisible, setTaskDetailVisible] = useState(false)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    loadWorkspaceData()

    // Ê®°ÊãüÂÆûÊó∂Êõ¥Êñ∞
    const interval = setInterval(() => {
      updateTaskProgress()
    }, 2000)

    return () => clearInterval(interval)
  }, [])

  const loadWorkspaceData = async () => {
    setLoading(true)
    setTimeout(() => {
      setTasks(mockAITasks)
      setAgents(mockAIAgents)
      setLoading(false)
    }, 1000)
  }

  const updateTaskProgress = () => {
    setTasks(prevTasks => 
      prevTasks.map(task => {
        if (task.status === 'running' && task.progress < 100) {
          const newProgress = Math.min(task.progress + Math.random() * 10, 100)
          const actions = [
            'Ê≠£Âú®ÂàÜÊûê‰ª£Á†ÅÁªìÊûÑ...',
            'ÁîüÊàêÁªÑ‰ª∂ÈÄªËæë...',
            'Ê∑ªÂä†Á±ªÂûãÂÆö‰πâ...',
            '‰ºòÂåñÊÄßËÉΩ...',
            'ÁîüÊàêÊµãËØïÁî®‰æã...',
            'ÂÆåÂñÑÊñáÊ°£Ê≥®Èáä...'
          ]

          return {
            ...task,
            progress: newProgress,
            currentAction: newProgress < 100 ? 
              actions[Math.floor(Math.random() * actions.length)] : 
              'Âç≥Â∞ÜÂÆåÊàê...'
          }
        }
        return task
      })
    )
  }

  const handleTaskAction = (taskId: string, action: 'pause' | 'resume' | 'stop') => {
    setTasks(prevTasks =>
      prevTasks.map(task => {
        if (task.id === taskId) {
          switch (action) {
            case 'pause':
              return { ...task, status: 'paused' as const }
            case 'resume':
              return { ...task, status: 'running' as const }
            case 'stop':
              return { ...task, status: 'failed' as const }
            default:
              return task
          }
        }
        return task
      })
    )
    message.success(`‰ªªÂä°${action === 'pause' ? 'ÊöÇÂÅú' : action === 'resume' ? 'ÊÅ¢Â§ç' : 'ÂÅúÊ≠¢'}ÊàêÂäü`)
  }

  const showTaskDetail = (task: AITask) => {
    setSelectedTask(task)
    setTaskDetailVisible(true)
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'running': return 'processing'
      case 'completed': return 'success'
      case 'failed': return 'error'
      case 'paused': return 'warning'
      default: return 'default'
    }
  }

  const getStatusText = (status: string) => {
    switch (status) {
      case 'running': return 'ËøêË°å‰∏≠'
      case 'completed': return 'Â∑≤ÂÆåÊàê'
      case 'failed': return 'Â§±Ë¥•'
      case 'paused': return 'ÊöÇÂÅú'
      case 'queued': return 'ÈòüÂàó‰∏≠'
      default: return 'Êú™Áü•'
    }
  }

  const getLogIcon = (level: string) => {
    switch (level) {
      case 'success': return <CheckCircleOutlined style={{ color: 'var(--ant-color-success)' }} />
      case 'warning': return <WarningOutlined style={{ color: 'var(--ant-color-warning)' }} />
      case 'error': return <StopOutlined style={{ color: 'var(--ant-color-error)' }} />
      default: return <MessageOutlined style={{ color: 'var(--ant-color-primary)' }} />
    }
  }

  return (
    <div>
      {/* AIÂ∑•‰ΩúÂè∞Ê¶ÇËßà */}
      <Row gutter={[24, 24]}>
        <Col span={16}>
          <Card 
            title="AI‰ªªÂä°ÁõëÊéß" 
            extra={
              <Space>
                <Button 
                  type="primary" 
                  icon={<PlayCircleOutlined />}
                  onClick={() => setNewTaskVisible(true)}
                >
                  Êñ∞Âª∫‰ªªÂä°
                </Button>
              </Space>
            }
            loading={loading}
          >
            <div style={{ marginBottom: 16 }}>
              <Space>
                <Text strong>Ê¥ªË∑É‰ªªÂä°: </Text>
                <Badge count={tasks.filter(t => t.status === 'running').length} />
                <Text strong>Â∑≤ÂÆåÊàê: </Text>
                <Badge count={tasks.filter(t => t.status === 'completed').length} />
                <Text strong>ÈòüÂàó‰∏≠: </Text>
                <Badge count={tasks.filter(t => t.status === 'queued').length} />
              </Space>
            </div>

            <List
              dataSource={tasks}
              renderItem={(task) => (
                <List.Item
                  actions={[
                    <Button 
                      key="detail"
                      type="link" 
                      icon={<EyeOutlined />}
                      onClick={() => showTaskDetail(task)}
                    >
                      ËØ¶ÊÉÖ
                    </Button>,
                    ...(task.status === 'running' ? [
                      <Button 
                        key="pause"
                        type="link" 
                        icon={<PauseCircleOutlined />}
                        onClick={() => handleTaskAction(task.id, 'pause')}
                      >
                        ÊöÇÂÅú
                      </Button>
                    ] : []),
                    ...(task.status === 'paused' ? [
                      <Button 
                        key="resume"
                        type="link" 
                        icon={<PlayCircleOutlined />}
                        onClick={() => handleTaskAction(task.id, 'resume')}
                      >
                        ÊÅ¢Â§ç
                      </Button>
                    ] : []),
                    <Button 
                      key="stop"
                      type="link" 
                      danger
                      icon={<StopOutlined />}
