'use client'
import { useState } from 'react'
import { 
  Table, 
  Button, 
  Input, 
  Space, 
  Tag, 
  Modal, 
  Form, 
  Select, 
  message,
  Popconfirm,
  Avatar,
  Typography,
  Card,
  Row,
  Col
} from 'antd'
import { 
  SearchOutlined, 
  PlusOutlined, 
  EditOutlined, 
  DeleteOutlined,
  UserOutlined,
  ReloadOutlined
} from '@ant-design/icons'

const { Search } = Input
const { Option } = Select
const { Text } = Typography

// MockÊï∞ÊçÆ
const initialUsers = [
  {
    id: '1',
    name: 'Âº†‰∏â',
    email: 'zhangsan@example.com',
    role: 'admin',
    status: 'active',
    avatar: '',
    department: 'ÊäÄÊúØÈÉ®',
    joinDate: '2023-01-15'
  },
  {
    id: '2',
    name: 'ÊùéÂõõ',
    email: 'lisi@example.com',
    role: 'user',
    status: 'active',
    avatar: '',
    department: '‰∫ßÂìÅÈÉ®',
    joinDate: '2023-03-20'
  },
  {
    id: '3',
    name: 'Áéã‰∫î',
    email: 'wangwu@example.com',
    role: 'editor',
    status: 'inactive',
    avatar: '',
    department: 'ËÆæËÆ°ÈÉ®',
    joinDate: '2023-05-10'
  }
]

export default function AntdUserManager() {
  const [users, setUsers] = useState(initialUsers)
  const [loading, setLoading] = useState(false)
  const [searchText, setSearchText] = useState('')
  const [modalVisible, setModalVisible] = useState(false)
  const [editingUser, setEditingUser] = useState(null)
  const [form] = Form.useForm()

  // Á≠õÈÄâÁî®Êà∑
  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchText.toLowerCase()) ||
    user.email.toLowerCase().includes(searchText.toLowerCase())
  )

  // Áä∂ÊÄÅÊ†áÁ≠æÈ¢úËâ≤
  const getStatusColor = (status: string) => {
    return status === 'active' ? 'success' : 'default'
  }

  // ËßíËâ≤Ê†áÁ≠æÈ¢úËâ≤
  const getRoleColor = (role: string) => {
    const colors = {
      admin: 'red',
      editor: 'blue',
      user: 'green'
    }
    return colors[role] || 'default'
  }

  // Ê∑ªÂä†/ÁºñËæëÁî®Êà∑
  const handleSubmit = async (values: any) => {
    setLoading(true)
    try {
      await new Promise(resolve => setTimeout(resolve, 500)) // Ê®°ÊãüAPIË∞ÉÁî®

      if (editingUser) {
        // ÁºñËæë
        setUsers(prev => prev.map(user => 
          user.id === editingUser.id ? { ...user, ...values } : user
        ))
        message.success('Áî®Êà∑Êõ¥Êñ∞ÊàêÂäü')
      } else {
        // Êñ∞Â¢û
        const newUser = {
          id: Date.now().toString(),
          ...values,
          avatar: '',
          joinDate: new Date().toISOString().split('T')[0]
        }
        setUsers(prev => [...prev, newUser])
        message.success('Áî®Êà∑ÂàõÂª∫ÊàêÂäü')
      }

      setModalVisible(false)
      setEditingUser(null)
      form.resetFields()
    } catch (error) {
      message.error('Êìç‰ΩúÂ§±Ë¥•')
    } finally {
      setLoading(false)
    }
  }

  // Âà†Èô§Áî®Êà∑
  const handleDelete = async (id: string) => {
    setLoading(true)
    try {
      await new Promise(resolve => setTimeout(resolve, 300))
      setUsers(prev => prev.filter(user => user.id !== id))
      message.success('Áî®Êà∑Âà†Èô§ÊàêÂäü')
    } catch (error) {
      message.error('Âà†Èô§Â§±Ë¥•')
    } finally {
      setLoading(false)
    }
  }

  // ÁºñËæëÁî®Êà∑
  const handleEdit = (user: any) => {
    setEditingUser(user)
    form.setFieldsValue(user)
    setModalVisible(true)
  }

  // Êñ∞Â¢ûÁî®Êà∑
  const handleAdd = () => {
    setEditingUser(null)
    form.resetFields()
    setModalVisible(true)
  }

  // Ë°®Ê†ºÂàóÂÆö‰πâ
  const columns = [
    {
      title: 'Áî®Êà∑',
      dataIndex: 'name',
      key: 'name',
