          <Avatar icon={<UserOutlined />} src={record.avatar} />
          <div>
            <div>{text}</div>
            <Text type="secondary" style={{ fontSize: '12px' }}>
              {record.email}
            </Text>
          </div>
        </Space>
      )
    },
    {
      title: 'ËßíËâ≤',
      dataIndex: 'role',
      key: 'role',
      render: (role: string) => (
        <Tag color={getRoleColor(role)}>
          {role === 'admin' ? 'ÁÆ°ÁêÜÂëò' : role === 'editor' ? 'ÁºñËæëËÄÖ' : 'ÊôÆÈÄöÁî®Êà∑'}
        </Tag>
      )
    },
    {
      title: 'ÈÉ®Èó®',
      dataIndex: 'department',
      key: 'department'
    },
    {
      title: 'Áä∂ÊÄÅ',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getStatusColor(status)}>
          {status === 'active' ? 'Ê¥ªË∑É' : 'ÈùûÊ¥ªË∑É'}
        </Tag>
      )
    },
    {
      title: 'Âä†ÂÖ•Êó∂Èó¥',
      dataIndex: 'joinDate',
      key: 'joinDate'
    },
    {
      title: 'Êìç‰Ωú',
      key: 'actions',
      render: (_: any, record: any) => (
        <Space>
          <Button 
            type="link" 
            icon={<EditOutlined />} 
            onClick={() => handleEdit(record)}
          >
            ÁºñËæë
          </Button>
          <Popconfirm
            title="Á°ÆÂÆöÂà†Èô§Ëøô‰∏™Áî®Êà∑ÂêóÔºü"
            onConfirm={() => handleDelete(record.id)}
            okText="Á°ÆÂÆö"
            cancelText="ÂèñÊ∂à"
          >
            <Button 
              type="link" 
              danger 
              icon={<DeleteOutlined />}
            >
              Âà†Èô§
            </Button>
          </Popconfirm>
        </Space>
      )
    }
  ]

  return (
    <div>
      {/* ÁªüËÆ°Âç°Áâá */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#1890ff' }}>
                {users.length}
              </div>
              <div style={{ color: '#666' }}>ÊÄªÁî®Êà∑Êï∞</div>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#52c41a' }}>
                {users.filter(u => u.status === 'active').length}
              </div>
              <div style={{ color: '#666' }}>Ê¥ªË∑ÉÁî®Êà∑</div>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#f5222d' }}>
                {users.filter(u => u.role === 'admin').length}
              </div>
              <div style={{ color: '#666' }}>ÁÆ°ÁêÜÂëò</div>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#fa8c16' }}>
                {users.filter(u => u.role === 'editor').length}
              </div>
              <div style={{ color: '#666' }}>ÁºñËæëËÄÖ</div>
            </div>
          </Card>
        </Col>
      </Row>

      {/* Êìç‰ΩúÊ†è */}
      <Card style={{ marginBottom: 16 }}>
