        </Tag>
      )
    },
    {
      title: 'ÈÉ®Èó®',
      dataIndex: 'department',
      key: 'department'
    },
    {
      title: 'Áä∂ÊÄÅ',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getStatusColor(status)}>
          {status === 'active' ? 'Ê¥ªË∑É' : 'ÈùûÊ¥ªË∑É'}
        </Tag>
      )
    },
    {
      title: 'Âä†ÂÖ•Êó∂Èó¥',
      dataIndex: 'joinDate',
      key: 'joinDate'
    },
    {
      title: 'Êìç‰Ωú',
      key: 'actions',
      render: (_: any, record: any) => (
        <Space>
          <Button 
            type="text" 
            icon={<IconEdit />} 
            onClick={() => handleEdit(record)}
          >
            ÁºñËæë
          </Button>
          <Popconfirm
            title="Á°ÆÂÆöÂà†Èô§Ëøô‰∏™Áî®Êà∑ÂêóÔºü"
            onOk={() => handleDelete(record.id)}
            okText="Á°ÆÂÆö"
            cancelText="ÂèñÊ∂à"
          >
            <Button 
              type="text" 
              status="danger"
              icon={<IconDelete />}
            >
              Âà†Èô§
            </Button>
          </Popconfirm>
        </Space>
      )
    }
  ]

  return (
    <div>
      {/* ÁªüËÆ°Âç°Áâá */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
