import type { Metadata } from 'next'
import { AntdRegistry } from '@ant-design/nextjs-registry'
import { ThemeProvider } from '@/contexts/ThemeContext'
import { ClientThemeWrapper } from '@/components/ClientThemeWrapper'
import AppLayout from '@/components/layout/AppLayout'
import './globals.css'

export const metadata: Metadata = {
  title: 'åŸå­ä¸Šä¸‹æ–‡åº“ç®¡ç†ç³»ç»Ÿ',
  description: 'åŸºäºåŸå­åŒ–ç†å¿µçš„ä¸Šä¸‹æ–‡ç®¡ç†ç³»ç»Ÿï¼Œæ”¯æŒç‰ˆæœ¬ç®¡ç†ã€åŠŸèƒ½æ ‡ç­¾ç¼–è¾‘å’Œå†…å®¹å¯¹æ¯”',
}

export default function RootLayout({
  children,
}: {
  children: React.ReactNode
}) {
  return (
    <html lang="zh-CN">
      <body>
        <AntdRegistry>
          <ThemeProvider>
            <ClientThemeWrapper>
              <AppLayout>{children}</AppLayout>
            </ClientThemeWrapper>
          </ThemeProvider>
        </AntdRegistry>
      </body>
    </html>
  )
}

'use client'
import { useState } from 'react'
import { useRouter, usePathname } from 'next/navigation'
import { Layout, Button, Typography, Menu, Tooltip } from 'antd'
import { 
  SunOutlined, 
  MoonOutlined, 
  MenuFoldOutlined, 
  MenuUnfoldOutlined 
} from '@ant-design/icons'
import { useTheme } from '@/contexts/ThemeContext'
import { menuItems } from '@/config/navigation'

const { Header, Content, Sider } = Layout
const { Title } = Typography

export default function AppLayout({ children }: { children: React.ReactNode }) {
  const { theme, toggleTheme } = useTheme()
  const [collapsed, setCollapsed] = useState(false)
  const router = useRouter()
  const pathname = usePathname()

  // æ ¹æ®å½“å‰è·¯å¾„è·å–é€‰ä¸­çš„èœå•é¡¹
  const selectedKeys = menuItems
    .filter(item => item.path === pathname)
    .map(item => item.key)

  const handleMenuClick = ({ key }: { key: string }) => {
    const menuItem = menuItems.find(item => item.key === key)
    if (menuItem) {
      router.push(menuItem.path)
    }
  }

  const menuItemsWithIcons = menuItems.map(item => ({
    key: item.key,
    icon: <item.icon />,
    label: item.label,
    title: item.description
  }))

  return (
    <Layout style={{ minHeight: '100vh' }}>
      {/* å·¦ä¾§å¯¼èˆªæ  */}
      <Sider
        trigger={null}
        collapsible
        collapsed={collapsed}
        width={240}
        style={{
          overflow: 'auto',
          height: '100vh',
          position: 'fixed',
          left: 0,
          top: 0,
          bottom: 0,
          zIndex: 100
        }}
      >
        {/* LogoåŒºåŸŸ */}
        <div style={{ 
          height: 64, 
          margin: 16, 
          display: 'flex', 
          alignItems: 'center',
          justifyContent: collapsed ? 'center' : 'flex-start'
        }}>
          <Title 
            level={collapsed ? 5 : 4} 
            style={{ 
              margin: 0, 
              color: theme === 'dark' ? '#fff' : '#000',
              whiteSpace: 'nowrap',
              overflow: 'hidden'
            }}
          >
            {collapsed ? 'AC' : 'åŸå­ä¸Šä¸‹æ–‡'}
          </Title>
        </div>

        {/* å¯¼èˆªèœå• */}
        <Menu
          mode="inline"
          selectedKeys={selectedKeys}
          items={menuItemsWithIcons}
          onClick={handleMenuClick}
          style={{ borderRight: 0 }}
        />
      </Sider>

      {/* ä¸»å†…å®¹åŒºåŸŸ */}
      <Layout style={{ marginLeft: collapsed ? 80 : 240, transition: 'margin-left 0.2s' }}>
        {/* é¡¶éƒ¨å¯¼èˆªæ  */}
        <Header style={{ 
          background: 'transparent',
          padding: '0 24px',
          borderBottom: '1px solid',
          borderBottomColor: theme === 'dark' ? '#434343' : '#d9d9d9',
          position: 'sticky',
          top: 0,
          zIndex: 10
        }}>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            height: '100%'
          }}>
            {/* å·¦ä¾§æŠ˜å æŒ‰é’®å’Œé¡µé¢æ ‡é¢˜ */}
            <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
              <Button
                type="text"
                icon={collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
                onClick={() => setCollapsed(!collapsed)}
                style={{
                  fontSize: '16px',
                  width: 40,
                  height: 40,
                }}
              />

              <Title level={4} style={{ margin: 0, color: 'inherit' }}>
                {menuItems.find(item => item.path === pathname)?.label || 'åŸå­ä¸Šä¸‹æ–‡åº“ç®¡ç†ç³»ç»Ÿ'}
              </Title>
            </div>

            {/* å³ä¸Šè§’ä¸»é¢˜åˆ‡æ¢æŒ‰é’® */}
            <Tooltip title={`åˆ‡æ¢åˆ°${theme === 'dark' ? 'äº®è‰²' : 'æš—è‰²'}æ¨¡å¼`}>
              <Button
                type="text"
                icon={theme === 'dark' ? <SunOutlined /> : <MoonOutlined />}
                onClick={toggleTheme}
                style={{ 
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  width: 40,
                  height: 40,
                }}
              />
            </Tooltip>
          </div>
        </Header>

        {/* é¡µé¢å†…å®¹ */}
        <Content style={{ 
          margin: 24,
          padding: 0,
          minHeight: 'calc(100vh - 112px)',
          overflow: 'initial'
        }}>
          {children}
        </Content>
      </Layout>
    </Layout>
  )
}

'use client'
import { useState, useEffect } from 'react'
import { 
  Card, 
  Input, 
  Select, 
  Space, 
  Row, 
  Col, 
  Button, 
  Tag, 
  Typography, 
  Divider,
  Badge,
  message,
  Modal,
  Timeline,
  Drawer
} from 'antd'
import { 
  SearchOutlined, 
  FilterOutlined, 
  SortAscendingOutlined,
  EyeOutlined,
  BranchesOutlined,
  DiffOutlined,
  PlayCircleOutlined,
  ClockCircleOutlined
} from '@ant-design/icons'
import { AtomContext, FilterOptions } from '@/types'
import { MockAPI } from '@/mock/api'
import { categories, statusOptions } from '@/mock/data'
import { EditableTags } from '@/components/common/EditableTags'
import { PageInfo } from '@/components/common/PageInfo'

const { Search } = Input
const { Option } = Select
const { Text, Title, Paragraph } = Typography

export default function HomePage() {
  const [atoms, setAtoms] = useState<AtomContext[]>([])
  const [filteredAtoms, setFilteredAtoms] = useState<AtomContext[]>([])
  const [loading, setLoading] = useState(true)
  const [selectedAtom, setSelectedAtom] = useState<AtomContext | null>(null)
  const [versionDrawerVisible, setVersionDrawerVisible] = useState(false)
  const [detailDrawerVisible, setDetailDrawerVisible] = useState(false)

  const [filter, setFilter] = useState<FilterOptions>({
    search: '',
    category: 'all',
    tags: [],
    status: 'all',
    sortBy: 'updatedAt',
    sortOrder: 'desc'
  })

  // åŠ è½½æ•°æ®
  useEffect(() => {
    loadAtoms()
  }, [])

  // åº”ç”¨ç­›é€‰
  useEffect(() => {
    applyFilter()
  }, [atoms, filter]) // eslint-disable-line react-hooks/exhaustive-deps

  const loadAtoms = async () => {
    try {
      setLoading(true)
      const response = await MockAPI.getAtoms()
      if (response.success) {
        setAtoms(response.data)
      }
    } catch {
      message.error('åŠ è½½æ•°æ®å¤±è´¥')
    } finally {
      setLoading(false)
    }
  }

  const applyFilter = async () => {
    try {
      const response = await MockAPI.searchAtoms(filter)
      if (response.success) {
        setFilteredAtoms(response.data)
      }
    } catch {
      message.error('ç­›é€‰å¤±è´¥')
    }
  }

  const handleSearch = (value: string) => {
    setFilter(prev => ({ ...prev, search: value }))
  }

  const handleCategoryChange = (value: string) => {
    setFilter(prev => ({ ...prev, category: value }))
  }

  const handleStatusChange = (value: string) => {
    setFilter(prev => ({ ...prev, status: value }))
  }

  const handleSortChange = (value: string) => {
    const [sortBy, sortOrder] = value.split('-') as [FilterOptions['sortBy'], 'asc' | 'desc']
    setFilter(prev => ({ ...prev, sortBy, sortOrder }))
  }

  const handleTagsChange = async (atomId: string, newTags: string[]) => {
    try {
      const response = await MockAPI.updateAtomTags(atomId, newTags)
      if (response.success) {
        // æ›´æ–°æœ¬åœ°æ•°æ®
        setAtoms(prev => prev.map(atom => 
          atom.id === atomId ? response.data : atom
        ))
        message.success('æ ‡ç­¾æ›´æ–°æˆåŠŸ')
      }
    } catch {
      message.error('æ ‡ç­¾æ›´æ–°å¤±è´¥')
    }
  }

  const handleUseAtom = async (atomId: string) => {
    try {
      // eslint-disable-next-line react-hooks/rules-of-hooks
      const response = await MockAPI.useAtom(atomId)
      if (response.success) {
        // æ›´æ–°æœ¬åœ°æ•°æ®
        setAtoms(prev => prev.map(atom => 
          atom.id === atomId ? response.data : atom
        ))
        message.success('å·²åº”ç”¨åŸå­åˆ°é¡¹ç›®ä¸­')
      }
    } catch {
      message.error('åº”ç”¨å¤±è´¥')
    }
  }

  const showVersionHistory = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setVersionDrawerVisible(true)
  }

  const showAtomDetail = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setDetailDrawerVisible(true)
  }

  const compareVersions = (atom: AtomContext) => {
    if (atom.versions.length < 2) {
      message.info('éœ€è¦è‡³å°‘2ä¸ªç‰ˆæœ¬æ‰èƒ½è¿›è¡Œå¯¹æ¯”')
      return
    }

    const latestVersion = atom.versions[0]
    const previousVersion = atom.versions[1]

    Modal.info({
      title: 'ç‰ˆæœ¬å¯¹æ¯”',
      width: 800,
      content: (
        <div>
          <Row gutter={16}>
            <Col span={12}>
              <Card title={`ç‰ˆæœ¬ ${previousVersion.version}`} size="small">
                <Text>{previousVersion.description}</Text>
                <br />
                <Text type="secondary">{previousVersion.timestamp}</Text>
              </Card>
            </Col>
            <Col span={12}>
              <Card title={`ç‰ˆæœ¬ ${latestVersion.version}`} size="small">
                <Text>{latestVersion.description}</Text>
                <br />
                <Text type="secondary">{latestVersion.timestamp}</Text>
              </Card>
            </Col>
          </Row>
          <Divider />
          <div>
            <Text strong>ä¸»è¦å˜æ›´ï¼š</Text>
            <ul>
              {latestVersion.changes.map((change, index) => (
                <li key={index}>{change}</li>
              ))}
            </ul>
          </div>
        </div>
      )
    })
  }

  const getCategoryLabel = (category: string) => {
    const categoryItem = categories.find(cat => cat.value === category)
    return categoryItem ? categoryItem.label : category
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'green'
      case 'draft': return 'orange'
      case 'archived': return 'gray'
      default: return 'blue'
    }
  }

  return (
    <div>
      {/* ç­›é€‰æ§åˆ¶æ  */}
      <Card style={{ marginBottom: 24 }}>
        <Row gutter={[16, 16]} align="middle">
          <Col flex="auto">
            <Search
              placeholder="æœç´¢åŸå­æ ‡é¢˜ã€æè¿°æˆ–å†…å®¹..."
              allowClear
              enterButton={<SearchOutlined />}
              size="large"
              onSearch={handleSearch}
            />
          </Col>
          <Col>
            <Space>
              <Select
                value={filter.category}
                onChange={handleCategoryChange}
                style={{ width: 120 }}
                suffixIcon={<FilterOutlined />}
              >
                <Option value="all">å…¨éƒ¨åˆ†ç±»</Option>
                {categories.map(cat => (
                  <Option key={cat.value} value={cat.value}>
                    {cat.label}
                  </Option>
                ))}
              </Select>

              <Select
                value={filter.status}
                onChange={handleStatusChange}
                style={{ width: 100 }}
              >
                <Option value="all">å…¨éƒ¨çŠ¶æ€</Option>
                {statusOptions.map(status => (
                  <Option key={status.value} value={status.value}>
                    {status.label}
                  </Option>
                ))}
              </Select>

              <Select
                value={`${filter.sortBy}-${filter.sortOrder}`}
                onChange={handleSortChange}
                style={{ width: 140 }}
                suffixIcon={<SortAscendingOutlined />}
              >
                <Option value="updatedAt-desc">æœ€æ–°æ›´æ–°</Option>
                <Option value="createdAt-desc">æœ€æ–°åˆ›å»º</Option>
                <Option value="usageCount-desc">ä½¿ç”¨æœ€å¤š</Option>
                <Option value="title-asc">æ ‡é¢˜A-Z</Option>
                <Option value="version-desc">ç‰ˆæœ¬å·</Option>
              </Select>
            </Space>
          </Col>
        </Row>
      </Card>

      {/* åŸå­åˆ—è¡¨ */}
      <Row gutter={[24, 24]}>
        {filteredAtoms.map(atom => (
          <Col xs={24} sm={12} lg={8} xl={6} key={atom.id}>
            <Card
              hoverable
              style={{ height: '100%' }}
              bodyStyle={{ padding: '16px' }}
              actions={[
                <Button
                  key="view"
                  type="text"
                  icon={<EyeOutlined />}
                  onClick={() => showAtomDetail(atom)}
                >
                  æŸ¥çœ‹
                </Button>,
                <Button
                  key="version"
                  type="text"
                  icon={<BranchesOutlined />}
                  onClick={() => showVersionHistory(atom)}
                >
                  ç‰ˆæœ¬
                </Button>,
                <Button
                  key="compare"
                  type="text"
                  icon={<DiffOutlined />}
                  onClick={() => compareVersions(atom)}
                  disabled={atom.versions.length < 2}
                >
                  å¯¹æ¯”
                </Button>,
                <Button
                  key="use"
                  type="text"
                  icon={<PlayCircleOutlined />}
                  onClick={() => handleUseAtom(atom.id)}
                >
                  ä½¿ç”¨
                </Button>
              ]}
            >
              <div style={{ marginBottom: 12 }}>
                <Space>
                  <Badge count={atom.usageCount} color="blue">
                    <Title level={5} style={{ margin: 0 }}>
                      {atom.title}
                    </Title>
                  </Badge>
                  <Tag color={getStatusColor(atom.status)}>
                    {statusOptions.find(s => s.value === atom.status)?.label}
                  </Tag>
                </Space>
              </div>

              <Paragraph 
                ellipsis={{ rows: 2 }}
                style={{ marginBottom: 12, minHeight: 44 }}
              >
                {atom.description}
              </Paragraph>

              <div style={{ marginBottom: 12 }}>
                <EditableTags
                  tags={atom.tags}
                  onChange={(newTags) => handleTagsChange(atom.id, newTags)}
                />
              </div>

              <Divider style={{ margin: '12px 0' }} />

              <Row justify="space-between" align="middle">
                <Col>
                  <Space direction="vertical" size={0}>
                    <Text type="secondary" style={{ fontSize: '12px' }}>
                      {getCategoryLabel(atom.category)} â€¢ v{atom.version}
                    </Text>
                    <Text type="secondary" style={{ fontSize: '12px' }}>
                      by {atom.author} â€¢ {atom.updatedAt}
                    </Text>
                  </Space>
                </Col>
              </Row>
            </Card>
          </Col>
        ))}
      </Row>

      {/* ç©ºçŠ¶æ€ */}
      {filteredAtoms.length === 0 && !loading && (
        <Card style={{ textAlign: 'center', padding: '40px 0' }}>
          <Text type="secondary">æ²¡æœ‰æ‰¾åˆ°åŒ¹é…çš„åŸå­</Text>
        </Card>
      )}

      {/* ç‰ˆæœ¬å†å²æŠ½å±‰ */}
      <Drawer
        title={`ç‰ˆæœ¬å†å² - ${selectedAtom?.title}`}
        placement="right"
        onClose={() => setVersionDrawerVisible(false)}
        open={versionDrawerVisible}
        width={480}
      >
        {selectedAtom && (
          <Timeline>
            {selectedAtom.versions.map((version, index) => (
              <Timeline.Item
                key={version.version}
                color={index === 0 ? 'green' : 'gray'}
                dot={index === 0 ? <ClockCircleOutlined /> : null}
              >
                <div>
                  <Space>
                    <Text strong>ç‰ˆæœ¬ {version.version}</Text>
                    <Text type="secondary">{version.timestamp}</Text>
                  </Space>
                  <br />
                  <Text>{version.description}</Text>
                  <br />
                  <Text type="secondary">by {version.author}</Text>
                  {version.changes.length > 0 && (
                    <div style={{ marginTop: 8 }}>
                      <Text strong>å˜æ›´å†…å®¹ï¼š</Text>
                      <ul style={{ marginTop: 4 }}>
                        {version.changes.map((change, idx) => (
                          <li key={idx}><Text type="secondary">{change}</Text></li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              </Timeline.Item>
            ))}
          </Timeline>
        )}
      </Drawer>

      {/* è¯¦æƒ…æŠ½å±‰ */}
      <Drawer
        title={selectedAtom?.title}
        placement="right"
        onClose={() => setDetailDrawerVisible(false)}
        open={detailDrawerVisible}
        width={600}
      >
        {selectedAtom && (
          <Space direction="vertical" size="large" style={{ width: '100%' }}>
            <div>
              <Space>
                <Tag color={getStatusColor(selectedAtom.status)}>
                  {statusOptions.find(s => s.value === selectedAtom.status)?.label}
                </Tag>
                <Text type="secondary">ç‰ˆæœ¬ {selectedAtom.version}</Text>
                <Text type="secondary">ä½¿ç”¨ {selectedAtom.usageCount} æ¬¡</Text>
              </Space>
            </div>

            <div>
              <Text strong>æè¿°ï¼š</Text>
              <br />
              <Text>{selectedAtom.description}</Text>
            </div>

            <div>
              <Text strong>å†…å®¹ï¼š</Text>
              <div style={{ 
                background: '#f5f5f5', 
                padding: '12px', 
                borderRadius: '6px',
                marginTop: '8px',
                maxHeight: '300px',
                overflow: 'auto'
              }}>
                <pre style={{ margin: 0, whiteSpace: 'pre-wrap' }}>
                  {selectedAtom.content}
                </pre>
              </div>
            </div>

            <div>
              <Text strong>åŠŸèƒ½æ ‡ç­¾ï¼š</Text>
              <br />
              <EditableTags
                tags={selectedAtom.tags}
                onChange={(newTags) => handleTagsChange(selectedAtom.id, newTags)}
              />
            </div>

            <div>
              <Text strong>åˆ›å»ºä¿¡æ¯ï¼š</Text>
              <br />
              <Text type="secondary">
                ç”± {selectedAtom.author} åˆ›å»ºäº {selectedAtom.createdAt}
              </Text>
              <br />
              <Text type="secondary">
                æœ€åæ›´æ–°äº {selectedAtom.updatedAt}
              </Text>
            </div>
          </Space>
        )}
      </Drawer>

      {/* é¡µé¢åŸå‹è¯´æ˜ */}
      <PageInfo
        title="åŸå­ä¸Šä¸‹æ–‡åº“ç®¡ç†ç³»ç»Ÿ"
        description="åŸºäºåŸå­åŒ–ç†å¿µçš„ä¸Šä¸‹æ–‡ç®¡ç†ç³»ç»Ÿï¼Œæ¯ä¸ªåŸå­éƒ½æ˜¯æœ€å°çš„åŠŸèƒ½å•å…ƒï¼Œæ”¯æŒç‰ˆæœ¬ç®¡ç†ã€åŠŸèƒ½æ ‡ç­¾ç¼–è¾‘å’Œå†…å®¹å¯¹æ¯”ã€‚ä¸“æ³¨äºå¿«é€ŸåŸå‹å¼€å‘çš„ä¸Šä¸‹æ–‡å¤ç”¨å’Œç®¡ç†ã€‚"
        features={[
          "åŸå­åŒ–ä¸Šä¸‹æ–‡ç®¡ç†",
          "å¤šç‰ˆæœ¬æ”¯æŒå’Œç‰ˆæœ¬å¯¹æ¯”",
          "å¯ç¼–è¾‘åŠŸèƒ½æ ‡ç­¾",
          "å®æ—¶æœç´¢å’Œç­›é€‰",
          "å˜æ›´æ—¥å¿—è®°å½•",
          "ä½¿ç”¨ç»Ÿè®¡è·Ÿè¸ª"
        ]}
        interactions={[
          "ç‚¹å‡»åŠŸèƒ½æ ‡ç­¾åŒºåŸŸ â†’ è¿›å…¥ç¼–è¾‘æ¨¡å¼ï¼Œå¯ä»¥å¢åˆ æ ‡ç­¾",
          "ç‚¹å‡»ç‰ˆæœ¬æŒ‰é’® â†’ æŸ¥çœ‹ç‰ˆæœ¬å†å²å’Œæ—¶é—´çº¿",
          "ç‚¹å‡»å¯¹æ¯”æŒ‰é’® â†’ å¯¹æ¯”ç›¸é‚»ç‰ˆæœ¬çš„å†…å®¹å·®å¼‚",
          "ç‚¹å‡»ä½¿ç”¨æŒ‰é’® â†’ æ¨¡æ‹Ÿåº”ç”¨åŸå­åˆ°é¡¹ç›®ä¸­",
          "ç‚¹å‡»æŸ¥çœ‹æŒ‰é’® â†’ æŸ¥çœ‹åŸå­çš„è¯¦ç»†ä¿¡æ¯",
          "æœç´¢æ¡†è¾“å…¥ â†’ å®æ—¶ç­›é€‰åŸå­åˆ—è¡¨",
          "ç­›é€‰ä¸‹æ‹‰æ¡† â†’ æŒ‰åˆ†ç±»ã€çŠ¶æ€ã€æ’åºæ–¹å¼ç­›é€‰"
        ]}
        usage="è¿™ä¸ªé¡µé¢é€‚ç”¨äºç®¡ç†åŸå‹å¼€å‘ä¸­çš„å„ç§ä¸Šä¸‹æ–‡ç‰‡æ®µï¼Œå¦‚è§’è‰²å®šä¹‰ã€è®¾è®¡åŸåˆ™ã€ä»£ç æ¨¡æ¿ç­‰ã€‚æ¯ä¸ªåŸå­éƒ½å¯ä»¥ç‹¬ç«‹ç®¡ç†ç‰ˆæœ¬ï¼Œæ–¹ä¾¿åœ¨ä¸åŒé¡¹ç›®ä¸­å¤ç”¨å’Œè¿­ä»£ã€‚æ”¯æŒå›¢é˜Ÿåä½œå’ŒçŸ¥è¯†ç§¯ç´¯ã€‚"
