  SettingOutlined
} from '@ant-design/icons'
import { BestPractice, FilterOptions } from '@/types'
import { MockAPI } from '@/mock/api'
import { categories, statusOptions } from '@/mock/data'
import { EditableTags } from '@/components/common/EditableTags'
import { PageInfo } from '@/components/common/PageInfo'

const { Search } = Input
const { Option } = Select
const { Text, Title, Paragraph } = Typography
const { Panel } = Collapse

export default function BestPracticesPage() {
  const [practices, setPractices] = useState<BestPractice[]>([])
  const [filteredPractices, setFilteredPractices] = useState<BestPractice[]>([])
  const [loading, setLoading] = useState(true)
  const [selectedPractice, setSelectedPractice] = useState<BestPractice | null>(null)
  const [detailDrawerVisible, setDetailDrawerVisible] = useState(false)

  const [filter, setFilter] = useState<FilterOptions>({
    search: '',
    category: 'all',
    tags: [],
    status: 'all',
    sortBy: 'updatedAt',
    sortOrder: 'desc'
  })

  // åŠ è½½æ•°æ®
  useEffect(() => {
    loadPractices()
  }, [])

  // åº”ç”¨ç­›é€‰
  useEffect(() => {
    applyFilter()
  }, [practices, filter]) // eslint-disable-line react-hooks/exhaustive-deps

  const loadPractices = async () => {
    try {
      setLoading(true)
      const response = await MockAPI.getBestPractices()
      if (response.success) {
        setPractices(response.data)
      }
    } catch {
      message.error('åŠ è½½æ•°æ®å¤±è´¥')
    } finally {
      setLoading(false)
    }
  }

  const applyFilter = async () => {
    try {
      const response = await MockAPI.searchBestPractices(filter)
      if (response.success) {
        setFilteredPractices(response.data)
      }
    } catch {
      message.error('ç­›é€‰å¤±è´¥')
    }
  }

  const handleSearch = (value: string) => {
    setFilter(prev => ({ ...prev, search: value }))
  }

  const handleCategoryChange = (value: string) => {
    setFilter(prev => ({ ...prev, category: value }))
  }

  const handleStatusChange = (value: string) => {
    setFilter(prev => ({ ...prev, status: value }))
  }

  const handleSortChange = (value: string) => {
    const [sortBy, sortOrder] = value.split('-') as [FilterOptions['sortBy'], 'asc' | 'desc']
    setFilter(prev => ({ ...prev, sortBy, sortOrder }))
  }

  const handleTagsChange = async (practiceId: string, newTags: string[]) => {
    try {
      const response = await MockAPI.updateBestPracticeTags(practiceId, newTags)
      if (response.success) {
        setPractices(prev => prev.map(practice => 
          practice.id === practiceId ? response.data : practice
        ))
        message.success('æ ‡ç­¾æ›´æ–°æˆåŠŸ')
      }
    } catch {
      message.error('æ ‡ç­¾æ›´æ–°å¤±è´¥')
    }
  }

  const handleUsePractice = async (practiceId: string) => {
    try {
      const response = await MockAPI.useBestPractice(practiceId)
      if (response.success) {
        setPractices(prev => prev.map(practice => 
          practice.id === practiceId ? response.data : practice
        ))
        message.success('å·²åº”ç”¨å®è·µç»„åˆåˆ°é¡¹ç›®ä¸­')
      }
    } catch {
      message.error('åº”ç”¨å¤±è´¥')
    }
  }

  const showPracticeDetail = (practice: BestPractice) => {
    setSelectedPractice(practice)
    setDetailDrawerVisible(true)
  }

  const getCategoryLabel = (category: string) => {
    const categoryItem = categories.find(cat => cat.value === category)
    return categoryItem ? categoryItem.label : category
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'green'
      case 'draft': return 'orange'
      case 'archived': return 'gray'
      default: return 'blue'
    }
  }

  const getComplexityColor = (complexity: string) => {
    switch (complexity) {
      case 'basic': return 'green'
      case 'intermediate': return 'orange'
      case 'advanced': return 'red'
      default: return 'blue'
    }
  }

  const getComplexityLabel = (complexity: string) => {
    switch (complexity) {
      case 'basic': return 'åŸºç¡€'
      case 'intermediate': return 'ä¸­çº§'
      case 'advanced': return 'é«˜çº§'
      default: return complexity
    }
  }

  return (
    <div>
      {/* ç­›é€‰æ§åˆ¶æ  */}
      <Card style={{ marginBottom: 24 }}>
        <Row gutter={[16, 16]} align="middle">
          <Col flex="auto">
            <Search
              placeholder="æœç´¢ç»„åˆå®è·µæ ‡é¢˜ã€æè¿°æˆ–å†…å®¹..."
              allowClear
              enterButton={<SearchOutlined />}
              size="large"
              onSearch={handleSearch}
            />
          </Col>
          <Col>
            <Space>
              <Select
                value={filter.category}
                onChange={handleCategoryChange}
                style={{ width: 120 }}
                suffixIcon={<FilterOutlined />}
              >
                <Option value="all">å…¨éƒ¨åˆ†ç±»</Option>
                {categories.map(cat => (
                  <Option key={cat.value} value={cat.value}>
                    {cat.label}
                  </Option>
                ))}
              </Select>

              <Select
                value={filter.status}
                onChange={handleStatusChange}
                style={{ width: 100 }}
              >
                <Option value="all">å…¨éƒ¨çŠ¶æ€</Option>
                {statusOptions.map(status => (
                  <Option key={status.value} value={status.value}>
                    {status.label}
                  </Option>
                ))}
              </Select>

              <Select
                value={`${filter.sortBy}-${filter.sortOrder}`}
                onChange={handleSortChange}
                style={{ width: 140 }}
                suffixIcon={<SortAscendingOutlined />}
              >
                <Option value="updatedAt-desc">æœ€æ–°æ›´æ–°</Option>
                <Option value="createdAt-desc">æœ€æ–°åˆ›å»º</Option>
                <Option value="usageCount-desc">ä½¿ç”¨æœ€å¤š</Option>
                <Option value="title-asc">æ ‡é¢˜A-Z</Option>
                <Option value="rating-desc">è¯„åˆ†æœ€é«˜</Option>
              </Select>
            </Space>
          </Col>
        </Row>
      </Card>

      {/* ç»„åˆå®è·µåˆ—è¡¨ */}
      <Row gutter={[24, 24]}>
        {filteredPractices.map(practice => (
          <Col xs={24} sm={12} lg={8} xl={6} key={practice.id}>
            <Card
              hoverable
              style={{ height: '100%' }}
