import { AtomContext, FilterOptions, AtomVersion } from '@/types'
import { mockAtoms, availableTags } from './data'

// ç¬æ—¶APIæ¨¡æ‹Ÿç±»
export class MockAPI {

  // è·å–æ‰€æœ‰åŸå­æ•°æ®
  static async getAtoms(): Promise<{ success: boolean; data: AtomContext[] }> {
    // æ¨¡æ‹Ÿç½‘ç»œå»¶è¿Ÿï¼ˆä½†ä¿æŒç¬æ—¶å“åº”ï¼‰
    await new Promise(resolve => setTimeout(resolve, 10))
    return {
      success: true,
      data: mockAtoms
    }
  }

  // æ ¹æ®IDè·å–å•ä¸ªåŸå­
  static async getAtomById(id: string): Promise<{ success: boolean; data: AtomContext | null }> {
    await new Promise(resolve => setTimeout(resolve, 10))
    const atom = mockAtoms.find(atom => atom.id === id)
    return {
      success: true,
      data: atom || null
    }
  }

  // æœç´¢å’Œç­›é€‰åŸå­
  static async searchAtoms(filter: FilterOptions): Promise<{ success: boolean; data: AtomContext[] }> {
    await new Promise(resolve => setTimeout(resolve, 10))

    let filteredAtoms = [...mockAtoms]

    // æœç´¢è¿‡æ»¤
    if (filter.search) {
      const searchLower = filter.search.toLowerCase()
      filteredAtoms = filteredAtoms.filter(atom =>
        atom.title.toLowerCase().includes(searchLower) ||
        atom.description.toLowerCase().includes(searchLower) ||
        atom.content.toLowerCase().includes(searchLower) ||
        atom.tags.some(tag => tag.toLowerCase().includes(searchLower))
      )
    }

    // åˆ†ç±»è¿‡æ»¤
    if (filter.category && filter.category !== 'all') {
      filteredAtoms = filteredAtoms.filter(atom => atom.category === filter.category)
    }

    // çŠ¶æ€è¿‡æ»¤
    if (filter.status && filter.status !== 'all') {
      filteredAtoms = filteredAtoms.filter(atom => atom.status === filter.status)
    }

    // æ ‡ç­¾è¿‡æ»¤
    if (filter.tags && filter.tags.length > 0) {
      filteredAtoms = filteredAtoms.filter(atom =>
        filter.tags.some(tag => atom.tags.includes(tag))
      )
    }

    // æ’åº
    filteredAtoms.sort((a, b) => {
      let aValue, bValue

      switch (filter.sortBy) {
        case 'title':
          aValue = a.title
          bValue = b.title
          break
        case 'version':
          aValue = a.version
          bValue = b.version
          break
        case 'createdAt':
          aValue = new Date(a.createdAt).getTime()
          bValue = new Date(b.createdAt).getTime()
          break
        case 'updatedAt':
          aValue = new Date(a.updatedAt).getTime()
          bValue = new Date(b.updatedAt).getTime()
          break
        case 'usageCount':
          aValue = a.usageCount
          bValue = b.usageCount
          break
        default:
          aValue = a.title
          bValue = b.title
      }

      if (filter.sortOrder === 'desc') {
        return aValue > bValue ? -1 : aValue < bValue ? 1 : 0
      } else {
        return aValue < bValue ? -1 : aValue > bValue ? 1 : 0
      }
    })

    return {
      success: true,
      data: filteredAtoms
    }
  }

  // æ›´æ–°åŸå­æ ‡ç­¾
  static async updateAtomTags(atomId: string, newTags: string[]): Promise<{ success: boolean; data: AtomContext }> {
    await new Promise(resolve => setTimeout(resolve, 10))

    const atomIndex = mockAtoms.findIndex(atom => atom.id === atomId)
    if (atomIndex === -1) {
      throw new Error('åŸå­ä¸å­˜åœ¨')
    }

    // æ›´æ–°æ ‡ç­¾
    mockAtoms[atomIndex] = {
      ...mockAtoms[atomIndex],
      tags: newTags,
      updatedAt: new Date().toISOString().split('T')[0]
    }

    return {
      success: true,
      data: mockAtoms[atomIndex]
    }
  }

  // ä½¿ç”¨åŸå­ï¼ˆå¢åŠ ä½¿ç”¨æ¬¡æ•°ï¼‰
  static async useAtom(atomId: string): Promise<{ success: boolean; data: AtomContext }> {
    await new Promise(resolve => setTimeout(resolve, 10))

    const atomIndex = mockAtoms.findIndex(atom => atom.id === atomId)
    if (atomIndex === -1) {
      throw new Error('åŸå­ä¸å­˜åœ¨')
    }

    // å¢åŠ ä½¿ç”¨æ¬¡æ•°
    mockAtoms[atomIndex] = {
      ...mockAtoms[atomIndex],
      usageCount: mockAtoms[atomIndex].usageCount + 1
    }

    return {
      success: true,
      data: mockAtoms[atomIndex]
    }
  }

  // è·å–ç‰ˆæœ¬å†å²
  static async getVersionHistory(atomId: string): Promise<{ success: boolean; data: AtomVersion[] }> {
    await new Promise(resolve => setTimeout(resolve, 10))

    const atom = mockAtoms.find(atom => atom.id === atomId)
    if (!atom) {
      throw new Error('åŸå­ä¸å­˜åœ¨')
    }

    return {
      success: true,
      data: atom.versions
    }
  }

  // æ¯”è¾ƒç‰ˆæœ¬
  static async compareVersions(atomId: string, version1: string, version2: string): Promise<{
    success: boolean
    data: {
      version1: AtomVersion | null
      version2: AtomVersion | null
      diff: string[]
    }
  }> {
    await new Promise(resolve => setTimeout(resolve, 10))

    const atom = mockAtoms.find(atom => atom.id === atomId)
    if (!atom) {
      throw new Error('åŸå­ä¸å­˜åœ¨')
    }

    const v1 = atom.versions.find(v => v.version === version1)
    const v2 = atom.versions.find(v => v.version === version2)

    // ç®€å•çš„å·®å¼‚æ¯”è¾ƒï¼ˆå®é™…é¡¹ç›®ä¸­å¯ä»¥ä½¿ç”¨æ›´å¤æ‚çš„diffç®—æ³•ï¼‰
    const diff = []
    if (v1 && v2) {
      if (v1.content !== v2.content) {
        diff.push('å†…å®¹å‘ç”Ÿå˜åŒ–')
      }
      if (v1.description !== v2.description) {
        diff.push('æè¿°å‘ç”Ÿå˜åŒ–')
      }
      if (JSON.stringify(v1.changes) !== JSON.stringify(v2.changes)) {
        diff.push('å˜æ›´è®°å½•ä¸åŒ')
      }
    }

    return {
      success: true,
      data: {
        version1: v1 || null,
        version2: v2 || null,
        diff
      }
    }
  }

  // è·å–å¯ç”¨æ ‡ç­¾
  static async getAvailableTags(): Promise<{ success: boolean; data: string[] }> {
    await new Promise(resolve => setTimeout(resolve, 10))
    return {
      success: true,
      data: availableTags
    }
  }

  // åˆ›å»ºæ–°åŸå­ï¼ˆæ¨¡æ‹Ÿï¼‰
  static async createAtom(atomData: Partial<AtomContext>): Promise<{ success: boolean; data: AtomContext }> {
    await new Promise(resolve => setTimeout(resolve, 10))

    const newAtom: AtomContext = {
      id: `atom-${Date.now()}`,
      title: atomData.title || 'æ–°åŸå­',
      content: atomData.content || '',
      description: atomData.description || '',
      version: 'v1.0',
      tags: atomData.tags || [],
      category: atomData.category || 'other',
      status: 'draft',
      author: 'å½“å‰ç”¨æˆ·',
      createdAt: new Date().toISOString().split('T')[0],
      updatedAt: new Date().toISOString().split('T')[0],
      usageCount: 0,
      versions: [{
        version: 'v1.0',
        content: atomData.content || '',
        description: 'åˆå§‹ç‰ˆæœ¬',
        timestamp: new Date().toISOString().split('T')[0],
        author: 'å½“å‰ç”¨æˆ·',
        changes: ['åˆ›å»ºåŸå­']
      }],
      changelog: [{
        id: `log-${Date.now()}`,
        version: 'v1.0',
        type: 'created',
        description: 'åˆ›å»ºæ–°åŸå­',
        timestamp: new Date().toISOString().split('T')[0],
        author: 'å½“å‰ç”¨æˆ·',
        changes: ['åˆ›å»ºåŸå­']
      }]
    }

    return {
