'use client'
import { useState, useEffect } from 'react'
import { 
  Card, 
  Row, 
  Col, 
  Statistic, 
  Table,
  Tag,
  Progress,
  Space,
  Select,
  DatePicker,
  Typography
} from 'antd'
import { 
  BarChartOutlined,
  RiseOutlined,
  FileTextOutlined,
  TagsOutlined,
  EyeOutlined
} from '@ant-design/icons'
import { mockAtoms } from '@/mock/data'
import { PageInfo } from '@/components/common/PageInfo'
import dayjs from 'dayjs'

const { RangePicker } = DatePicker
const { Title, Text } = Typography

interface AnalyticsData {
  totalAtoms: number
  totalUsage: number
  totalTags: number
  totalCategories: number
  avgUsagePerAtom: number
  mostUsedAtom: {
    title: string
    usageCount: number
    tags: string[]
    version: string
    updatedAt: string
  }
  recentActivity: Array<{
    action: string
    atom: string
    time: string
    type: string
  }>
  categoryDistribution: Array<{
    category: string
    count: number
    percentage: string
  }>
  tagPopularity: Array<{
    tag: string
    count: number
  }>
  versionStats: Array<{
    title: string
    version: string
    versionCount: number
    lastUpdate: string
  }>
  dailyUsage: Array<{
    date: string
    usage: number
  }>
}

export default function AnalyticsPage() {
  const [data, setData] = useState<AnalyticsData | null>(null)
  const [loading, setLoading] = useState(true)
  const [dateRange, setDateRange] = useState<[dayjs.Dayjs | null, dayjs.Dayjs | null] | null>(null)
  const [timeframe, setTimeframe] = useState('30days')

  useEffect(() => {
    loadAnalyticsData()
  }, [timeframe, dateRange])

  const loadAnalyticsData = async () => {
    setLoading(true)

    // æ¨¡æ‹ŸAPIè°ƒç”¨
    setTimeout(() => {
      const totalUsage = mockAtoms.reduce((sum, atom) => sum + atom.usageCount, 0)
      const allTags = mockAtoms.flatMap(atom => atom.tags)
      const uniqueTags = [...new Set(allTags)]

      // åˆ†ç±»åˆ†å¸ƒç»Ÿè®¡
      const categoryStats = mockAtoms.reduce((acc, atom) => {
        acc[atom.category] = (acc[atom.category] || 0) + 1
        return acc
      }, {} as Record<string, number>)

      const categoryDistribution = Object.entries(categoryStats).map(([category, count]) => ({
        category,
        count,
        percentage: ((count / mockAtoms.length) * 100).toFixed(1)
      }))

      // æ ‡ç­¾çƒ­åº¦ç»Ÿè®¡
      const tagStats = allTags.reduce((acc, tag) => {
        acc[tag] = (acc[tag] || 0) + 1
        return acc
      }, {} as Record<string, number>)

      const tagPopularity = Object.entries(tagStats)
        .map(([tag, count]) => ({ tag, count }))
        .sort((a, b) => b.count - a.count)
        .slice(0, 10)

      // ç‰ˆæœ¬ç»Ÿè®¡
      const versionStats = mockAtoms.map(atom => ({
        title: atom.title,
        version: atom.version,
        versionCount: atom.versions.length,
        lastUpdate: atom.updatedAt
      }))

      // æ¨¡æ‹Ÿæœ€è¿‘æ´»åŠ¨
      const recentActivity = [
        { action: 'æ›´æ–°åŸå­', atom: 'å‰ç«¯è®¾è®¡å¸ˆè§’è‰²å®šä¹‰', time: '2å°æ—¶å‰', type: 'update' },
        { action: 'ä½¿ç”¨åŸå­', atom: 'Mockæ•°æ®é©±åŠ¨ç­–ç•¥', time: '4å°æ—¶å‰', type: 'usage' },
        { action: 'åˆ›å»ºç‰ˆæœ¬', atom: 'äº¤äº’å®Œæ•´æ€§åŸåˆ™', time: '6å°æ—¶å‰', type: 'version' },
        { action: 'æ·»åŠ æ ‡ç­¾', atom: 'æŒ‰é’®äº¤äº’æ ‡å‡†ä»£ç ', time: '1å¤©å‰', type: 'tag' },
        { action: 'ä½¿ç”¨åŸå­', atom: 'å‰ç«¯è®¾è®¡å¸ˆè§’è‰²å®šä¹‰', time: '1å¤©å‰', type: 'usage' }
      ]

      // æ¨¡æ‹Ÿæ¯æ—¥ä½¿ç”¨é‡
      const dailyUsage = Array.from({ length: 30 }, (_, i) => ({
        date: dayjs().subtract(29 - i, 'day').format('MM-DD'),
        usage: Math.floor(Math.random() * 20) + 5
      }))

      const analyticsData: AnalyticsData = {
        totalAtoms: mockAtoms.length,
        totalUsage,
        totalTags: uniqueTags.length,
        totalCategories: Object.keys(categoryStats).length,
        avgUsagePerAtom: parseFloat((totalUsage / mockAtoms.length).toFixed(1)),
        mostUsedAtom: mockAtoms.reduce((max, atom) => 
          atom.usageCount > max.usageCount ? atom : max
        ),
        recentActivity,
        categoryDistribution,
        tagPopularity,
        versionStats,
        dailyUsage
      }

      setData(analyticsData)
      setLoading(false)
    }, 1000)
  }

  if (!data) {
    return <div>åŠ è½½ä¸­...</div>
  }

  const activityColumns = [
    {
      title: 'æ“ä½œ',
      dataIndex: 'action',
      key: 'action',
      render: (action: string, record: { type: string }) => (
        <Space>
          <Tag color={getActionColor(record.type)}>{action}</Tag>
        </Space>
      )
    },
    {
      title: 'åŸå­',
      dataIndex: 'atom',
      key: 'atom'
    },
    {
      title: 'æ—¶é—´',
      dataIndex: 'time',
      key: 'time'
    }
  ]

  function getActionColor(type: string) {
    const colors: Record<string, string> = {
      'update': 'blue',
      'usage': 'green', 
      'version': 'purple',
      'tag': 'orange'
    }
    return colors[type] || 'default'
  }

  return (
    <div>
      {/* æ—¶é—´ç­›é€‰ */}
      <Card style={{ marginBottom: 24 }}>
        <Row gutter={16} align="middle">
          <Col>
            <Text strong>æ—¶é—´èŒƒå›´ï¼š</Text>
          </Col>
          <Col>
            <Select
              value={timeframe}
              onChange={setTimeframe}
              style={{ width: 120 }}
            >
              <Select.Option value="7days">è¿‘7å¤©</Select.Option>
              <Select.Option value="30days">è¿‘30å¤©</Select.Option>
              <Select.Option value="90days">è¿‘90å¤©</Select.Option>
            </Select>
          </Col>
          <Col>
            <RangePicker
              value={dateRange}
              onChange={setDateRange}
              format="YYYY-MM-DD"
            />
          </Col>
        </Row>
      </Card>

      {/* æ ¸å¿ƒæŒ‡æ ‡ */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title="æ€»åŸå­æ•°"
              value={data.totalAtoms}
              prefix={<FileTextOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="æ€»ä½¿ç”¨æ¬¡æ•°"
              value={data.totalUsage}
              prefix={<RiseOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="å¹³å‡ä½¿ç”¨æ¬¡æ•°"
              value={data.avgUsagePerAtom}
              precision={1}
              prefix={<BarChartOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="æ´»è·ƒæ ‡ç­¾æ•°"
              value={data.totalTags}
              prefix={<TagsOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={16} style={{ marginBottom: 24 }}>
        {/* åˆ†ç±»åˆ†å¸ƒ */}
        <Col span={12}>
          <Card title="åˆ†ç±»åˆ†å¸ƒ" extra={<EyeOutlined />}>
            <Space direction="vertical" style={{ width: '100%' }}>
              {data.categoryDistribution.map((item, index) => (
                <div key={index}>
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    marginBottom: 8 
                  }}>
                    <Text>{item.category}</Text>
                    <Text>{item.count} ({item.percentage}%)</Text>
                  </div>
                  <Progress 
                    percent={parseFloat(item.percentage)} 
                    showInfo={false}
                    strokeColor="#1890ff"
                  />
                </div>
              ))}
            </Space>
          </Card>
        </Col>

        {/* çƒ­é—¨æ ‡ç­¾ */}
        <Col span={12}>
          <Card title="çƒ­é—¨æ ‡ç­¾" extra={<TagsOutlined />}>
            <Space direction="vertical" style={{ width: '100%' }}>
              {data.tagPopularity.slice(0, 8).map((item, index) => (
                <div key={index}>
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    marginBottom: 8 
                  }}>
                    <Tag color="blue">{item.tag}</Tag>
                    <Text>{item.count} æ¬¡ä½¿ç”¨</Text>
                  </div>
                  <Progress 
                    percent={(item.count / data.tagPopularity[0].count) * 100} 
                    showInfo={false}
                    strokeColor="#52c41a"
                  />
                </div>
              ))}
            </Space>
          </Card>
        </Col>
      </Row>

      <Row gutter={16} style={{ marginBottom: 24 }}>
        {/* æœ€å—æ¬¢è¿åŸå­ */}
        <Col span={12}>
          <Card title="æœ€å—æ¬¢è¿åŸå­">
            <div style={{ textAlign: 'center', padding: '20px 0' }}>
              <Title level={4}>{data.mostUsedAtom.title}</Title>
              <Statistic
                value={data.mostUsedAtom.usageCount}
                suffix="æ¬¡ä½¿ç”¨"
                valueStyle={{ color: '#52c41a', fontSize: '24px' }}
              />
              <div style={{ marginTop: 16 }}>
                <Space wrap>
                  {data.mostUsedAtom.tags.map((tag: string) => (
                    <Tag key={tag} color="blue">{tag}</Tag>
                  ))}
                </Space>
              </div>
              <Text type="secondary" style={{ display: 'block', marginTop: 8 }}>
                ç‰ˆæœ¬: {data.mostUsedAtom.version} | æ›´æ–°äº: {data.mostUsedAtom.updatedAt}
              </Text>
            </div>
          </Card>
        </Col>

        {/* ç‰ˆæœ¬ç»Ÿè®¡ */}
        <Col span={12}>
          <Card title="ç‰ˆæœ¬ç»Ÿè®¡">
            <Table
              dataSource={data.versionStats}
              rowKey="title"
              pagination={false}
              size="small"
              columns={[
                {
                  title: 'åŸå­åç§°',
                  dataIndex: 'title',
                  key: 'title',
                  ellipsis: true
                },
                {
                  title: 'å½“å‰ç‰ˆæœ¬',
                  dataIndex: 'version',
                  key: 'version',
                  render: (version: string) => <Tag>{version}</Tag>
                },
                {
                  title: 'ç‰ˆæœ¬æ•°',
                  dataIndex: 'versionCount',
                  key: 'versionCount',
                  sorter: (a: { versionCount: number }, b: { versionCount: number }) => a.versionCount - b.versionCount
                }
              ]}
            />
          </Card>
        </Col>
      </Row>

      {/* æœ€è¿‘æ´»åŠ¨ */}
      <Card title="æœ€è¿‘æ´»åŠ¨" style={{ marginBottom: 24 }}>
        <Table
          dataSource={data.recentActivity}
          columns={activityColumns}
          rowKey={(record, index) => `activity-${index}`}
          pagination={false}
          size="small"
        />
      </Card>

      {/* é¡µé¢åŸå‹è¯´æ˜ */}
      <PageInfo
        title="ç»Ÿè®¡åˆ†æ"
        description="å±•ç¤ºåŸå­ä¸Šä¸‹æ–‡åº“çš„ä½¿ç”¨ç»Ÿè®¡å’Œè¶‹åŠ¿åˆ†æï¼ŒåŒ…æ‹¬åŸå­ä½¿ç”¨æƒ…å†µã€åˆ†ç±»åˆ†å¸ƒã€æ ‡ç­¾çƒ­åº¦ã€ç‰ˆæœ¬ç»Ÿè®¡ç­‰å¤šç»´åº¦æ•°æ®åˆ†æã€‚"
        features={[
          "æ ¸å¿ƒæŒ‡æ ‡ç»Ÿè®¡å±•ç¤º",
          "åˆ†ç±»åˆ†å¸ƒå¯è§†åŒ–",
          "çƒ­é—¨æ ‡ç­¾æ’è¡Œ",
          "æœ€å—æ¬¢è¿åŸå­å±•ç¤º",
          "ç‰ˆæœ¬ç»Ÿè®¡åˆ†æ",
          "æœ€è¿‘æ´»åŠ¨è®°å½•"
        ]}
        interactions={[
          "æ—¶é—´èŒƒå›´é€‰æ‹© â†’ ç­›é€‰ç»Ÿè®¡æ•°æ®",
          "æ—¥æœŸèŒƒå›´é€‰æ‹© â†’ è‡ªå®šä¹‰æ—¶é—´åŒºé—´",
          "è¿›åº¦æ¡ â†’ æ˜¾ç¤ºç›¸å¯¹æ¯”ä¾‹",
          "è¡¨æ ¼æ’åº â†’ æŒ‰ç‰ˆæœ¬æ•°ç­‰å­—æ®µæ’åº",
