#!/usr/bin/env node

/**
 * SylloOS CLI - 统一命令行入口点
 * 管理所有子CLI工具
 */

const { spawn } = require('child_process');
const path = require('path');
const fs = require('fs');

// CLI工具配置
const CLI_TOOLS = {
  'mermaid': {
    name: 'Mermaid Flow CLI',
    description: 'Mermaid流程图管理工具',
    path: 'apps/cli/mermaid-flow-cli',
    command: 'python',
    args: ['-m', 'mermaid_flow_cli.main'],
    pythonPath: true
  },
  'task': {
    name: 'Task CLI',
    description: '任务管理CLI工具',
    path: 'apps/cli/task-cli',
    command: 'bash',
    args: ['task-hub'],
    workdir: true
  },
  'task-hub': {
    name: 'Task Hub Slice CLI',
    description: '任务管理功能片段CLI',
    path: 'apps/task-hub/features/slice1-task-management/cli',
    command: 'bash',
    args: ['task-hub'],
    workdir: true
  }
};

// 颜色输出
const colors = {
  reset: '\x1b[0m',
  bright: '\x1b[1m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  magenta: '\x1b[35m',
  cyan: '\x1b[36m'
};

function colorize(text, color) {
  return `${colors[color]}${text}${colors.reset}`;
}

// 获取项目根目录
function getProjectRoot() {
  let currentDir = __dirname;
  while (currentDir !== path.dirname(currentDir)) {
    if (fs.existsSync(path.join(currentDir, 'nx.json'))) {
      return currentDir;
    }
    currentDir = path.dirname(currentDir);
  }
  throw new Error('无法找到项目根目录 (nx.json)');
}

// 显示帮助信息
function showHelp() {
  console.log(colorize('SylloOS CLI - 统一命令行工具管理器', 'cyan'));
  console.log('');
  console.log(colorize('用法:', 'bright'));
  console.log('  syllo <tool> [options]');
  console.log('  syllo --list             # 列出所有可用工具');
  console.log('  syllo --help             # 显示帮助信息');
  console.log('');
  console.log(colorize('可用工具:', 'bright'));

  Object.entries(CLI_TOOLS).forEach(([key, tool]) => {
    console.log(`  ${colorize(key.padEnd(12), 'green')} ${tool.description}`);
  });

  console.log('');
  console.log(colorize('示例:', 'bright'));
  console.log('  syllo mermaid --help     # 查看mermaid工具帮助');
  console.log('  syllo task list          # 使用任务管理CLI');
  console.log('  syllo task-hub status    # 使用任务中心CLI');
  console.log('');
}

// 列出所有工具
function listTools() {
  console.log(colorize('可用的CLI工具:', 'cyan'));
  console.log('');

  Object.entries(CLI_TOOLS).forEach(([key, tool]) => {
    const statusColor = fs.existsSync(path.join(getProjectRoot(), tool.path)) ? 'green' : 'red';
    const status = fs.existsSync(path.join(getProjectRoot(), tool.path)) ? '✓' : '✗';

    console.log(`${colorize(status, statusColor)} ${colorize(key.padEnd(12), 'bright')} ${tool.name}`);
    console.log(`   ${colorize('描述:', 'yellow')} ${tool.description}`);
    console.log(`   ${colorize('路径:', 'yellow')} ${tool.path}`);
    console.log('');
  });
}

// 执行CLI工具
function executeTool(toolName, args) {
  const tool = CLI_TOOLS[toolName];
  if (!tool) {
    console.error(colorize(`错误: 未知的工具 "${toolName}"`, 'red'));
    console.log('');
    console.log('使用 "syllo --list" 查看可用工具');
    process.exit(1);
  }

  const projectRoot = getProjectRoot();
  const toolPath = path.join(projectRoot, tool.path);

  if (!fs.existsSync(toolPath)) {
    console.error(colorize(`错误: 工具路径不存在: ${toolPath}`, 'red'));
    process.exit(1);
  }

  // 设置工作目录
  const workingDir = tool.workdir ? toolPath : projectRoot;

  // 设置环境变量
  const env = { ...process.env };
  if (tool.pythonPath) {
    env.PYTHONPATH = `${toolPath}:${env.PYTHONPATH || ''}`;
  }

  // 构建完整命令
  let command = tool.command;
  let commandArgs = [...tool.args, ...args];

  // 如果是Python命令，尝试使用虚拟环境
  if (tool.command === 'python') {
    const venvPython = path.join(toolPath, 'venv', 'bin', 'python');
    if (fs.existsSync(venvPython)) {
      command = venvPython;
      commandArgs = tool.args.slice(1).concat(args); // 移除 -m 前的 python 部分
    }
  }

  console.log(colorize(`启动 ${tool.name}...`, 'cyan'));

  // 执行命令
  const child = spawn(command, commandArgs, {
    cwd: workingDir,
    env: env,
    stdio: 'inherit'
  });

  child.on('error', (error) => {
    console.error(colorize(`执行错误: ${error.message}`, 'red'));
    process.exit(1);
  });

  child.on('close', (code) => {
    process.exit(code);
  });
}

// 主函数
function main() {
  const args = process.argv.slice(2);

  if (args.length === 0 || args[0] === '--help' || args[0] === '-h') {
    showHelp();
    return;
  }

  if (args[0] === '--list' || args[0] === '-l') {
    listTools();
    return;
  }

  const toolName = args[0];
  const toolArgs = args.slice(1);

  executeTool(toolName, toolArgs);
}

// 错误处理
process.on('uncaughtException', (error) => {
  console.error(colorize(`未捕获的异常: ${error.message}`, 'red'));
  process.exit(1);
});

process.on('unhandledRejection', (reason) => {
  console.error(colorize(`未处理的Promise拒绝: ${reason}`, 'red'));
  process.exit(1);
});

if (require.main === module) {
  main();
}

module.exports = { CLI_TOOLS, executeTool };

