/**
 * Nx Docs Command - 显示项目文档
 * 使用: nx docs <project> [section]
 */

const fs = require('fs');
const path = require('path');

const [,, project, section] = process.argv;

const PROJECTS = {
  'mermaid-cli': 'apps/cli/mermaid-flow-cli',
  'task-cli': 'apps/cli/task-cli',
  'task-hub-cli': 'apps/task-hub/features/slice1-task-management/cli'
};

function showDocs(project, section) {
  if (!project) {
    console.log('📚 可用的文档项目:');
    Object.keys(PROJECTS).forEach(p => {
      console.log(`  - ${p}`);
    });
    return;
  }

  const projectPath = PROJECTS[project];
  if (!projectPath) {
    console.error(`❌ 未知项目: ${project}`);
    return;
  }

  const docPath = path.join(projectPath, 'README.md');
  if (!fs.existsSync(docPath)) {
    console.error(`❌ 文档不存在: ${docPath}`);
    return;
  }

  const content = fs.readFileSync(docPath, 'utf8');

  if (section) {
    // 显示特定章节
    const sections = content.split(/^##\s+/m);
    const targetSection = sections.find(s => 
      s.toLowerCase().includes(section.toLowerCase())
    );

    if (targetSection) {
      console.log(`## ${targetSection}`);
    } else {
      console.error(`❌ 未找到章节: ${section}`);
    }
  } else {
    // 显示完整文档
    console.log(content);
  }
}

showDocs(project, section);

