const fs = require('fs');
const path = require('path');

console.log('🏗️ Syllo Nx Workspace Structure Test');
console.log('=====================================');

const requiredDirs = [
  'apps/frontend',
  'apps/backend', 
  'apps/cli',
  'libs/shared',
  'libs/frontend',
  'libs/backend',
  'libs/engines',
  'standards',
  'context-engineering',
  'docs',
  'external-knowledge',
  'tools'
];

const requiredFiles = [
  'nx.json',
  'workspace.json',
  'package.json',
  'tsconfig.base.json',
  'README.md'
];

console.log('📁 检查目录结构...');
requiredDirs.forEach(dir => {
  const dirPath = path.join(__dirname, dir);
  const exists = fs.existsSync(dirPath);
  console.log(`${exists ? '✅' : '❌'} ${dir}`);
});

console.log('\n📄 检查配置文件...');
requiredFiles.forEach(file => {
  const filePath = path.join(__dirname, file);
  const exists = fs.existsSync(filePath);
  console.log(`${exists ? '✅' : '❌'} ${file}`);
});

console.log('\n🎯 Nx 工作空间结构验证完成!');

