import { AtomContext, AtomVersion, ChangeLog, SearchFilters } from '@/types'
import { mockAtomContexts, mockChangeLogs, availableTags, categoryOptions } from './data'
import dayjs from 'dayjs'

export class MockAPI {
  // 获取所有原子上下文
  static getAtomContexts = (filters?: Partial<SearchFilters>): Promise<{
    success: boolean
    data: AtomContext[]
    total: number
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        let filteredData = [...mockAtomContexts]

        // 关键词搜索
        if (filters?.keyword) {
          const keyword = filters.keyword.toLowerCase()
          filteredData = filteredData.filter(atom => 
            atom.name.toLowerCase().includes(keyword) ||
            atom.description.toLowerCase().includes(keyword) ||
            atom.content.toLowerCase().includes(keyword)
          )
        }

        // 分类筛选
        if (filters?.category && filters.category !== 'all') {
          filteredData = filteredData.filter(atom => atom.category === filters.category)
        }

        // 标签筛选
        if (filters?.tags && filters.tags.length > 0) {
          filteredData = filteredData.filter(atom =>
            filters.tags!.some(tag => atom.tags.includes(tag))
          )
        }

        // 状态筛选
        if (filters?.status && filters.status !== 'all') {
          filteredData = filteredData.filter(atom => atom.status === filters.status)
        }

        // 排序
        if (filters?.sortBy) {
          filteredData.sort((a, b) => {
            let aValue, bValue
            switch (filters.sortBy) {
              case 'name':
                aValue = a.name
                bValue = b.name
                break
              case 'createdAt':
                aValue = new Date(a.createdAt).getTime()
                bValue = new Date(b.createdAt).getTime()
                break
              case 'updatedAt':
                aValue = new Date(a.updatedAt).getTime()
                bValue = new Date(b.updatedAt).getTime()
                break
              case 'usageCount':
                aValue = a.usageCount
                bValue = b.usageCount
                break
              default:
                return 0
            }

            if (filters.sortOrder === 'desc') {
              return aValue > bValue ? -1 : 1
            } else {
              return aValue < bValue ? -1 : 1
            }
          })
        }

        resolve({
          success: true,
          data: filteredData,
          total: filteredData.length
        })
      }, 50) // 50ms延迟模拟网络请求
    })
  }

  // 获取单个原子上下文
  static getAtomContext = (id: string): Promise<{
    success: boolean
    data: AtomContext | null
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const atom = mockAtomContexts.find(a => a.id === id)
        resolve({
          success: true,
          data: atom || null
        })
      }, 30)
    })
  }

  // 保存原子上下文
  static saveAtomContext = (atom: Partial<AtomContext>): Promise<{
    success: boolean
    data: AtomContext
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const newAtom: AtomContext = {
          id: atom.id || `atom-${Date.now()}`,
          name: atom.name || '',
          description: atom.description || '',
          content: atom.content || '',
          version: atom.version || 'v1.0',
          category: atom.category || 'template',
          tags: atom.tags || [],
          createdAt: atom.createdAt || dayjs().format('YYYY-MM-DD'),
          updatedAt: dayjs().format('YYYY-MM-DD'),
          author: atom.author || '当前用户',
          status: atom.status || 'active',
          usageCount: atom.usageCount || 0,
          versions: atom.versions || []
        }

        // 如果是新建，添加到mock数据中
        if (!atom.id) {
          mockAtomContexts.push(newAtom)
        } else {
          // 如果是更新，替换existing数据
          const index = mockAtomContexts.findIndex(a => a.id === atom.id)
          if (index !== -1) {
            mockAtomContexts[index] = newAtom
          }
        }

        resolve({
          success: true,
          data: newAtom
        })
      }, 100)
    })
  }

  // 删除原子上下文
  static deleteAtomContext = (id: string): Promise<{
    success: boolean
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const index = mockAtomContexts.findIndex(a => a.id === id)
        if (index !== -1) {
          mockAtomContexts.splice(index, 1)
        }
        resolve({ success: true })
      }, 50)
    })
  }

  // 获取版本历史
  static getVersionHistory = (atomId: string): Promise<{
    success: boolean
    data: AtomVersion[]
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const atom = mockAtomContexts.find(a => a.id === atomId)
        resolve({
          success: true,
          data: atom?.versions || []
        })
      }, 30)
    })
  }

  // 版本对比
  static compareVersions = (atomId: string, version1: string, version2: string): Promise<{
    success: boolean
    data: {
      version1: AtomVersion | null
      version2: AtomVersion | null
      diff: string[]
    }
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const atom = mockAtomContexts.find(a => a.id === atomId)
        const v1 = atom?.versions.find(v => v.version === version1) || null
        const v2 = atom?.versions.find(v => v.version === version2) || null

        // 简单的文本差异模拟
        const diff = []
        if (v1 && v2) {
          if (v1.content !== v2.content) {
            diff.push(`内容从 "${v1.content.substring(0, 50)}..." 变更为 "${v2.content.substring(0, 50)}..."`)
          }
          if (v1.author !== v2.author) {
            diff.push(`作者从 "${v1.author}" 变更为 "${v2.author}"`)
          }
        }

        resolve({
          success: true,
          data: { version1: v1, version2: v2, diff }
        })
      }, 80)
    })
  }

  // 更新标签
  static updateTags = (atomId: string, tags: string[]): Promise<{
    success: boolean
    data: AtomContext
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const atom = mockAtomContexts.find(a => a.id === atomId)
        if (atom) {
          atom.tags = tags
          atom.updatedAt = dayjs().format('YYYY-MM-DD')
        }
        resolve({
          success: true,
          data: atom!
        })
      }, 60)
    })
  }

  // 获取可用标签
  static getAvailableTags = (): Promise<{
    success: boolean
    data: string[]
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          success: true,
          data: availableTags
        })
      }, 20)
    })
  }

  // 获取分类选项
  static getCategoryOptions = (): Promise<{
    success: boolean
    data: Array<{ value: string; label: string }>
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          success: true,
          data: categoryOptions
        })
      }, 20)
    })
  }

  // 获取变更日志
  static getChangeLogs = (atomId?: string): Promise<{
    success: boolean
    data: ChangeLog[]
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        let logs = [...mockChangeLogs]
        if (atomId) {
          logs = logs.filter(log => log.atomId === atomId)
        }
        logs.sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime())

        resolve({
          success: true,
          data: logs
        })
      }, 40)
    })
  }

  // 模拟使用原子
  static useAtom = (atomId: string): Promise<{
    success: boolean
    message: string
  }> => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const atom = mockAtomContexts.find(a => a.id === atomId)
        if (atom) {
          atom.usageCount += 1
        }
        resolve({
          success: true,
          message: `原子 "${atom?.name}" 已应用到项目中`
        })
      }, 100)
    })
  }
}

