'use client'
import { useState, useEffect } from 'react'
import { 
  Card, 
  Table, 
  Button, 
  Input, 
  Select, 
  Tag, 
  Space, 
  Modal, 
  Typography, 
  Tooltip,
  Badge,
  Timeline,
  message,
  Popconfirm,
  Row,
  Col
} from 'antd'
import { 
  SearchOutlined, 
  PlusOutlined, 
  EyeOutlined, 
  EditOutlined, 
  DeleteOutlined,
  HistoryOutlined,
  DiffOutlined,
  PlayCircleOutlined,
  TagOutlined,
  FilterOutlined,
  SortAscendingOutlined
} from '@ant-design/icons'
import { AtomContext, SearchFilters } from '@/types'
import { MockAPI } from '@/mock/api'
import PageInfo from '@/components/common/PageInfo'

const { Text, Paragraph } = Typography
const { TextArea } = Input

export default function AtomContextPage() {
  const [atoms, setAtoms] = useState<AtomContext[]>([])
  const [loading, setLoading] = useState(false)
  const [searchFilters, setSearchFilters] = useState<Partial<SearchFilters>>({
    keyword: '',
    category: 'all',
    status: 'all',
    sortBy: 'updatedAt',
    sortOrder: 'desc'
  })

  // Modal状态
  const [viewModalVisible, setViewModalVisible] = useState(false)
  const [editModalVisible, setEditModalVisible] = useState(false)
  const [versionModalVisible, setVersionModalVisible] = useState(false)
  const [compareModalVisible, setCompareModalVisible] = useState(false)
  const [selectedAtom, setSelectedAtom] = useState<AtomContext | null>(null)

  // 分类和标签选项
  const [categoryOptions, setCategoryOptions] = useState<Array<{ value: string; label: string }>>([])
  const [availableTags, setAvailableTags] = useState<string[]>([])

  // 加载数据
  const loadData = async () => {
    setLoading(true)
    try {
      const result = await MockAPI.getAtomContexts(searchFilters)
      if (result.success) {
        setAtoms(result.data)
      }
    } catch (error) {
      message.error('加载数据失败')
    } finally {
      setLoading(false)
    }
  }

  // 加载选项数据
  const loadOptions = async () => {
    try {
      const [categoriesResult, tagsResult] = await Promise.all([
        MockAPI.getCategoryOptions(),
        MockAPI.getAvailableTags()
      ])

      if (categoriesResult.success) {
        setCategoryOptions([
          { value: 'all', label: '全部分类' },
          ...categoriesResult.data
        ])
      }

      if (tagsResult.success) {
        setAvailableTags(tagsResult.data)
      }
    } catch (error) {
      message.error('加载选项失败')
    }
  }

  useEffect(() => {
    loadData()
  }, [searchFilters])

  useEffect(() => {
    loadOptions()
  }, [])

  // 搜索处理
  const handleSearch = (value: string) => {
    setSearchFilters(prev => ({ ...prev, keyword: value }))
  }

  // 筛选处理
  const handleFilterChange = (key: keyof SearchFilters, value: any) => {
    setSearchFilters(prev => ({ ...prev, [key]: value }))
  }

  // 查看原子详情
  const handleView = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setViewModalVisible(true)
  }

  // 编辑功能标签
  const handleEditTags = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setEditModalVisible(true)
  }

  // 保存标签
  const handleSaveTags = async (tags: string[]) => {
    if (!selectedAtom) return

    try {
      const result = await MockAPI.updateTags(selectedAtom.id, tags)
      if (result.success) {
        message.success('标签更新成功')
        setEditModalVisible(false)
        loadData()
      }
    } catch (error) {
      message.error('标签更新失败')
    }
  }

  // 版本历史
  const handleVersionHistory = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setVersionModalVisible(true)
  }

  // 版本对比
  const handleVersionCompare = (atom: AtomContext) => {
    setSelectedAtom(atom)
    setCompareModalVisible(true)
  }

  // 使用原子
  const handleUseAtom = async (atom: AtomContext) => {
    try {
      const result = await MockAPI.useAtom(atom.id)
      if (result.success) {
        message.success(result.message)
        loadData() // 刷新使用次数
      }
    } catch (error) {
      message.error('应用失败')
    }
  }

  // 删除原子
  const handleDelete = async (atomId: string) => {
    try {
      const result = await MockAPI.deleteAtomContext(atomId)
      if (result.success) {
        message.success('删除成功')
        loadData()
      }
    } catch (error) {
      message.error('删除失败')
    }
  }

  const columns = [
    {
      title: '原子名称',
      dataIndex: 'name',
      key: 'name',
      width: 200,
      render: (name: string, record: AtomContext) => (
        <Space direction="vertical" size={4}>
          <Text strong>{name}</Text>
          <Text type="secondary" style={{ fontSize: '12px' }}>
            {record.category === 'role' && '🎭 角色定义'}
            {record.category === 'principle' && '📏 设计原则'}
            {record.category === 'strategy' && '🎯 策略方法'}
            {record.category === 'code' && '💻 代码模板'}
            {record.category === 'template' && '📄 通用模板'}
          </Text>
        </Space>
      )
    },
    {
      title: '功能标签',
      dataIndex: 'tags',
      key: 'tags',
      width: 250,
      render: (tags: string[], record: AtomContext) => (
        <div 
          style={{ cursor: 'pointer' }} 
          onClick={() => handleEditTags(record)}
          title="点击编辑标签"
        >
          <Space wrap>
            {tags.slice(0, 3).map(tag => (
              <Tag key={tag} color="blue" style={{ margin: 2 }}>
                {tag}
              </Tag>
            ))}
            {tags.length > 3 && (
              <Tooltip title={tags.slice(3).join(', ')}>
                <Tag color="gray">+{tags.length - 3}</Tag>
              </Tooltip>
            )}
            <Tag 
              color="cyan" 
              style={{ cursor: 'pointer', border: '1px dashed' }}
              icon={<EditOutlined />}
            >
              编辑
            </Tag>
          </Space>
        </div>
      )
    },
    {
      title: '版本信息',
      key: 'version',
      width: 120,
      render: (_, record: AtomContext) => (
        <Space direction="vertical" size={4}>
          <Badge status="processing" text={record.version} />
          <Text type="secondary" style={{ fontSize: '12px' }}>
            使用 {record.usageCount} 次
          </Text>
        </Space>
      )
    },
    {
      title: '更新时间',
      dataIndex: 'updatedAt',
      key: 'updatedAt',
      width: 100,
      render: (date: string) => (
        <Text type="secondary" style={{ fontSize: '12px' }}>
          {date}
        </Text>
      )
    },
    {
      title: '操作',
      key: 'actions',
      width: 200,
      render: (_, record: AtomContext) => (
        <Space wrap>
          <Tooltip title="查看详情">
            <Button size="small" icon={<EyeOutlined />} onClick={() => handleView(record)} />
          </Tooltip>
          <Tooltip title="版本历史">
            <Button size="small" icon={<HistoryOutlined />} onClick={() => handleVersionHistory(record)} />
          </Tooltip>
          <Tooltip title="版本对比">
            <Button size="small" icon={<DiffOutlined />} onClick={() => handleVersionCompare(record)} />
          </Tooltip>
          <Tooltip title="使用原子">
            <Button 
              size="small" 
              type="primary" 
              icon={<PlayCircleOutlined />} 
              onClick={() => handleUseAtom(record)}
            />
          </Tooltip>
          <Popconfirm title="确认删除?" onConfirm={() => handleDelete(record.id)}>
            <Button size="small" danger icon={<DeleteOutlined />} />
          </Popconfirm>
        </Space>
      )
    }
  ]

  return (
    <div style={{ padding: 0 }}>
      <Card title="原子上下文库管理" extra={
        <Button type="primary" icon={<PlusOutlined />}>
          新建原子
        </Button>
      }>
        {/* 搜索和筛选区域 */}
        <Row gutter={16} style={{ marginBottom: 16 }}>
          <Col span={8}>
            <Input.Search
              placeholder="搜索原子名称、描述、内容..."
              allowClear
              onSearch={handleSearch}
              style={{ width: '100%' }}
            />
          </Col>
          <Col span={4}>
            <Select
              style={{ width: '100%' }}
              placeholder="分类筛选"
              value={searchFilters.category}
              onChange={(value) => handleFilterChange('category', value)}
              options={categoryOptions}
            />
          </Col>
          <Col span={4}>
            <Select
              style={{ width: '100%' }}
              placeholder="状态筛选"
              value={searchFilters.status}
              onChange={(value) => handleFilterChange('status', value)}
              options={[
                { value: 'all', label: '全部状态' },
                { value: 'active', label: '活跃' },
                { value: 'inactive', label: '不活跃' },
                { value: 'deprecated', label: '已废弃' }
              ]}
            />
          </Col>
          <Col span={4}>
            <Select
              style={{ width: '100%' }}
              placeholder="排序方式"
              value={searchFilters.sortBy}
              onChange={(value) => handleFilterChange('sortBy', value)}
              options={[
                { value: 'name', label: '按名称' },
                { value: 'createdAt', label: '按创建时间' },
                { value: 'updatedAt', label: '按更新时间' },
                { value: 'usageCount', label: '按使用次数' }
              ]}
            />
          </Col>
          <Col span={4}>
            <Select
              style={{ width: '100%' }}
              value={searchFilters.sortOrder}
              onChange={(value) => handleFilterChange('sortOrder', value)}
              options={[
                { value: 'asc', label: '升序' },
                { value: 'desc', label: '降序' }
              ]}
            />
          </Col>
        </Row>

        {/* 统计信息 */}
        <Row gutter={16} style={{ marginBottom: 16 }}>
          <Col span={6}>
            <Card size="small">
              <Text type="secondary">总原子数</Text>
              <br />
              <Text strong style={{ fontSize: '18px' }}>{atoms.length}</Text>
            </Card>
          </Col>
          <Col span={6}>
            <Card size="small">
              <Text type="secondary">活跃原子</Text>
              <br />
              <Text strong style={{ fontSize: '18px', color: '#52c41a' }}>
                {atoms.filter(a => a.status === 'active').length}
              </Text>
            </Card>
          </Col>
          <Col span={6}>
            <Card size="small">
              <Text type="secondary">总使用次数</Text>
              <br />
              <Text strong style={{ fontSize: '18px', color: '#1890ff' }}>
                {atoms.reduce((sum, a) => sum + a.usageCount, 0)}
              </Text>
            </Card>
          </Col>
          <Col span={6}>
            <Card size="small">
              <Text type="secondary">平均版本</Text>
              <br />
              <Text strong style={{ fontSize: '18px', color: '#722ed1' }}>
                v{(atoms.reduce((sum, a) => sum + parseFloat(a.version.replace('v', '')), 0) / atoms.length || 0).toFixed(1)}
              </Text>
            </Card>
          </Col>
        </Row>

        {/* 表格 */}
        <Table
          columns={columns}
          dataSource={atoms}
          rowKey="id"
          loading={loading}
          pagination={{
            total: atoms.length,
            pageSize: 10,
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total) => `共 ${total} 条记录`
          }}
        />
      </Card>

      {/* 查看详情Modal */}
      <Modal
        title={`${selectedAtom?.name} - 详情`}
        open={viewModalVisible}
        onCancel={() => setViewModalVisible(false)}
        footer={null}
        width={800}
      >
        {selectedAtom && (
          <Space direction="vertical" style={{ width: '100%' }}>
            <div>
              <Text strong>描述：</Text>
              <Paragraph>{selectedAtom.description}</Paragraph>
            </div>
            <div>
              <Text strong>内容：</Text>
              <Paragraph 
                code 
                style={{ 
                  background: '#f6f8fa', 
                  padding: 16, 
                  borderRadius: 6,
                  maxHeight: 300,
                  overflow: 'auto'
                }}
              >
                {selectedAtom.content}
              </Paragraph>
            </div>
            <Row gutter={16}>
              <Col span={12}>
                <Text strong>版本：</Text> {selectedAtom.version}
              </Col>
              <Col span={12}>
                <Text strong>作者：</Text> {selectedAtom.author}
              </Col>
            </Row>
            <Row gutter={16}>
              <Col span={12}>
                <Text strong>创建时间：</Text> {selectedAtom.createdAt}
              </Col>
              <Col span={12}>
                <Text strong>使用次数：</Text> {selectedAtom.usageCount}
              </Col>
            </Row>
          </Space>
        )}
      </Modal>

      {/* 编辑标签Modal */}
      <TagEditModal
        visible={editModalVisible}
        atom={selectedAtom}
        availableTags={availableTags}
        onSave={handleSaveTags}
        onCancel={() => setEditModalVisible(false)}
      />

      {/* 版本历史Modal */}
      <VersionHistoryModal
        visible={versionModalVisible}
        atom={selectedAtom}
        onCancel={() => setVersionModalVisible(false)}
      />

      {/* 版本对比Modal */}
      <VersionCompareModal
        visible={compareModalVisible}
        atom={selectedAtom}
        onCancel={() => setCompareModalVisible(false)}
      />
    </div>
  )
}

// 标签编辑Modal组件
function TagEditModal({ 
  visible, 
  atom, 
  availableTags, 
  onSave, 
  onCancel 
}: {
  visible: boolean
  atom: AtomContext | null
  availableTags: string[]
  onSave: (tags: string[]) => void
  onCancel: () => void
}) {
  const [selectedTags, setSelectedTags] = useState<string[]>([])

  useEffect(() => {
    if (atom) {
      setSelectedTags(atom.tags)
    }
  }, [atom])

  return (
    <Modal
      title="编辑功能标签"
      open={visible}
      onOk={() => onSave(selectedTags)}
      onCancel={onCancel}
      width={600}
    >
      <Space direction="vertical" style={{ width: '100%' }}>
        <div>
          <Text strong>原子名称：</Text> {atom?.name}
        </div>
        <div>
          <Text strong>当前标签：</Text>
          <div style={{ marginTop: 8 }}>
            <Select
              mode="multiple"
              style={{ width: '100%' }}
              placeholder="选择或输入标签"
              value={selectedTags}
              onChange={setSelectedTags}
              options={availableTags.map(tag => ({ value: tag, label: tag }))}
              maxTagCount={10}
            />
          </div>
        </div>
        <div>
          <Text type="secondary">
            💡 提示：可以选择现有标签，也可以直接输入新标签
          </Text>
        </div>
      </Space>
    </Modal>
  )
}

// 版本历史Modal组件
function VersionHistoryModal({ 
  visible, 
  atom, 
  onCancel 
}: {
  visible: boolean
  atom: AtomContext | null
  onCancel: () => void
}) {
  return (
    <Modal
      title={`${atom?.name} - 版本历史`}
      open={visible}
      onCancel={onCancel}
      footer={null}
      width={700}
    >
      {atom && (
        <Timeline>
          {atom.versions.map((version, index) => (
            <Timeline.Item
              key={version.id}
              color={version.isActive ? 'green' : 'blue'}
              dot={version.isActive ? <Badge status="processing" /> : undefined}
            >
              <div>
                <Space>
                  <Text strong>{version.version}</Text>
                  {version.isActive && <Tag color="green">当前版本</Tag>}
                </Space>
                <br />
                <Text type="secondary" style={{ fontSize: '12px' }}>
                  {version.createdAt} by {version.author}
                </Text>
                <br />
                <Paragraph style={{ marginTop: 8 }}>
                  {version.changelog}
                </Paragraph>
              </div>
            </Timeline.Item>
          ))}
        </Timeline>
      )}
    </Modal>
  )
}

// 版本对比Modal组件
function VersionCompareModal({ 
  visible, 
  atom, 
  onCancel 
}: {
  visible: boolean
  atom: AtomContext | null
  onCancel: () => void
}) {
  const [version1, setVersion1] = useState('')
  const [version2, setVersion2] = useState('')
  const [compareResult, setCompareResult] = useState<any>(null)
  const [comparing, setComparing] = useState(false)

  const handleCompare = async () => {
    if (!atom || !version1 || !version2) return

    setComparing(true)
    try {
      const result = await MockAPI.compareVersions(atom.id, version1, version2)
      if (result.success) {
        setCompareResult(result.data)
      }
    } catch (error) {
      message.error('对比失败')
    } finally {
      setComparing(false)
    }
  }

  const versionOptions = atom?.versions.map(v => ({
    value: v.version,
    label: `${v.version} (${v.createdAt})`
  })) || []

  return (
    <Modal
      title={`${atom?.name} - 版本对比`}
      open={visible}
      onCancel={onCancel}
      footer={null}
      width={900}
    >
      <Space direction="vertical" style={{ width: '100%' }}>
        <Row gutter={16}>
          <Col span={10}>
            <Text strong>版本1：</Text>
            <Select
              style={{ width: '100%', marginTop: 4 }}
              placeholder="选择版本1"
              value={version1}
              onChange={setVersion1}
              options={versionOptions}
            />
          </Col>
          <Col span={4} style={{ textAlign: 'center', paddingTop: 30 }}>
            <Button 
              type="primary" 
              icon={<DiffOutlined />}
              loading={comparing}
              disabled={!version1 || !version2}
              onClick={handleCompare}
            >
              对比
            </Button>
          </Col>
          <Col span={10}>
            <Text strong>版本2：</Text>
            <Select
              style={{ width: '100%', marginTop: 4 }}
              placeholder="选择版本2"
              value={version2}
              onChange={setVersion2}
              options={versionOptions}
            />
          </Col>
        </Row>

        {compareResult && (
          <div style={{ marginTop: 16 }}>
            <Text strong>差异对比：</Text>
            <div style={{ marginTop: 8 }}>
              {compareResult.diff.length > 0 ? (
                compareResult.diff.map((diff: string, index: number) => (
                  <div key={index} style={{ 
                    padding: 8, 
                    background: '#fff1f0', 
                    border: '1px solid #ffccc7', 
                    borderRadius: 4,
                    marginBottom: 8
                  }}>
                    {diff}
                  </div>
                ))
              ) : (
                <div style={{ 
                  padding: 16, 
                  background: '#f6ffed', 
                  border: '1px solid #b7eb8f', 
                  borderRadius: 4,
                  textAlign: 'center'
                }}>
                  ✅ 两个版本内容完全相同
                </div>
              )}
            </div>
          </div>
        )}
      </Space>
    </Modal>
  )
}

