'use client'
import { ConfigProvider, theme } from 'antd'
import zhCN from 'antd/locale/zh_CN'
import { useTheme } from '@/contexts/ThemeContext'

export const ClientThemeWrapper: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const { theme: currentTheme } = useTheme()

  const themeConfig = currentTheme === 'dark' ? {
    algorithm: theme.darkAlgorithm,
    token: {
      // 背景色
      colorBgContainer: '#000000',      // 纯黑底
      colorBgElevated: '#111111',       // 弹出层背景
      colorBgLayout: '#000000',         // 纯黑底
      colorBgBase: '#141414',           // 基础背景色

      // 文本色
      colorText: '#ffffff',             // 主文本色
      colorTextSecondary: '#a6a6a6',    // 次要文本色
      colorTextPlaceholder: '#595959',  // placeholder文本色
      colorTextDisabled: '#424242',     // 禁用文本色

      // 边框色
      colorBorder: '#424242',           // 主边框色
      colorBorderSecondary: '#303030',  // 次要边框色

      // 填充色
      colorFillAlter: '#1f1f1f',        // 填充色
      colorFillContent: '#262626',      // 内容填充色
      colorFillContentHover: '#303030', // 悬停填充色

      // 主色调
      colorPrimary: '#1890ff',          // 主色
      colorSuccess: '#52c41a',          // 成功色
      colorWarning: '#faad14',          // 警告色
      colorError: '#ff4d4f',            // 错误色
    }
  } : {}

  return (
    <ConfigProvider locale={zhCN} theme={themeConfig}>
      {children}
    </ConfigProvider>
  )
}

