      // 控件背景色
      controlItemBgHover: '#1f1f1f',    // 控件项悬停背景
      controlItemBgActive: '#262626',   // 控件项激活背景
      controlItemBgActiveHover: '#303030', // 控件项激活悬停背景

      // 主色调保持
      colorPrimary: '#1890ff',          // 主色
      colorSuccess: '#52c41a',          // 成功色  
      colorWarning: '#faad14',          // 警告色
      colorError: '#ff4d4f',            // 错误色
      colorInfo: '#1890ff',             // 信息色
    },
    components: {
      // Layout组件专门配置
      Layout: {
        headerBg: '#000000',            // Header背景纯黑
