      Table: {
        colorBgContainer: '#000000',    // 表格背景纯黑
        colorText: '#ffffff',           // 表格文字白色
        colorTextHeading: '#ffffff',    // 表头文字白色
        colorBorderSecondary: '#424242', // 表格边框
        rowHoverBg: '#1f1f1f',          // 行悬停背景
