  const router = useRouter()
  const pathname = usePathname()
  const { theme, toggleTheme } = useTheme()

  const menuItems = [
    {
      key: '/',
      icon: <HomeOutlined />,
      label: 'é¦–é¡µ',
    },
    {
      key: '/atom-context',
      icon: <DatabaseOutlined />,
      label: 'åŸå­ä¸Šä¸‹æ–‡åº“',
