            åŸå­ä¸Šä¸‹æ–‡åº“ç®¡ç†ç³»ç»Ÿ
          </Typography.Title>
          <Menu
            theme={theme}
            mode="horizontal"
            selectedKeys={[pathname]}
            items={menuItems}
            onClick={handleMenuClick}
            style={{ minWidth: 200, backgroundColor: 'transparent' }}
          />
        </Space>
        <Button
          type="text"
          icon={theme === 'dark' ? <SunOutlined /> : <MoonOutlined />}
          onClick={toggleTheme}
          title={theme === 'dark' ? 'åˆ‡æ¢åˆ°äº®è‰²æ¨¡å¼' : 'åˆ‡æ¢åˆ°æš—è‰²æ¨¡å¼'}
        />
      </Layout.Header>
      <Layout.Content style={{ padding: 24 }}>
        {children}
      </Layout.Content>
    </Layout>
  )
}

