              dot={version.isActive ? <Badge status="processing" /> : undefined}
            >
              <div>
                <Space>
                  <Text strong>{version.version}</Text>
                  {version.isActive && <Tag color="green">å½“å‰ç‰ˆæœ¬</Tag>}
                </Space>
                <br />
                <Text type="secondary" style={{ fontSize: '12px' }}>
                  {version.createdAt} by {version.author}
                </Text>
                <br />
                <Paragraph style={{ marginTop: 8 }}>
                  {version.changelog}
                </Paragraph>
              </div>
            </Timeline.Item>
          ))}
        </Timeline>
      )}
    </Modal>
  )
}

// ç‰ˆæœ¬å¯¹æ¯”Modalç»„ä»¶
function VersionCompareModal({ 
  visible, 
  atom, 
  onCancel 
}: {
  visible: boolean
  atom: AtomContext | null
  onCancel: () => void
}) {
  const [version1, setVersion1] = useState('')
  const [version2, setVersion2] = useState('')
  const [compareResult, setCompareResult] = useState<any>(null)
  const [comparing, setComparing] = useState(false)

  const handleCompare = async () => {
    if (!atom || !version1 || !version2) return

    setComparing(true)
    try {
      const result = await MockAPI.compareVersions(atom.id, version1, version2)
      if (result.success) {
        setCompareResult(result.data)
      }
    } catch (error) {
      message.error('å¯¹æ¯”å¤±è´¥')
    } finally {
      setComparing(false)
    }
  }

  const versionOptions = atom?.versions.map(v => ({
    value: v.version,
    label: `${v.version} (${v.createdAt})`
  })) || []

  return (
    <Modal
      title={`${atom?.name} - ç‰ˆæœ¬å¯¹æ¯”`}
      open={visible}
      onCancel={onCancel}
      footer={null}
      width={900}
    >
      <Space direction="vertical" style={{ width: '100%' }}>
        <Row gutter={16}>
          <Col span={10}>
            <Text strong>ç‰ˆæœ¬1ï¼š</Text>
            <Select
              style={{ width: '100%', marginTop: 4 }}
              placeholder="é€‰æ‹©ç‰ˆæœ¬1"
              value={version1}
              onChange={setVersion1}
              options={versionOptions}
            />
          </Col>
          <Col span={4} style={{ textAlign: 'center', paddingTop: 30 }}>
            <Button 
              type="primary" 
              icon={<DiffOutlined />}
              loading={comparing}
              disabled={!version1 || !version2}
              onClick={handleCompare}
            >
              å¯¹æ¯”
            </Button>
          </Col>
          <Col span={10}>
            <Text strong>ç‰ˆæœ¬2ï¼š</Text>
            <Select
              style={{ width: '100%', marginTop: 4 }}
              placeholder="é€‰æ‹©ç‰ˆæœ¬2"
              value={version2}
              onChange={setVersion2}
              options={versionOptions}
            />
          </Col>
        </Row>

        {compareResult && (
          <div style={{ marginTop: 16 }}>
            <Text strong>å·®å¼‚å¯¹æ¯”ï¼š</Text>
            <div style={{ marginTop: 8 }}>
              {compareResult.diff.length > 0 ? (
                compareResult.diff.map((diff: string, index: number) => (
                  <div key={index} style={{ 
                    padding: 8, 
                    background: '#fff1f0', 
                    border: '1px solid #ffccc7', 
                    borderRadius: 4,
                    marginBottom: 8
                  }}>
                    {diff}
                  </div>
                ))
              ) : (
                <div style={{ 
                  padding: 16, 
                  background: '#f6ffed', 
                  border: '1px solid #b7eb8f', 
                  borderRadius: 4,
                  textAlign: 'center'
                }}>
                  âœ… ä¸¤ä¸ªç‰ˆæœ¬å†…å®¹å®Œå…¨ç›¸åŒ
                </div>
              )}
            </div>
          </div>
        )}
      </Space>
    </Modal>
  )
}

