        <div>
          <Text strong>åŸå­åç§°ï¼š</Text> {atom?.name}
        </div>
        <div>
          <Text strong>å½“å‰æ ‡ç­¾ï¼š</Text>
          <div style={{ marginTop: 8 }}>
            <Select
              mode="multiple"
              style={{ width: '100%' }}
              placeholder="é€‰æ‹©æˆ–è¾“å…¥æ ‡ç­¾"
              value={selectedTags}
              onChange={setSelectedTags}
