      colorTextPlaceholder: '#595959',  // placeholderæ–‡æœ¬è‰²
      colorTextDisabled: '#424242',     // ç¦ç”¨æ–‡æœ¬è‰²

      // è¾¹æ¡†è‰²
      colorBorder: '#424242',           // ä¸»è¾¹æ¡†è‰²
      colorBorderSecondary: '#303030',  // æ¬¡è¦è¾¹æ¡†è‰²

