'use client'

import { Button } from '@/components/ui/button'
import { Card } from '@/components/ui/card'
import { useTheme } from '@/contexts/ThemeContext'
import { Sun, Moon, Atom, Search, Filter, Plus } from 'lucide-react'
import { Input } from '@/components/ui/input'

interface AppLayoutProps {
  children: React.ReactNode
}

export default function AppLayout({ children }: AppLayoutProps) {
  const { theme, toggleTheme } = useTheme()

  return (
    <div className="min-h-screen bg-gray-900 dark:bg-gray-900 bg-gray-50 transition-colors">
      {/* Header */}
      <header className="border-b border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-800">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo */}
            <div className="flex items-center space-x-2">
              <div className="p-2 bg-blue-600 rounded-lg">
                <Atom className="h-6 w-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900 dark:text-white">
                  Syllo Prototype
                </h1>
                <p className="text-sm text-gray-500 dark:text-gray-400">
                  åŸå­ä¸Šä¸‹æ–‡åº“ç®¡ç†ç³»ç»Ÿ
                </p>
              </div>
            </div>

            {/* Theme Toggle */}
            <Button onClick={toggleTheme} variant="ghost" size="icon">
              {theme === 'dark' ? (
                <Sun className="h-4 w-4" />
              ) : (
                <Moon className="h-4 w-4" />
              )}
            </Button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {children}
      </main>

      {/* Toast Container */}
      <div id="toast-container" />
    </div>
  )
}

