}

export function useFormValidation(fields: Record<string, FieldConfig>) {
  const errors = ref<Record<string, string>>({})
  const isValidating = ref(false)
  const hasError = computed(() => Object.keys(errors.value).length > 0)

  const validateField = async (fieldName: string) => {
    const field = fields[fieldName]
    if (!field) return true

    // æ¸…é™¤ä¹‹å‰çš„é”™è¯¯
    delete errors.value[fieldName]

    // æ£€æŸ¥å¿…å¡«
    if (field.required && (!field.value || field.value === '')) {
      errors.value[fieldName] = 'æ­¤å­—æ®µä¸ºå¿…å¡«é¡¹'
      return false
    }

    // æ‰§è¡ŒéªŒè¯è§„åˆ™
    for (const rule of field.rules) {
      try {
        const isValid = await rule.validator(field.value)
        if (!isValid) {
          errors.value[fieldName] = rule.message
          return false
        }
      } catch (error) {
        errors.value[fieldName] = 'éªŒè¯è¿‡ç¨‹ä¸­å‘ç”Ÿé”™è¯¯'
        return false
      }
    }

    return true
  }

  const validateAll = async () => {
    isValidating.value = true
    errors.value = {}

    const results = await Promise.all(
      Object.keys(fields).map(fieldName => validateField(fieldName))
    )

    isValidating.value = false
    return results.every(result => result)
  }

  const clearErrors = () => {
    errors.value = {}
  }

  // ç›‘å¬å­—æ®µå˜åŒ–ï¼Œå®æ—¶éªŒè¯
  Object.keys(fields).forEach(fieldName => {
    watch(
      () => fields[fieldName].value,
      () => validateField(fieldName),
      { debounce: 300 }
    )
  })

  return {
    errors: computed(() => errors.value),
    hasError,
    isValidating: computed(() => isValidating.value),
    validateField,
    validateAll,
    clearErrors
  }
}`,
