import type { Atom, MockAPIResponse, SearchFilters } from '@/types'
import { enrichedMockAtoms, categories, statusOptions } from './data'

// Mockå»¶è¿Ÿå‡½æ•°ï¼Œæ¨¡æ‹Ÿç½‘ç»œè¯·æ±‚
const delay = (ms: number = 300) => new Promise(resolve => setTimeout(resolve, ms))

export class MockAPI {
  static atoms = [...enrichedMockAtoms]

  // è·å–æ‰€æœ‰åŸå­
  static async getAtoms(filters?: SearchFilters): Promise<MockAPIResponse<Atom[]>> {
    await delay(200)

    let filteredAtoms = [...this.atoms]

    // åº”ç”¨ç­›é€‰æ¡ä»¶
    if (filters) {
      if (filters.category) {
        filteredAtoms = filteredAtoms.filter(atom => atom.category === filters.category)
      }

      if (filters.status) {
        filteredAtoms = filteredAtoms.filter(atom => atom.status === filters.status)
      }

      if (filters.searchQuery) {
        const query = filters.searchQuery.toLowerCase()
        filteredAtoms = filteredAtoms.filter(atom => 
          atom.name.toLowerCase().includes(query) ||
          atom.description.toLowerCase().includes(query) ||
          atom.tags.some(tag => tag.toLowerCase().includes(query))
        )
      }

      if (filters.tags && filters.tags.length > 0) {
        filteredAtoms = filteredAtoms.filter(atom => 
          filters.tags!.some(tag => atom.tags.includes(tag))
        )
      }

      if (filters.author) {
        filteredAtoms = filteredAtoms.filter(atom => 
          atom.author.toLowerCase().includes(filters.author!.toLowerCase())
        )
      }

      // æ’åº
      if (filters.sortBy) {
        filteredAtoms.sort((a, b) => {
          let aValue: any, bValue: any

          switch (filters.sortBy) {
            case 'name':
              aValue = a.name.toLowerCase()
              bValue = b.name.toLowerCase()
              break
            case 'updatedAt':
              aValue = a.updatedAt.getTime()
              bValue = b.updatedAt.getTime()
              break
            case 'usageCount':
              aValue = a.usageCount
              bValue = b.usageCount
              break
            case 'createdAt':
              aValue = a.createdAt.getTime()
              bValue = b.createdAt.getTime()
              break
            default:
              return 0
          }

          if (filters.sortOrder === 'desc') {
