          atom.description.toLowerCase().includes(query) ||
          atom.tags.some(tag => tag.toLowerCase().includes(query))
        )
      }

      if (filters.tags && filters.tags.length > 0) {
        filteredAtoms = filteredAtoms.filter(atom => 
          filters.tags!.some(tag => atom.tags.includes(tag))
        )
      }

      if (filters.author) {
        filteredAtoms = filteredAtoms.filter(atom => 
          atom.author.toLowerCase().includes(filters.author!.toLowerCase())
        )
      }

      // æ’åº
      if (filters.sortBy) {
        filteredAtoms.sort((a, b) => {
          let aValue: any, bValue: any

          switch (filters.sortBy) {
            case 'name':
              aValue = a.name.toLowerCase()
              bValue = b.name.toLowerCase()
              break
            case 'updatedAt':
              aValue = a.updatedAt.getTime()
              bValue = b.updatedAt.getTime()
              break
            case 'usageCount':
              aValue = a.usageCount
              bValue = b.usageCount
              break
            case 'createdAt':
              aValue = a.createdAt.getTime()
              bValue = b.createdAt.getTime()
              break
            default:
              return 0
          }

          if (filters.sortOrder === 'desc') {
            return aValue < bValue ? 1 : -1
          }
          return aValue > bValue ? 1 : -1
        })
      }
    }

    return {
      success: true,
      data: filteredAtoms,
      total: filteredAtoms.length
    }
  }

  // è·å–å•ä¸ªåŸå­è¯¦æƒ…
  static async getAtomById(id: string): Promise<MockAPIResponse<Atom | null>> {
    await delay(150)

    const atom = this.atoms.find(a => a.id === id)

    return {
      success: true,
      data: atom || null,
      message: atom ? undefined : 'åŸå­ä¸å­˜åœ¨'
    }
  }

  // ä½¿ç”¨åŸå­ï¼ˆå¢åŠ ä½¿ç”¨è®¡æ•°ï¼‰
  static async useAtom(id: string, project: string = 'æœªçŸ¥é¡¹ç›®'): Promise<MockAPIResponse<boolean>> {
    await delay(100)

    const atom = this.atoms.find(a => a.id === id)
    if (atom) {
      atom.usageCount++
      atom.usageStats.totalUsage++
      atom.usageStats.weeklyUsage++
      atom.usageStats.monthlyUsage++

      // æ›´æ–°æœ€è¿‘ä½¿ç”¨è®°å½•
      atom.usageStats.recentUsage.unshift({
        date: new Date(),
        project,
        user: 'å½“å‰ç”¨æˆ·'
      })

      // ä¿æŒæœ€è¿‘ä½¿ç”¨è®°å½•ä¸è¶…è¿‡10æ¡
      if (atom.usageStats.recentUsage.length > 10) {
        atom.usageStats.recentUsage = atom.usageStats.recentUsage.slice(0, 10)
      }

      return {
        success: true,
        data: true,
        message: 'ä½¿ç”¨æˆåŠŸï¼å·²å¤åˆ¶åˆ°å‰ªè´´æ¿'
      }
    }

    return {
      success: false,
      data: false,
      message: 'åŸå­ä¸å­˜åœ¨'
    }
  }

  // æ›´æ–°åŸå­æ ‡ç­¾
  static async updateAtomTags(id: string, tags: string[]): Promise<MockAPIResponse<Atom | null>> {
    await delay(250)

    const atom = this.atoms.find(a => a.id === id)
    if (atom) {
      const oldTags = [...atom.tags]
      atom.tags = tags
      atom.updatedAt = new Date()

      // æ·»åŠ å˜æ›´æ—¥å¿—
      atom.changelog.unshift({
        id: `${id}-change-${Date.now()}`,
        atomId: atom.id,
        version: atom.version,
        type: 'tag',
        description: 'æ›´æ–°æ ‡ç­¾',
        author: 'å½“å‰ç”¨æˆ·',
        timestamp: new Date(),
        changes: [{
          field: 'tags',
          oldValue: oldTags,
          newValue: tags
        }]
      })

      return {
        success: true,
        data: atom,
        message: 'æ ‡ç­¾æ›´æ–°æˆåŠŸï¼'
      }
    }

    return {
      success: false,
      data: null,
      message: 'åŸå­ä¸å­˜åœ¨'
    }
  }

  // åˆ›å»ºæ–°åŸå­
  static async createAtom(atomData: Partial<Atom>): Promise<MockAPIResponse<Atom>> {
    await delay(300)

    const newAtom: Atom = {
      id: `atom-${Date.now()}`,
      name: atomData.name || 'æœªå‘½ååŸå­',
      description: atomData.description || '',
      content: atomData.content || '',
      category: atomData.category || 'other',
      tags: atomData.tags || [],
      status: 'draft',
      author: 'å½“å‰ç”¨æˆ·',
      createdAt: new Date(),
      updatedAt: new Date(),
      version: 1,
      usageCount: 0,
      versions: [],
      changelog: [],
      usageStats: {
        totalUsage: 0,
        weeklyUsage: 0,
        monthlyUsage: 0,
        topProjects: [],
        recentUsage: [],
        trendData: []
      }
    }

    this.atoms.unshift(newAtom)

    return {
      success: true,
      data: newAtom,
      message: 'åŸå­åˆ›å»ºæˆåŠŸï¼'
    }
  }

  // åˆ é™¤åŸå­
  static async deleteAtom(id: string): Promise<MockAPIResponse<boolean>> {
    await delay(200)

    const index = this.atoms.findIndex(a => a.id === id)
    if (index > -1) {
      this.atoms.splice(index, 1)
      return {
        success: true,
        data: true,
        message: 'åŸå­åˆ é™¤æˆåŠŸï¼'
      }
    }

    return {
      success: false,
      data: false,
      message: 'åŸå­ä¸å­˜åœ¨'
    }
  }

  // è·å–åˆ†ç±»åˆ—è¡¨
  static async getCategories(): Promise<MockAPIResponse<typeof categories>> {
    await delay(100)

    // é‡æ–°è®¡ç®—åˆ†ç±»æ•°é‡
    const categoryCounts = this.atoms.reduce((acc, atom) => {
      acc[atom.category] = (acc[atom.category] || 0) + 1
      return acc
    }, {} as Record<string, number>)

    const updatedCategories = categories.map(cat => ({
      ...cat,
      count: categoryCounts[cat.value] || 0
    }))

    return {
      success: true,
      data: updatedCategories
    }
  }

  // è·å–çŠ¶æ€ç»Ÿè®¡
  static async getStatusStats(): Promise<MockAPIResponse<typeof statusOptions>> {
    await delay(100)

    const statusCounts = this.atoms.reduce((acc, atom) => {
      acc[atom.status] = (acc[atom.status] || 0) + 1
      return acc
    }, {} as Record<string, number>)

    const updatedStatusOptions = statusOptions.map(status => ({
      ...status,
      count: statusCounts[status.value] || 0
    }))

    return {
      success: true,
      data: updatedStatusOptions
    }
  }

  // è·å–çƒ­é—¨æ ‡ç­¾
  static async getPopularTags(): Promise<MockAPIResponse<string[]>> {
    await delay(100)

    const tagCounts = this.atoms.reduce((acc, atom) => {
      atom.tags.forEach(tag => {
        acc[tag] = (acc[tag] || 0) + 1
      })
      return acc
    }, {} as Record<string, number>)

    const sortedTags = Object.entries(tagCounts)
      .sort(([, a], [, b]) => b - a)
      .slice(0, 20)
      .map(([tag]) => tag)

    return {
      success: true,
      data: sortedTags
    }
  }
}

