.dark ::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

.dark ::-webkit-scrollbar-track {
  background: rgb(75 85 99 / 0.3);
  border-radius: 4px;
}

.dark ::-webkit-scrollbar-thumb {
  background: rgb(107 114 128 / 0.5);
  border-radius: 4px;
}

.dark ::-webkit-scrollbar-thumb:hover {
  background: rgb(107 114 128 / 0.7);
}

/* äº®è‰²æ¨¡å¼æ»šåŠ¨æ¡æ ·å¼ */
.light ::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

.light ::-webkit-scrollbar-track {
  background: rgb(241 245 249 / 0.3);
  border-radius: 4px;
}

.light ::-webkit-scrollbar-thumb {
  background: rgb(100 116 139 / 0.5);
  border-radius: 4px;
}

.light ::-webkit-scrollbar-thumb:hover {
  background: rgb(100 116 139 / 0.7);
}

/* æš—é»‘æ¨¡å¼æ¯›ç»ç’ƒæ•ˆæœå¼¹çª— */
.dark .dialog-overlay {
  background-color: rgba(0, 0, 0, 0.7);
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
}

.dark .dialog-content {
  background: rgb(55 65 81 / 0.95);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
  border: 1px solid rgb(107 114 128 / 0.5);
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.5);
}

/* äº®è‰²æ¨¡å¼å¼¹çª—æ ·å¼ */
.light .dialog-overlay {
  background-color: rgba(0, 0, 0, 0.5);
  backdrop-filter: blur(4px);
  -webkit-backdrop-filter: blur(4px);
}

.light .dialog-content {
  background: rgb(255 255 255 / 0.95);
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
  border: 1px solid rgb(226 232 240 / 0.8);
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1);
}

