'use client'

import { useState, useEffect } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { toast } from 'sonner'
import { 
  Search, 
  Filter, 
  Plus, 
  Eye, 
  Copy, 
  Tag, 
  Clock, 
  User,
  TrendingUp,
  MoreVertical
} from 'lucide-react'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'
import type { Atom, SearchFilters } from '@/types'
import { MockAPI } from '@/mock/api'
import AtomDetailDialog from '@/components/atoms/AtomDetailDialog'
import CreateAtomDialog from '@/components/atoms/CreateAtomDialog'

export default function HomePage() {
  const [atoms, setAtoms] = useState<Atom[]>([])
  const [loading, setLoading] = useState(true)
  const [searchQuery, setSearchQuery] = useState('')
  const [selectedCategory, setSelectedCategory] = useState<string>('')
  const [selectedStatus, setSelectedStatus] = useState<string>('')
  const [sortBy, setSortBy] = useState<string>('updatedAt')
  const [selectedAtom, setSelectedAtom] = useState<Atom | null>(null)
  const [showDetailDialog, setShowDetailDialog] = useState(false)
  const [showCreateDialog, setShowCreateDialog] = useState(false)

  // åŠ è½½åŸå­æ•°æ®
  const loadAtoms = async () => {
    setLoading(true)
    try {
      const filters: SearchFilters = {
        searchQuery: searchQuery || undefined,
        category: selectedCategory || undefined,
        status: selectedStatus || undefined,
        sortBy: sortBy as any,
        sortOrder: 'desc'
      }

      const result = await MockAPI.getAtoms(filters)
      if (result.success) {
        setAtoms(result.data)
      }
    } catch (error) {
      toast.error('åŠ è½½æ•°æ®å¤±è´¥')
    } finally {
      setLoading(false)
    }
  }

  // åˆå§‹åŒ–åŠ è½½å’Œæœç´¢å˜åŒ–æ—¶é‡æ–°åŠ è½½
  useEffect(() => {
    loadAtoms()
  }, [searchQuery, selectedCategory, selectedStatus, sortBy])

  // ä½¿ç”¨åŸå­
  const handleUseAtom = async (atom: Atom, event: React.MouseEvent) => {
    event.stopPropagation()
    try {
      await navigator.clipboard.writeText(atom.content)
      const result = await MockAPI.useAtom(atom.id)
      if (result.success) {
        toast.success('å·²å¤åˆ¶åˆ°å‰ªè´´æ¿ï¼')
        // æ›´æ–°æœ¬åœ°ä½¿ç”¨æ¬¡æ•°
        setAtoms(prev => prev.map(a => 
          a.id === atom.id ? { ...a, usageCount: a.usageCount + 1 } : a
        ))
      }
    } catch (error) {
      toast.error('å¤åˆ¶å¤±è´¥')
    }
  }

  // æŸ¥çœ‹åŸå­è¯¦æƒ…
  const handleViewAtom = (atom: Atom) => {
    setSelectedAtom(atom)
    setShowDetailDialog(true)
  }

  // åˆ›å»ºæ–°åŸå­
  const handleCreateAtom = () => {
    setShowCreateDialog(true)
  }

  // åˆ é™¤åŸå­
  const handleDeleteAtom = async (atom: Atom, event: React.MouseEvent) => {
    event.stopPropagation()
    if (confirm(`ç¡®å®šè¦åˆ é™¤åŸå­ "${atom.name}" å—ï¼Ÿ`)) {
      try {
        const result = await MockAPI.deleteAtom(atom.id)
        if (result.success) {
          toast.success('åˆ é™¤æˆåŠŸï¼')
          setAtoms(prev => prev.filter(a => a.id !== atom.id))
        }
      } catch (error) {
        toast.error('åˆ é™¤å¤±è´¥')
      }
    }
  }

  // è·å–çŠ¶æ€æ˜¾ç¤ºæ ·å¼
  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'active':
        return <Badge className="bg-green-500 hover:bg-green-600">æ´»è·ƒ</Badge>
      case 'draft':
        return <Badge variant="secondary">è‰ç¨¿</Badge>
      case 'archived':
        return <Badge variant="outline">å·²å½’æ¡£</Badge>
      default:
        return <Badge variant="secondary">{status}</Badge>
    }
  }

  // æ ¼å¼åŒ–æ—¶é—´æ˜¾ç¤º
  const formatTimeAgo = (date: Date) => {
    const now = new Date()
    const diff = now.getTime() - date.getTime()
    const days = Math.floor(diff / (1000 * 60 * 60 * 24))

    if (days === 0) return 'ä»Šå¤©'
    if (days === 1) return '1å¤©å‰'
    if (days < 7) return `${days}å¤©å‰`
    if (days < 30) return `${Math.floor(days / 7)}å‘¨å‰`
    return `${Math.floor(days / 30)}ä¸ªæœˆå‰`
  }

  return (
    <div className="space-y-6">
      {/* é¡µé¢å¤´éƒ¨ */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">åŸå­ä¸Šä¸‹æ–‡åº“</h1>
          <p className="text-muted-foreground">
            ç®¡ç†å’Œå¤ç”¨æ‚¨çš„ä»£ç ç‰‡æ®µã€é…ç½®æ¨¡æ¿å’ŒçŸ¥è¯†åŸå­
          </p>
        </div>
        <Button onClick={handleCreateAtom} className="shrink-0">
          <Plus className="h-4 w-4 mr-2" />
          åˆ›å»ºåŸå­
        </Button>
      </div>

      {/* æœç´¢å’Œç­›é€‰æ  */}
      <Card>
        <CardContent className="p-4">
          <div className="flex flex-col sm:flex-row gap-4">
            {/* æœç´¢æ¡† */}
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="æœç´¢åŸå­åç§°ã€æè¿°æˆ–æ ‡ç­¾..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
              />
            </div>

            {/* ç­›é€‰å™¨ */}
            <div className="flex gap-2">
              <Select 
                value={selectedCategory} 
                onValueChange={(value) => {
                  setSelectedCategory(value)
                  if (value) {
                    toast.info(`ç­›é€‰åˆ†ç±»ï¼š${value}`)
                  }
